import { useEffect, useState, useCallback, useRef } from "react";
import api from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";
import { Dropdown, Modal, Button, FormControl, ListGroup, Badge, Form } from "react-bootstrap"; 

/* --- Embedded Call Component (Feature Preserved) --- */
const VideoCallOverlay = ({ currentRoom, user, type, onEnd }) => {
  const jitsiContainerRef = useRef(null);
  const jitsiApiRef = useRef(null);

  useEffect(() => {
    const domain = "meet.jit.si";
    const options = {
      roomName: `MernChat_Room_${currentRoom._id}`,
      width: "100%",
      height: "100%",
      parentNode: jitsiContainerRef.current,
      userInfo: { displayName: user?.username || "User" },
      configOverwrite: {
        startWithAudioMuted: false,
        startWithVideoMuted: type === 'audio',
        disableInviteFunctions: true
      },
    };
    jitsiApiRef.current = new window.JitsiMeetExternalAPI(domain, options);
    jitsiApiRef.current.addEventListener("videoConferenceLeft", onEnd);

    return () => {
      if (jitsiApiRef.current) jitsiApiRef.current.dispose();
    };
  }, [currentRoom._id, user, type, onEnd]);

  return (
    <div className="position-absolute top-0 start-0 w-100 h-100 bg-dark d-flex flex-column" style={{ zIndex: 1050 }}>
      <div className="p-2 d-flex justify-content-between align-items-center bg-black text-white border-bottom border-secondary">
        <span className="fw-bold px-2">{type === 'video' ? 'üìπ Video Call' : 'üìû Audio Call'}: {currentRoom.name}</span>
        <Button variant="danger" size="sm" onClick={onEnd}>End Call</Button>
      </div>
      <div ref={jitsiContainerRef} className="flex-grow-1" />
    </div>
  );
};

export default function Chat({ setAuth }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showInfoModal, setShowInfoModal] = useState(false);
  const [isCreating, setIsCreating] = useState(false);
  const [callConfig, setCallConfig] = useState({ active: false, type: 'video' });
  const [showSearchBar, setShowSearchBar] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  // üîµ NEW: State to track unread Room IDs
  const [unreadRooms, setUnreadRooms] = useState([]);

  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
    showHistoryToNewMembers: true,
  });

  const navigate = useNavigate();

  const fetchData = useCallback(async () => {
    try {
      const [roomsRes, usersRes] = await Promise.all([
        api.get("/rooms"),
        api.get("/auth/users"),
      ]);
      setRooms(roomsRes.data || []);
      setUsers(usersRes.data || []);
      if (currentRoom) {
        const updatedRoom = (roomsRes.data || []).find(r => r._id === currentRoom._id);
        if (updatedRoom) setCurrentRoom(updatedRoom);
      }
    } catch (err) {
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [currentRoom]);

  const handleLogout = useCallback(() => {
    if (setAuth) { setAuth(null); } 
    else { localStorage.clear(); window.location.href = "/login"; }
    socket.disconnect();
  }, [setAuth]);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
        socket.connect();
      } catch (e) { handleLogout(); }
    }
    fetchData();

    const handleRoomDeleted = ({ roomId }) => {
      setRooms((prev) => prev.filter((r) => r._id !== roomId));
      setCurrentRoom((prev) => {
        if (prev?._id === roomId) {
          setMessages([]);
          setShowInfoModal(false);
          return null;
        }
        return prev;
      });
    };

    socket.on("room_deleted", handleRoomDeleted);
    return () => {
      socket.off("room_deleted", handleRoomDeleted);
      socket.disconnect();
    };
  }, []); 

  useEffect(() => {
    if (!currentRoom || !user) return;
    const roomId = currentRoom._id;

    // üîµ Clear unread when we enter a room
    setUnreadRooms(prev => prev.filter(id => id !== roomId));

    socket.emit("join_room", { roomId, username: user.username });

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error(err));

    const handleReceive = (msg) => {
      const incomingRoomId = msg.roomId || msg.room;

      if (incomingRoomId === roomId) {
        // Message is for the open room
        setMessages((prev) => [...prev, msg]);
      } else {
        // üîµ NOTIFICATION LOGIC: Message is for a background room
        setUnreadRooms((prev) => {
          if (!prev.includes(incomingRoomId)) return [...prev, incomingRoomId];
          return prev;
        });
      }
    };

    const handleUserJoined = ({ username }) => {
      const systemMessage = {
        _id: `system-${Date.now()}`,
        content: `üëã ${username} joined the chat`,
        sender: { username: "System" },
        isSystem: true,
        createdAt: new Date()
      };
      setMessages((prev) => [...prev, systemMessage]);
    };

    socket.on("receive_message", handleReceive);
    socket.on("user_joined", handleUserJoined);

    return () => {
      socket.off("receive_message", handleReceive);
      socket.off("user_joined", handleUserJoined);
    };
  }, [currentRoom?._id, user?.username]);

  /* ================= HANDLERS ================= */

  const handleSelectRoom = async (room) => {
    const userId = user?._id || user?.id;
    const isMember = room.members?.some(m => {
      const mId = m.user?._id || m.user || m;
      return mId.toString() === userId?.toString();
    });

    if (!room.isPrivate || isMember) {
      setCurrentRoom(room);

      // üîµ Mark as read immediately when selected
      setUnreadRooms(prev => prev.filter(id => id !== room._id));

      if (!isMember && !room.isPrivate) {
        try {
          await api.post(`/rooms/join-id/${room._id}`);
          fetchData(); 
        } catch (err) { console.error("Background join skipped"); }
      }
    } else {
      alert("This is a private room. Use invite code.");
      return;
    }

    setIsCreating(false); 
    setCallConfig({active: false}); 
    setShowSearchBar(false);
    setSearchTerm("");
  };

  // ... (All other handlers: handleJoinRoom, handleStartCall, etc. remain unchanged)
  const handleJoinRoom = async (item) => {
    try {
      let res;
      if (typeof item === "object") {
        res = await api.post(`/rooms/join-id/${item._id}`);
      } else {
        const input = item.trim();
        res = await api.post(`/rooms/join-code/${input}`);
      }
      await fetchData(); 
      setCurrentRoom(res.data.room || res.data);
      setIsCreating(false);
    } catch (err) {
      if (err.response?.status !== 400) {
        alert(err.response?.data?.message || "Error joining room.");
      }
    }
  };

  const handleStartCall = (type) => {
    if (!currentRoom) return;
    setCallConfig({ active: true, type });
    onSendMessage(`üìû Started a ${type} call.`);
  };

  const handleDeleteRoomById = async (roomId) => {
    if (window.confirm("Permanently delete this room?")) {
      try {
        await api.delete(`/rooms/${roomId}`);
        socket.emit("delete_room", { roomId });
        setRooms((prev) => prev.filter((r) => r._id !== roomId));
        if (currentRoom?._id === roomId) {
          setCurrentRoom(null);
          setMessages([]);
        }
        setShowInfoModal(false);
      } catch (err) { alert("Delete failed."); }
    }
  };

  const onSendMessage = async (content, file = null) => {
    if (!currentRoom) return;
    try {
      let res;
      if (file) {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("roomId", currentRoom._id);
        res = await api.post("/messages/upload", formData, { headers: { "Content-Type": "multipart/form-data" } });
      } else {
        res = await api.post("/messages", { roomId: currentRoom._id, content });
      }
      socket.emit("send_message", res.data);
    } catch (err) { console.error(err); }
  };

  const handleClearChat = async () => {
    if (window.confirm("Clear all messages?")) {
      try {
        await api.delete(`/messages/clear/${currentRoom._id}`);
        setMessages([]);
      } catch (err) { alert("Failed to clear chat"); }
    }
  };

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/rooms", { name: newRoomData.name, isPrivate: newRoomData.isPrivate });
      await fetchData(); 
      setCurrentRoom(res.data);
      setIsCreating(false);
      setNewRoomData({ name: "", isPrivate: false });
    } catch (err) { alert("Error creating room"); }
  };

  const handleSelectUser = async (targetUser) => {
    try {
      const userId = user?._id || user?.id;
      const existingDm = rooms.find(r => r.isDirectMessage && r.members?.some(m => (m.user?._id || m.user || m).toString() === targetUser._id.toString()));
      if (existingDm) { setCurrentRoom(existingDm); } 
      else {
        const res = await api.post("/rooms/private", { targetUserId: targetUser._id });
        await fetchData(); 
        setCurrentRoom(res.data);
      }
    } catch (err) { console.error("DM Error:", err); }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    alert("Copied to clipboard!");
  };

  const channels = (rooms || []).filter(r => !r.isDirectMessage);
  const directMessages = (rooms || []).filter(r => r.isDirectMessage);
  const currentUserRealId = user?._id || user?.id;
  const roomOwnerId = currentRoom?.owner?._id || currentRoom?.owner;
  const isOwner = roomOwnerId && currentUserRealId && roomOwnerId.toString() === currentUserRealId.toString();

  const getUniqueMembers = (members) => {
    const seen = new Set();
    return (members || []).filter(member => {
      const id = member.user?._id || member.user || member._id;
      if (!id || seen.has(id.toString())) return false;
      seen.add(id.toString());
      return true;
    });
  };

  const getDMName = () => {
    const otherMember = currentRoom.members?.find(m => (m.user?._id || m.user || m).toString() !== currentUserRealId?.toString());
    return otherMember?.user?.username || otherMember?.username || "Private Chat";
  };

  const filteredMessages = messages.filter(msg => msg.content?.toLowerCase().includes(searchTerm.toLowerCase()));

  if (loading) return <div className="vh-100 d-flex justify-content-center align-items-center">Loading‚Ä¶</div>;

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 overflow-hidden">
        <RoomList
          rooms={rooms || []}
          channels={channels}
          directMessages={directMessages}
          users={users || []}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          // üîµ Pass unread state to the list UI
          unreadRooms={unreadRooms} 
          onSelectRoom={handleSelectRoom}
          onSelectUser={handleSelectUser}
          onJoinRoom={handleJoinRoom} 
          onCreateRoom={() => setIsCreating(true)}
          onLogout={handleLogout}
          onRefresh={fetchData}
          onDeleteRoom={handleDeleteRoomById} 
        />

        <div className="flex-grow-1 bg-white d-flex flex-column position-relative">
          {callConfig.active && (
            <VideoCallOverlay currentRoom={currentRoom} user={user} type={callConfig.type} onEnd={() => setCallConfig({ active: false })} />
          )}

          {isCreating ? (
            <div className="p-5 d-flex justify-content-center">
              <div className="card shadow-sm p-4 w-100" style={{maxWidth: '500px'}}>
                <h3>Create New Room</h3>
                <Form onSubmit={handleCreateRoom}>
                  <Form.Group className="mb-3">
                    <Form.Label>Room Name</Form.Label>
                    <Form.Control type="text" required onChange={(e) => setNewRoomData({...newRoomData, name: e.target.value})} />
                  </Form.Group>
                  <Form.Check type="switch" label="Private Room (Requires Code)" className="mb-4" checked={newRoomData.isPrivate} onChange={(e) => setNewRoomData({...newRoomData, isPrivate: e.target.checked})} />
                  <div className="d-flex gap-2">
                    <Button variant="primary" type="submit">Create</Button>
                    <Button variant="secondary" onClick={() => setIsCreating(false)}>Cancel</Button>
                  </div>
                </Form>
              </div>
            </div>
          ) : currentRoom ? (
            <div className="d-flex flex-column h-100">
              <div className="d-flex justify-content-between align-items-center p-3 border-bottom bg-light">
                <div className="d-flex align-items-center gap-3" style={{ cursor: "pointer" }} onClick={() => setShowInfoModal(true)}>
                  <div className="bg-secondary rounded-circle d-flex align-items-center justify-content-center text-white" style={{ width: "40px", height: "40px" }}>
                    {currentRoom.isDirectMessage ? "üë§" : "üë•"}
                  </div>
                  <h5 className="mb-0 fw-bold">{currentRoom.isDirectMessage ? getDMName() : (currentRoom.isPrivate ? `üîí ${currentRoom.name}` : `üåê ${currentRoom.name}`)}</h5>
                </div>
                <div className="d-flex align-items-center gap-3">
                  <span className="text-secondary" style={{ cursor: "pointer", fontSize: "1.3rem" }} onClick={() => setShowSearchBar(!showSearchBar)}>üîç</span>
                  <span className="text-secondary" style={{ cursor: "pointer", fontSize: "1.3rem" }} onClick={() => handleStartCall('video')}>üìπ</span>
                  <span className="text-secondary" style={{ cursor: "pointer", fontSize: "1.3rem" }} onClick={() => handleStartCall('audio')}>üìû</span>
                  <Dropdown align="end">
                    <Dropdown.Toggle variant="link" className="text-secondary p-0 border-0 no-caret shadow-none"><span style={{ fontSize: "1.6rem" }}>‚ãÆ</span></Dropdown.Toggle>
                    <Dropdown.Menu>
                      <Dropdown.Item onClick={() => setShowInfoModal(true)}>‚ìò Info</Dropdown.Item>
                      <Dropdown.Item onClick={handleClearChat}>‚ûñ Clear messages</Dropdown.Item>
                      {isOwner && !currentRoom.isDirectMessage && (
                        <Dropdown.Item onClick={() => handleDeleteRoomById(currentRoom._id)} className="text-danger">üóëÔ∏è Delete</Dropdown.Item>
                      )}
                    </Dropdown.Menu>
                  </Dropdown>
                </div>
              </div>
              {showSearchBar && (
                <div className="p-2 border-bottom bg-light">
                  <FormControl type="text" placeholder="Search..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} autoFocus />
                </div>
              )}
              <div className="flex-grow-1 overflow-hidden">
                <ChatBox messages={searchTerm ? filteredMessages : messages} currentUserId={currentUserRealId} onSendText={(txt) => onSendMessage(txt)} onSendFile={(f) => onSendMessage(null, f)} />
              </div>
            </div>
          ) : (
            <div className="h-100 d-flex flex-column justify-content-center align-items-center text-muted">
              <h4>Welcome to Chat</h4>
              <p>Select a room to start talking!</p>
            </div>
          )}
        </div>
      </div>
      {/* (Modal remains unchanged) */}
    </div>
  );
}
