import { useEffect, useState, useCallback } from "react";
import { api } from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket"; 
import { useNavigate } from "react-router-dom";

export default function Chat({ setToken }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]); 
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [isUploading, setIsUploading] = useState(false);

  // UI NAVIGATION STATES
  const [isCreating, setIsCreating] = useState(false);
  const [isJoining, setIsJoining] = useState(false); 

  const [inviteCodeInput, setInviteCodeInput] = useState("");
  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
    showHistory: false 
  });

  const navigate = useNavigate();

  // --- LOGIC: LOGOUT & INITIAL FETCH ---
  const handleLogout = useCallback(() => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    if (setToken) setToken(null);
    socket.disconnect();
    navigate("/login");
  }, [navigate, setToken]);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");

    if (!storedUser || !token) {
      handleLogout();
      return;
    }

    const parsedUser = JSON.parse(storedUser);
    setUser(parsedUser);
    const userId = parsedUser.id || parsedUser._id;
    socket.connect(); 

    const fetchData = async () => {
      try {
        const [roomsRes, usersRes] = await Promise.all([
          api.get("/rooms"),
          api.get("/auth/users")
        ]);

        const visibleRooms = roomsRes.data.filter(r => 
          !r.isPrivate || r.members.some(m => (m.user?._id || m.user || m) === userId)
        );

        setRooms(visibleRooms);
        setUsers(usersRes.data);
      } catch (err) {
        if (err.response?.status === 401) handleLogout();
      }
    };
    fetchData();

    socket.on("room_created", (newRoom) => {
        setRooms((prev) => (prev.find(r => r._id === newRoom._id) ? prev : [...prev, newRoom]));
    });

    socket.on("room_deleted", (id) => {
      setRooms(p => p.filter(r => r._id !== id));
      if (currentRoom?._id === id) setCurrentRoom(null);
    });

    return () => {
      socket.off("room_created");
      socket.off("room_deleted");
      socket.disconnect();
    };
  }, [handleLogout, currentRoom]);

  // --- ROOM MESSAGING SYNC ---
  useEffect(() => {
    if (!currentRoom || !user) return;
    const roomId = currentRoom._id;
    const username = user.username;

    socket.emit("join_room", { roomId, username });

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error("History error:", err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId) setMessages((prev) => [...prev, msg]);
    };

    socket.on("receive_message", handleReceive);
    return () => {
      socket.off("receive_message", handleReceive);
      socket.emit("leave_room", { roomId, username });
    };
  }, [currentRoom, user]);

  // --- HANDLERS ---
  const handleCreateSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/rooms", {
        name: newRoomData.name.trim(),
        isPrivate: newRoomData.isPrivate,
        showHistoryToNewMembers: newRoomData.showHistory 
      });
      setRooms(prev => [...prev, res.data]);
      setCurrentRoom(res.data);
      setIsCreating(false);
      setNewRoomData({ name: "", isPrivate: false, showHistory: false });
      socket.emit("create_room", res.data); 
    } catch (err) { alert("Error creating room."); }
  };

  const handleJoinByCode = async (e) => {
    e.preventDefault();
    try {
      await api.post(`/rooms/join/${inviteCodeInput}`);
      alert("Join request sent to owner!");
      setIsJoining(false);
      setInviteCodeInput("");
    } catch (err) { alert("Invalid code or already requested."); }
  };

  const handleJoinRoom = async (room) => {
    try {
      const res = await api.post(`/rooms/${room._id}/join`);
      setRooms(prev => prev.map(r => r._id === res.data._id ? res.data : r));
      setCurrentRoom(res.data);
    } catch (err) { alert("Failed to join."); }
  };

  const handleSelectUser = async (targetUser) => {
    try {
      const res = await api.post("/rooms/private", { targetUserId: targetUser._id });
      setRooms(prev => (prev.find(r => r._id === res.data._id) ? prev : [...prev, res.data]));
      setCurrentRoom(res.data);
      setIsCreating(false);
      setIsJoining(false);
    } catch (err) { console.error("Private chat error:", err); }
  };

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 shadow-sm overflow-hidden">

        {/* SIDEBAR */}
        <div className="col-md-3 border-end bg-white d-flex flex-column h-100 p-0">
          <div className="p-3 border-bottom bg-white">
            <h4 className="text-primary fw-bold mb-0">ChatBox</h4>
          </div>

          <div className="p-3 border-bottom">
            <div className="d-grid gap-2">
              <button 
                className={`btn w-100 ${isCreating ? 'btn-primary' : 'btn-outline-primary'}`} 
                onClick={() => { setIsCreating(true); setIsJoining(false); setCurrentRoom(null); }}
              >
                + Create Room
              </button>
              <button 
                className={`btn w-100 ${isJoining ? 'btn-primary' : 'btn-outline-primary'}`}
                onClick={() => { setIsJoining(true); setIsCreating(false); setCurrentRoom(null); }}
              >
                # Join with ID
              </button>
            </div>
          </div>

          <div className="flex-grow-1 overflow-auto">
            <RoomList
              rooms={rooms}
              users={users}
              currentUser={user}
              currentRoomId={currentRoom?._id}
              onSelectRoom={(r) => { setCurrentRoom(r); setIsCreating(false); setIsJoining(false); }}
              onJoinRoom={handleJoinRoom}
              onSelectUser={handleSelectUser}
              onLogout={handleLogout}
            />
          </div>
        </div>

        {/* MAIN CONTENT */}
        <div className="d-flex flex-column flex-grow-1 bg-white">
          {isCreating ? (
            <div className="h-100 d-flex justify-content-center align-items-center bg-light">
              <div className="card shadow-sm p-4" style={{ width: "400px" }}>
                <h3 className="mb-4">Create Room</h3>
                <form onSubmit={handleCreateSubmit}>
                  <input className="form-control mb-3" placeholder="Room Name" onChange={e => setNewRoomData({...newRoomData, name: e.target.value})} required />
                  <div className="form-check form-switch mb-2">
                    <input className="form-check-input" type="checkbox" onChange={e => setNewRoomData({...newRoomData, isPrivate: e.target.checked})} />
                    <label className="form-check-label">Private Room</label>
                  </div>
                  <div className="form-check form-switch mb-4">
                    <input className="form-check-input" type="checkbox" checked={newRoomData.showHistory} onChange={e => setNewRoomData({...newRoomData, showHistory: e.target.checked})} />
                    <label className="form-check-label text-muted small">Allow new members to see old history</label>
                  </div>
                  <div className="d-flex gap-2">
                    <button type="submit" className="btn btn-primary w-100">Create</button>
                    <button type="button" className="btn btn-outline-secondary w-100" onClick={() => setIsCreating(false)}>Cancel</button>
                  </div>
                </form>
              </div>
            </div>
          ) : isJoining ? (
            <div className="h-100 d-flex justify-content-center align-items-center bg-light">
              <div className="card shadow-sm p-4" style={{ width: "400px" }}>
                <h3 className="mb-4">Join Room</h3>
                <form onSubmit={handleJoinByCode}>
                  <input className="form-control mb-3 text-center" placeholder="Enter 6-digit Code" value={inviteCodeInput} onChange={e => setInviteCodeInput(e.target.value.toUpperCase())} required />
                  <div className="d-flex gap-2">
                    <button type="submit" className="btn btn-primary w-100">Request Access</button>
                    <button type="button" className="btn btn-outline-secondary w-100" onClick={() => setIsJoining(false)}>Cancel</button>
                  </div>
                </form>
              </div>
            </div>
          ) : currentRoom ? (
            <div className="d-flex flex-column h-100">
               <div className="p-3 border-bottom bg-white d-flex justify-content-between align-items-center">
                <div>
                  <h5 className="mb-0 fw-bold">{currentRoom.isPrivate ? "ðŸ”’" : "#"} {currentRoom.name}</h5>
                  {currentRoom.isPrivate && <small className="text-muted">Code: {currentRoom.inviteCode}</small>}
                </div>
                <div className="d-flex gap-2">
                  {currentRoom.owner === (user?.id || user?._id) && (
                    <button className="btn btn-outline-primary btn-sm">Manage Requests ({currentRoom.joinRequests?.length || 0})</button>
                  )}
                  <button className="btn btn-danger btn-sm">Delete Room</button>
                </div>
              </div>
              <ChatBox messages={messages} currentUserId={user?.id || user?._id} />
            </div>
          ) : (
            <div className="h-100 d-flex justify-content-center align-items-center text-muted">
              Select a room or use the buttons to start chatting.
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
