import { useEffect, useState, useCallback } from "react";
import { api } from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket"; 
import { useNavigate } from "react-router-dom";

export default function Chat({ setToken }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]); 
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const navigate = useNavigate();

  // Helper to handle Logout consistently
  const handleLogout = useCallback(() => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    if (setToken) setToken(null);
    socket.disconnect();
    navigate("/login");
  }, [navigate, setToken]);

  // 1. Initial Data & Socket Connection
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");

    if (!storedUser || !token) {
      handleLogout();
      return;
    }

    const parsedUser = JSON.parse(storedUser);
    setUser(parsedUser);
    socket.connect(); 

    // Load Initial Data
    const fetchData = async () => {
      try {
        const [roomsRes, usersRes] = await Promise.all([
          api.get("/rooms"),
          api.get("/auth/users")
        ]);
        setRooms(roomsRes.data);
        setUsers(usersRes.data);

        // Auto-select first joined room
        const userId = parsedUser.id || parsedUser._id;
        const joinedRoom = roomsRes.data.find(r => r.members.some(m => (m._id || m) === userId));
        if (joinedRoom) setCurrentRoom(joinedRoom);
      } catch (err) {
        if (err.response?.status === 401) handleLogout();
      }
    };

    fetchData();

    // SOCKET LISTENERS: Room Updates
    socket.on("room_created", (newRoom) => {
      setRooms((prev) => (prev.find(r => r._id === newRoom._id) ? prev : [...prev, newRoom]));
    });

    socket.on("room_deleted", (roomId) => {
      setRooms((prev) => prev.filter(r => r._id !== roomId));
      if (currentRoom?._id === roomId) {
        setCurrentRoom(null);
        alert("This room has been deleted.");
      }
    });

    return () => {
      socket.off("room_created");
      socket.off("room_deleted");
      socket.disconnect();
    };
  }, [handleLogout]); // Only run on mount

  // 2. Messaging & Room Sync
  useEffect(() => {
    if (!currentRoom || !user) return;

    const roomId = currentRoom._id;
    socket.emit("join_room", roomId);

    // Fetch message history (Persistence)
    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error("History error:", err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId || msg.room === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("receive_message", handleReceive);

    return () => {
      socket.off("receive_message", handleReceive);
      socket.emit("leave_room", roomId);
    };
  }, [currentRoom, user]);

  // --- ACTIONS ---

  const handleCreateRoom = async () => {
    const roomName = prompt("Enter new room name:");
    if (!roomName?.trim()) return;
    try {
      const res = await api.post("/rooms", { name: roomName.trim() });
      const newRoom = res.data;
      setRooms(prev => [...prev, newRoom]);
      setCurrentRoom(newRoom);
      socket.emit("create_room", newRoom); // Notify others instantly
    } catch (err) {
      alert("Failed to create room.");
    }
  };

  const handleJoinRoom = async (room) => {
    try {
      const res = await api.post(`/rooms/${room._id}/join`);
      const updatedRoom = res.data;
      setRooms(prev => prev.map(r => r._id === updatedRoom._id ? updatedRoom : r));
      setCurrentRoom(updatedRoom);
    } catch (err) {
      alert("Failed to join.");
    }
  };

  const handleSelectUser = async (targetUser) => {
    const myId = user?.id || user?._id;
    if (targetUser._id === myId) return;
    try {
      const res = await api.post("/rooms/private", { targetUserId: targetUser._id });
      const privateRoom = res.data;
      setRooms(prev => (prev.find(r => r._id === privateRoom._id) ? prev : [...prev, privateRoom]));
      setCurrentRoom(privateRoom);
    } catch (err) {
      console.error("Private chat error:", err);
    }
  };

  const sendTextMessage = (text) => {
    if (!currentRoom || !text.trim() || !user) return;
    const messageData = {
      roomId: currentRoom._id,
      content: text,
      sender: user.id || user._id, 
      type: "text",
    };
    socket.emit("send_message", messageData);
  };

  const sendFileMessage = async (file) => {
    if (!currentRoom || !user) return;
    setIsUploading(true);
    const formData = new FormData();
    formData.append("file", file);
    formData.append("roomId", currentRoom._id);
    formData.append("senderId", user.id || user._id);
    try {
      // API call saves to Cloudinary & DB
      await api.post("/messages/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      // Logic: The server will emit "receive_message" via Socket after upload
    } catch (err) {
      alert("Media upload failed.");
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 shadow-sm overflow-hidden">
        {/* Sidebar */}
        <RoomList
          rooms={rooms}
          users={users}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={setCurrentRoom}
          onSelectUser={handleSelectUser}
          onJoinRoom={handleJoinRoom}
          onCreateRoom={handleCreateRoom} 
          onLogout={handleLogout}
        />

        {/* Chat Area */}
        <div className="d-flex flex-column flex-grow-1 bg-white position-relative">
          {isUploading && (
            <div className="position-absolute top-0 start-0 w-100 bg-primary text-white text-center py-1 z-3">
              Uploading media...
            </div>
          )}

          {currentRoom ? (
            <div className="d-flex flex-column h-100">
              <div className="p-3 border-bottom d-flex justify-content-between align-items-center bg-white shadow-sm">
                <h5 className="mb-0 fw-bold">
                  {currentRoom.isPrivate ? "ðŸ‘¤ " : "# "} {currentRoom.name || "Private Chat"}
                </h5>
                <button onClick={handleLogout} className="btn btn-outline-secondary btn-sm d-md-none">Logout</button>
              </div>

              <div className="flex-grow-1 overflow-hidden bg-light">
                {currentRoom.members?.some(m => (m._id || m) === (user?.id || user?._id)) ? (
                  <ChatBox 
                    messages={messages} 
                    onSendText={sendTextMessage} 
                    onSendFile={sendFileMessage}
                    currentUserId={user?.id || user?._id}
                  />
                ) : (
                  <div className="h-100 d-flex flex-column justify-content-center align-items-center">
                    <h3>Join #{currentRoom.name} to see messages</h3>
                    <button className="btn btn-success btn-lg mt-3" onClick={() => handleJoinRoom(currentRoom)}>
                      Join Now
                    </button>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div className="h-100 d-flex flex-column justify-content-center align-items-center text-center">
              <h4 className="text-muted">Welcome to ChatBox</h4>
              <p>Select a channel or user to start chatting.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
