import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const JoinRoom = () => {
  const [inviteCode, setInviteCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // --- 1. JOIN PRIVATE ROOM (Via Code) ---
  const handleJoinByCode = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // ✅ MATCHES BACKEND: /api/rooms/join-code
      const { data } = await axios.post('/api/rooms/join-code', { 
        code: inviteCode.trim().toUpperCase() 
      });

      console.log("Successfully joined private room:", data);
      navigate(`/chat/${data._id}`); // Redirect to the new room
    } catch (err) {
      // ✅ HELPS DEBUG: Catches 404, 400, and 500 errors
      const msg = err.response?.data?.message || "Failed to join. Check your code.";
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  // --- 2. JOIN PUBLIC ROOM (Via ID) ---
  // This would be called when clicking 'Join' on a list of public rooms
  const handleJoinPublic = async (roomId) => {
    setLoading(true);
    setError('');

    try {
      // ✅ MATCHES BACKEND: /api/rooms/join-public/:roomId
      const { data } = await axios.post(`/api/rooms/join-public/${roomId}`);
      
      navigate(`/chat/${data._id}`);
    } catch (err) {
      const msg = err.response?.data?.message || "Could not join public room.";
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card shadow border-0">
            <div className="card-body p-4">
              <h3 className="text-center mb-4">Join a Room</h3>

              {/* Error Alert - Vital for debugging mismatches */}
              {error && (
                <div className="alert alert-danger py-2" role="alert">
                  <small>{error}</small>
                </div>
              )}

              {/* Private Join Form */}
              <form onSubmit={handleJoinByCode}>
                <div className="mb-3">
                  <label className="form-label fw-bold">Invite Code</label>
                  <input
                    type="text"
                    className="form-control form-control-lg text-center"
                    placeholder="ENTER 6-DIGIT CODE"
                    maxLength="10"
                    value={inviteCode}
                    onChange={(e) => setInviteCode(e.target.value)}
                    required
                  />
                  <div className="form-text text-center">
                    Private rooms require a unique invite code.
                  </div>
                </div>

                <button 
                  type="submit" 
                  className="btn btn-primary w-100 btn-lg"
                  disabled={loading}
                >
                  {loading ? (
                    <span className="spinner-border spinner-border-sm"></span>
                  ) : (
                    'Join Private Room'
                  )}
                </button>
              </form>

              <hr className="my-4" />

              {/* Example Public Join (Normally this is in a list) */}
              <div className="text-center">
                <p className="text-muted small">Want to join a public channel?</p>
                <button 
                  className="btn btn-outline-secondary btn-sm"
                  onClick={() => navigate('/rooms')} // Navigate to room browser
                >
                  Browse Public Rooms
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JoinRoom;
