import React from "react";

/**
 * RoomList Component
 * @param {Array} rooms - The list of room objects from MongoDB
 * @param {String} currentRoomId - The ID of the currently active room
 * @param {Function} onSelectRoom - Function to switch the active room
 * @param {Function} onCreateRoom - Function to trigger the room creation prompt
 */
function RoomList({ rooms, currentRoomId, onSelectRoom, onCreateRoom }) {
  return (
    <div 
      className="bg-light border-end d-flex flex-column shadow-sm" 
      style={{ width: "260px", height: "100vh" }}
    >
      {/* Sidebar Header */}
      <div className="p-3 border-bottom bg-white d-flex justify-content-between align-items-center">
        <h5 className="mb-0 fw-bold text-primary">Channels</h5>
        <span className="badge bg-primary rounded-pill">
          {rooms ? rooms.length : 0}
        </span>
      </div>

      {/* Room Selection Area (Scrollable) */}
      <div className="list-group list-group-flush overflow-auto flex-grow-1 p-2 custom-scrollbar">
        {(!rooms || rooms.length === 0) ? (
          <div className="text-center mt-5">
            <p className="text-muted small mb-0">No rooms found.</p>
            <small className="text-decoration-underline text-primary" style={{ cursor: 'pointer' }} onClick={onCreateRoom}>
              Create one?
            </small>
          </div>
        ) : (
          rooms.map((room) => (
            <button
              key={room._id}
              onClick={() => onSelectRoom(room)}
              className={`list-group-item list-group-item-action border-0 rounded mb-1 d-flex align-items-center transition-all ${
                room._id === currentRoomId ? "active shadow-sm" : ""
              }`}
            >
              <span className="me-2 opacity-75">
                {room.isPrivate ? "ðŸ”’" : "#"}
              </span>
              <span className="text-truncate" style={{ maxWidth: "160px" }}>
                {room.name}
              </span>
              
              {/* Active Indicator */}
              {room._id === currentRoomId && (
                <div className="ms-auto">
                  <span className="badge border border-light rounded-circle p-1">
                    <span className="visually-hidden">Active</span>
                  </span>
                </div>
              )}
            </button>
          ))
        )}
      </div>
      
      {/* Sidebar Footer with Create Action */}
      <div className="p-3 border-top bg-white">
        <button 
          onClick={onCreateRoom} 
          className="btn btn-primary btn-sm w-100 fw-bold py-2 shadow-sm"
        >
          <i className="bi bi-plus-lg me-1"></i> + Create Room
        </button>
      </div>

      {/* Basic Inline CSS for a cleaner scrollbar */}
      <style>{`
        .custom-scrollbar::-webkit-scrollbar {
          width: 5px;
        }
        .custom-scrollbar::-webkit-scrollbar-thumb {
          background: #dee2e6;
          border-radius: 10px;
        }
        .transition-all {
          transition: all 0.2s ease-in-out;
        }
      `}</style>
    </div>
  );
}

export default RoomList;
