import axios from "axios";

/**
 * Axios instance configuration
 */
const api = axios.create({
  baseURL: "http://localhost:5000/api",
  withCredentials: true, 
});

/**
 * REQUEST INTERCEPTOR: Automatically attach JWT token
 */
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("token");
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

/**
 * RESPONSE INTERCEPTOR: Global Error Handling (Auto-Logout)
 */
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      console.error("Session expired or unauthorized. Logging out...");
      localStorage.removeItem("token");
      localStorage.removeItem("user");
      window.location.replace("/login");
    }
    return Promise.reject(error);
  }
);

/* ============================
    AUTH APIS
============================ */
export const login = (data) => api.post("/auth/login", data);
export const register = (data) => api.post("/auth/register", data);

/* ============================
    ROOM APIS
============================ */
export const getRooms = () => api.get("/rooms");
export const createRoom = (data) => api.post("/rooms", data);
// NEW: Join via invite code logic
export const joinRoom = (inviteCode) => api.post("/rooms/join", { inviteCode });
// NEW: Manage join requests (Owner/Admin only)
export const handleJoinRequest = (roomId, requestId, status) => 
  api.put(`/rooms/${roomId}/requests`, { requestId, status });

/* ============================
    MESSAGE APIS
============================ */
export const getMessages = (roomId) => api.get(`/messages/${roomId}`);

// For standard text messages
export const sendMessage = (data) => api.post("/messages", data);

// NEW: For File/Image uploads (requires FormData on the frontend)
export const uploadFile = (formData) => api.post("/messages/upload", formData, {
  headers: { "Content-Type": "multipart/form-data" }
});

export const deleteMessage = (messageId) => api.delete(`/messages/${messageId}`);
export const editMessage = (messageId, content) => api.put(`/messages/${messageId}`, { content });

// Primary Export (Fixes 'api' not found error)
export default api;
