import { useEffect, useState, useCallback } from "react";
import api from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";
import { Dropdown, Modal, Button, InputGroup, FormControl, ListGroup, Badge } from "react-bootstrap"; 

export default function Chat({ setAuth }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showInfoModal, setShowInfoModal] = useState(false);

  const [isCreating, setIsCreating] = useState(false);
  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
    showHistoryToNewMembers: true,
  });
// Inside Chat.js

  const navigate = useNavigate();

  /* ================= FETCH DATA ================= */
  const fetchData = useCallback(async () => {
    try {
      const [roomsRes, usersRes] = await Promise.all([
        api.get("/rooms"),
        api.get("/auth/users"),
      ]);
      setRooms(roomsRes.data || []);
      setUsers(usersRes.data || []);

      if (currentRoom) {
        const updatedRoom = (roomsRes.data || []).find(r => r._id === currentRoom._id);
        if (updatedRoom) setCurrentRoom(updatedRoom);
      }
    } catch (err) {
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [currentRoom]);

  const handleLogout = useCallback(() => {
    if (setAuth) { setAuth(null); } 
    else { localStorage.clear(); window.location.href = "/login"; }
    socket.disconnect();
  }, [setAuth]);

  /* ================= INITIAL LOAD ================= */
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
        socket.connect();
      } catch (e) { handleLogout(); }
    }
    fetchData();
    return () => {
      socket.off("receive_message");
      socket.disconnect();
    };
  }, []); 

  /* ================= ROOM SYNC ================= */
  useEffect(() => {
    if (!currentRoom || !user) return;
    const roomId = currentRoom._id;
    socket.emit("join_room", { roomId, username: user.username });

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error(err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId || msg.room === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };
    socket.on("receive_message", handleReceive);
    return () => socket.off("receive_message", handleReceive);
  }, [currentRoom?._id, user?.username]);

  /* ================= HANDLERS ================= */
  const onSendMessage = async (content, file = null) => {
    if (!currentRoom) return;
    try {
      let res;
      if (file) {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("roomId", currentRoom._id);
        res = await api.post("/messages/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" }
        });
      } else {
        res = await api.post("/messages", { roomId: currentRoom._id, content });
      }
      socket.emit("send_message", res.data);
    } catch (err) { console.error(err); }
  };

  const handleClearChat = async () => {
    if (window.confirm("Clear all messages?")) {
      try {
        await api.delete(`/messages/clear/${currentRoom._id}`);
        setMessages([]);
      } catch (err) { alert("Failed to clear chat"); }
    }
  };

  const handleDeleteRoom = async () => {
    if (window.confirm("Permanently delete this room? This action cannot be undone.")) {
      try {
        await api.delete(`/rooms/${currentRoom._id}`);
        setRooms(prev => prev.filter(r => r._id !== currentRoom._id));
        setCurrentRoom(null);
        setShowInfoModal(false);
      } catch (err) { alert("Delete failed"); }
    }
  };
const handleCreateRoom = async (e) => {
  e.preventDefault();
  try {
    const res = await api.post("/rooms", {
      name: newRoomData.name,
      isPrivate: newRoomData.isPrivate, // true or false from your form
    });

    // The backend should return the new room with a generated _id or inviteCode
    await fetchData(); // Refresh sidebar
    setCurrentRoom(res.data);
    setIsCreating(false);
    setNewRoomData({ name: "", isPrivate: false });
  } catch (err) {
    alert("Error creating room");
  }
};
  // ‚úÖ HANDLER TO CREATE/START DIRECT MESSAGE
  const handleSelectUser = async (targetUser) => {
    try {
      // Check if a DM already exists in our rooms list
      const existingDm = rooms.find(r => 
        r.isDirectMessage && 
        r.members?.some(m => (m.user?._id || m.user || m) === targetUser._id)
      );

      if (existingDm) {
        setCurrentRoom(existingDm);
      } else {
        // Create new DM room via backend
        const res = await api.post("/rooms/dm", { recipientId: targetUser._id });
        await fetchData(); // Refresh list to see the new DM
        setCurrentRoom(res.data);
      }
    } catch (err) {
      console.error("DM Error:", err);
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    alert("Copied!");
  };

  // ‚úÖ BULLETPROOF CATEGORIZATION LOGIC
  const channels = (rooms || []).filter(r => !r.isDirectMessage);
  const directMessages = (rooms || []).filter(r => r.isDirectMessage);

  const currentUserRealId = user?._id || user?.id;
  const roomOwnerId = currentRoom?.owner?._id || currentRoom?.owner;
  const isOwner = roomOwnerId && currentUserRealId && roomOwnerId.toString() === currentUserRealId.toString();

  if (loading) return <div className="vh-100 d-flex justify-content-center align-items-center">Loading‚Ä¶</div>;

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 overflow-hidden">
        <RoomList
          rooms={rooms || []}
          channels={channels}
          directMessages={directMessages}
          users={users || []}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={(room) => { setCurrentRoom(room); setIsCreating(false); }}
          onSelectUser={handleSelectUser} // Connects to the DM logic
          onJoinRoom={async (code) => {
             const res = await api.post(`/rooms/join`, { inviteCode: code });
             fetchData();
             if (res.data) setCurrentRoom(res.data);
          }}
          onCreateRoom={() => setIsCreating(true)}
          onLogout={handleLogout}
          onRefresh={fetchData}
        />

        <div className="flex-grow-1 bg-white d-flex flex-column">
          {isCreating ? (
            <div className="p-5 d-flex justify-content-center">
               <div className="card shadow-sm p-4 w-100" style={{maxWidth: '500px'}}>
                  <h3>Create New Room</h3>
                  <button className="btn btn-secondary mt-3" onClick={() => setIsCreating(false)}>Cancel</button>
               </div>
            </div>
          ) : currentRoom ? (
            <div className="d-flex flex-column h-100">

              <div className="d-flex justify-content-between align-items-center p-3 border-bottom bg-light">
                <h5 className="mb-0 fw-bold">
                    {currentRoom.isDirectMessage 
                      ? currentRoom.members?.find(m => (m.user?._id || m.user || m) !== currentUserRealId)?.user?.username || "Private Chat"
                      : currentRoom.name
                    }
                </h5>

                <div className="d-flex align-items-center gap-4">
                  <span style={{ cursor: "pointer", fontSize: "1.2rem" }} onClick={() => alert("Video Call")}>üìπ</span>
                  <span style={{ cursor: "pointer", fontSize: "1.2rem" }} onClick={() => alert("Audio Call")}>üìû</span>
                  <span style={{ cursor: "pointer", fontSize: "1.2rem" }} onClick={() => alert("Search")}>üîç</span>

                  <Dropdown align="end">
                    <Dropdown.Toggle variant="link" className="text-dark p-0 border-0 no-caret shadow-none">
                      <span style={{ fontSize: "1.6rem", cursor: "pointer" }}>‚ãÆ</span>
                    </Dropdown.Toggle>

                    <Dropdown.Menu style={{ minWidth: '220px', borderRadius: '12px', padding: '8px' }}>
                      <Dropdown.Item onClick={() => { fetchData(); setShowInfoModal(true); }}>‚ìò Contact info</Dropdown.Item>
                      <Dropdown.Item onClick={() => alert("Select mode")}>‚òë Select messages</Dropdown.Item>
                      <Dropdown.Item>üîï Mute notifications</Dropdown.Item>
                      <Dropdown.Item onClick={() => setCurrentRoom(null)}>‚úñ Close chat</Dropdown.Item>
                      <Dropdown.Divider />
                      <Dropdown.Item onClick={handleClearChat}>‚ûñ Clear chat</Dropdown.Item>

                      {isOwner && (
                        <Dropdown.Item 
                          onClick={handleDeleteRoom} 
                          className="bg-danger text-white rounded mt-2 py-2 text-center fw-bold"
                        >
                          üóë Delete chat
                        </Dropdown.Item>
                      )}
                    </Dropdown.Menu>
                  </Dropdown>
                </div>
              </div>

              <div className="flex-grow-1 overflow-hidden">
                <ChatBox messages={messages} currentUserId={currentUserRealId} onSendText={(txt) => onSendMessage(txt)} onSendFile={(f) => onSendMessage(null, f)} />
              </div>

              <Modal show={showInfoModal} onHide={() => setShowInfoModal(false)} centered scrollable>
                <Modal.Header closeButton>
                  <Modal.Title>Room Details</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  {/* ... modal content ... */}
                  <Button variant="outline-dark" className="w-100" onClick={() => { /* download history */ }}>üì• Download History</Button>
                </Modal.Body>
              </Modal>

            </div>
          ) : (
            <div className="h-100 d-flex flex-column justify-content-center align-items-center text-muted text-center p-4">
              <h4>Welcome to Chat</h4>
              <p>Select a room or a person to start talking!</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
