import React from "react";

function RoomList({ 
  rooms, 
  users, 
  currentUser, 
  currentRoomId, 
  onSelectRoom, 
  onSelectUser, 
  onJoinRoom, 
  onCreateRoom, 
  onLogout 
}) {
  
  // Filter rooms based on membership
  const myRooms = rooms.filter(r => r.members?.includes(currentUser?.id || currentUser?._id));
  const otherRooms = rooms.filter(r => !r.members?.includes(currentUser?.id || currentUser?._id) && !r.isPrivate);

  return (
    <div className="bg-light border-end d-flex flex-column shadow-sm" style={{ width: "280px", height: "100vh" }}>
      
      {/* 1. HEADER */}
      <div className="p-3 border-bottom bg-white">
        <h5 className="mb-0 fw-bold text-primary">ChatBox</h5>
      </div>

      <div className="flex-grow-1 overflow-auto custom-scrollbar p-2">
        
        {/* 2. MY CHANNELS */}
        <div className="p-2 fw-bold text-muted small text-uppercase">My Channels</div>
        <div className="list-group list-group-flush mb-3">
          {myRooms.map((room) => (
            <button
              key={room._id}
              onClick={() => onSelectRoom(room)}
              className={`list-group-item list-group-item-action border-0 rounded mb-1 d-flex align-items-center ${
                room._id === currentRoomId ? "active shadow-sm" : ""
              }`}
            >
              <span className="me-2">{room.isPrivate ? "ðŸ‘¤" : "#"}</span>
              <span className="text-truncate">{room.name || "Private Chat"}</span>
            </button>
          ))}
        </div>

        {/* 3. DISCOVER CHANNELS (Join Logic) */}
        {otherRooms.length > 0 && (
          <>
            <div className="p-2 fw-bold text-muted small text-uppercase">Discover</div>
            <div className="list-group list-group-flush mb-3">
              {otherRooms.map((room) => (
                <div key={room._id} className="list-group-item bg-transparent border-0 d-flex align-items-center justify-content-between p-1">
                  <span className="text-muted ps-2"># {room.name}</span>
                  <button 
                    className="btn btn-sm btn-outline-success py-0" 
                    onClick={() => onJoinRoom(room)}
                  >
                    Join
                  </button>
                </div>
              ))}
            </div>
          </>
        )}

        <hr />

        {/* 4. USERS LIST (Direct Messages) */}
        <div className="p-2 fw-bold text-muted small text-uppercase">Users</div>
        <div className="list-group list-group-flush">
          {users.filter(u => u._id !== (currentUser?.id || currentUser?._id)).map((u) => (
            <button
              key={u._id}
              onClick={() => onSelectUser(u)}
              className="list-group-item list-group-item-action border-0 rounded mb-1 d-flex align-items-center bg-transparent"
            >
              <div className="bg-secondary rounded-circle me-2" style={{ width: "8px", height: "8px" }}></div>
              <span className="small">{u.username}</span>
            </button>
          ))}
        </div>
      </div>

      {/* 5. FOOTER (Identity & Logout) */}
      <div className="p-3 border-top bg-white">
        <div className="d-flex align-items-center mb-3">
          <div className="bg-primary text-white rounded-circle d-flex justify-content-center align-items-center me-2" style={{ width: "35px", height: "35px" }}>
            {currentUser?.username?.charAt(0).toUpperCase()}
          </div>
          <div className="text-truncate">
            <div className="fw-bold small">{currentUser?.username}</div>
            <div className="text-muted" style={{ fontSize: "10px" }}>Online</div>
          </div>
        </div>
        <div className="d-grid gap-2">
          <button onClick={onCreateRoom} className="btn btn-sm btn-primary">+ Create Room</button>
          <button onClick={onLogout} className="btn btn-sm btn-outline-danger">Logout</button>
        </div>
      </div>

      <style>{`
        .custom-scrollbar::-webkit-scrollbar { width: 4px; }
        .custom-scrollbar::-webkit-scrollbar-thumb { background: #ccc; border-radius: 10px; }
        .list-group-item.active { background-color: #0d6efd !important; border-color: #0d6efd !important; }
      `}</style>
    </div>
  );
}

export default RoomList;
