import React, { useState, useRef, useEffect } from "react";

function ChatBox({ messages, onSendText, onSendFile, currentUserId }) {
  const [text, setText] = useState("");
  const [file, setFile] = useState(null);
  const scrollRef = useRef();

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    scrollRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSendText = (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    onSendText(text);
    setText("");
  };

  const handleSendFile = () => {
    if (!file) return;
    onSendFile(file);
    setFile(null);
    // Note: We don't clear text here in case user was typing a caption
  };

  return (
    <div className="card d-flex flex-column border-0 shadow-sm" style={{ height: "100%" }}>
      {/* Messages Body */}
      <div className="card-body overflow-auto bg-light p-4" style={{ flex: "1" }}>
        {messages.map((msg) => {
          // Check if the message was sent by the current logged-in user
          const isMe = msg.sender?._id === currentUserId || msg.sender === currentUserId;

          return (
            <div 
              key={msg._id} 
              className={`mb-3 d-flex flex-column ${isMe ? "align-items-end" : "align-items-start"}`}
            >
              <div 
                className={`p-2 px-3 rounded shadow-sm ${isMe ? "bg-primary text-white" : "bg-white border"}`} 
                style={{ maxWidth: "75%", width: "fit-content" }}
              >
                {!isMe && (
                  <small className="text-primary fw-bold d-block mb-1" style={{ fontSize: '0.75rem' }}>
                    {msg.sender?.username || "User"}
                  </small>
                )}
                
                {/* Text Content */}
                {msg.type === "text" && <p className="mb-0">{msg.content}</p>}

                {/* Image Content */}
                {msg.type === "image" && (
                  <div className="mt-1">
                    <img
                      src={`http://localhost:5000/${msg.content}`}
                      alt="uploaded"
                      className="img-fluid rounded"
                      style={{ maxHeight: "250px", cursor: "pointer" }}
                      onClick={() => window.open(`http://localhost:5000/${msg.content}`, '_blank')}
                    />
                  </div>
                )}

                {/* General File Attachment */}
                {msg.type === "file" && (
                  <div className={`mt-1 p-2 rounded border ${isMe ? "bg-white text-dark" : "bg-light"}`}>
                     ðŸ“Ž <a href={`http://localhost:5000/${msg.content}`} target="_blank" rel="noreferrer" className="text-decoration-none fw-bold">
                      View Attachment
                    </a>
                  </div>
                )}

                <small className={`d-block text-end mt-1 ${isMe ? "text-white-50" : "text-muted"}`} style={{ fontSize: '0.65rem' }}>
                  {new Date(msg.createdAt || Date.now()).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                </small>
              </div>
            </div>
          );
        })}
        <div ref={scrollRef} />
      </div>

      {/* Input Footer */}
      <div className="card-footer bg-white border-top p-3">
        {/* File Preview/Status Area */}
        {file && (
          <div className="alert alert-info py-1 px-2 mb-2 d-flex justify-content-between align-items-center">
            <small className="text-truncate" style={{ maxWidth: "200px" }}>ðŸ“Ž {file.name}</small>
            <button className="btn btn-sm btn-primary" onClick={handleSendFile}>Upload</button>
          </div>
        )}

        <form onSubmit={handleSendText}>
          <div className="input-group">
            {/* Hidden File Input */}
            <input
              type="file"
              id="file-input"
              hidden
              onChange={(e) => setFile(e.target.files[0])}
            />
            <button 
              className="btn btn-outline-secondary" 
              type="button" 
              onClick={() => document.getElementById('file-input').click()}
            >
              <i className="bi bi-paperclip"></i> ðŸ“Ž
            </button>
            
            <input
              type="text"
              className="form-control"
              placeholder="Write a message..."
              value={text}
              onChange={(e) => setText(e.target.value)}
            />
            <button className="btn btn-primary px-4" type="submit" disabled={!text.trim() && !file}>
              Send
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default ChatBox;
