import { useEffect, useState, useCallback, useRef } from "react";
import api from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";
import { Dropdown, Modal, Button, FormControl, ListGroup, Badge, Form } from "react-bootstrap"; 

/* --- Embedded Call Component --- */
const VideoCallOverlay = ({ currentRoom, user, type, onEnd }) => {
  const jitsiContainerRef = useRef(null);
  const jitsiApiRef = useRef(null);

  useEffect(() => {
    const domain = "meet.jit.si";
    const options = {
      roomName: `MernChat_Room_${currentRoom._id}`,
      width: "100%",
      height: "100%",
      parentNode: jitsiContainerRef.current,
      userInfo: { displayName: user?.username || "User" },
      configOverwrite: {
        startWithAudioMuted: false,
        startWithVideoMuted: type === 'audio',
        disableInviteFunctions: true
      },
    };
    jitsiApiRef.current = new window.JitsiMeetExternalAPI(domain, options);
    jitsiApiRef.current.addEventListener("videoConferenceLeft", onEnd);

    return () => {
      if (jitsiApiRef.current) jitsiApiRef.current.dispose();
    };
  }, [currentRoom._id, user, type, onEnd]);

  return (
    <div className="position-absolute top-0 start-0 w-100 h-100 bg-dark d-flex flex-column" style={{ zIndex: 1050 }}>
      <div className="p-2 d-flex justify-content-between align-items-center bg-black text-white border-bottom border-secondary">
        <span className="fw-bold px-2">{type === 'video' ? 'üìπ Video Call' : 'üìû Audio Call'}: {currentRoom.name}</span>
        <Button variant="danger" size="sm" onClick={onEnd}>End Call</Button>
      </div>
      <div ref={jitsiContainerRef} className="flex-grow-1" />
    </div>
  );
};

export default function Chat({ setAuth }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showInfoModal, setShowInfoModal] = useState(false);
  const [isCreating, setIsCreating] = useState(false);
  const [callConfig, setCallConfig] = useState({ active: false, type: 'video' });
  const [showSearchBar, setShowSearchBar] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
  });

  const fetchData = useCallback(async () => {
    try {
      const [roomsRes, usersRes] = await Promise.all([
        api.get("/rooms"),
        api.get("/auth/users"),
      ]);
      setRooms(roomsRes.data || []);
      setUsers(usersRes.data || []);
      if (currentRoom) {
        const updatedRoom = (roomsRes.data || []).find(r => r._id === currentRoom._id);
        if (updatedRoom) setCurrentRoom(updatedRoom);
      }
    } catch (err) {
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [currentRoom]);

  const handleLogout = useCallback(() => {
    if (setAuth) { setAuth(null); } 
    else { localStorage.clear(); window.location.href = "/login"; }
    socket.disconnect();
  }, [setAuth]);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
        socket.connect();
      } catch (e) { handleLogout(); }
    }
    fetchData();

    const handleRoomDeleted = ({ roomId }) => {
      setRooms((prev) => prev.filter((r) => r._id !== roomId));
      setCurrentRoom((prev) => {
        if (prev?._id === roomId) {
          setMessages([]);
          setShowInfoModal(false);
          return null;
        }
        return prev;
      });
    };

    socket.on("room_deleted", handleRoomDeleted);
    return () => {
      socket.off("room_deleted", handleRoomDeleted);
    };
  }, []); 

  useEffect(() => {
    if (!currentRoom || !user) return;
    const roomId = currentRoom._id;
    socket.emit("join_room", { roomId, username: user.username });

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error(err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId || msg.room === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };
    socket.on("receive_message", handleReceive);
    return () => socket.off("receive_message", handleReceive);
  }, [currentRoom?._id, user?.username]);

  /* ================= CLEANED HANDLERS ================= */

  const handleSelectRoom = async (room) => {
    const userId = user?._id || user?.id;
    const isMember = room.members?.some(m => (m.user?._id || m.user || m).toString() === userId?.toString());

    // HARD TRUTH: Public opens instantly. Private checks membership.
    if (!room.isPrivate || isMember) {
      setCurrentRoom(room);

      // Background join for public rooms so they stay in sidebar
      if (!isMember && !room.isPrivate) {
        try {
          await api.post(`/rooms/join-id/${room._id}`);
          fetchData(); 
        } catch (e) {}
      }
    } else {
      alert("This is a private room. Please enter the invite code to join.");
      return;
    }

    setIsCreating(false); 
    setCallConfig({active: false}); 
    setShowSearchBar(false);
    setSearchTerm("");
  };

  const handleJoinRoom = async (item) => {
    try {
      let res;
      if (typeof item === "object") {
        // From discover list (Public)
        res = await api.post(`/rooms/join-id/${item._id}`);
      } else {
        // From manual code entry (Private)
        const input = item.trim();
        res = await api.post(`/rooms/join-code/${input}`);
      }

      await fetchData(); 
      setCurrentRoom(res.data.room || res.data);
      setIsCreating(false);
    } catch (err) {
      if (err.response?.status !== 400) {
        alert(err.response?.data?.message || "Invalid Code.");
      }
    }
  };

  const onSendMessage = async (content, file = null) => {
    if (!currentRoom) return;
    try {
      let res;
      if (file) {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("roomId", currentRoom._id);
        res = await api.post("/messages/upload", formData, { headers: { "Content-Type": "multipart/form-data" } });
      } else {
        res = await api.post("/messages", { roomId: currentRoom._id, content });
      }
      socket.emit("send_message", res.data);
    } catch (err) { console.error(err); }
  };

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/rooms", { name: newRoomData.name, isPrivate: newRoomData.isPrivate });
      await fetchData(); 
      setCurrentRoom(res.data);
      setIsCreating(false);
      setNewRoomData({ name: "", isPrivate: false });
    } catch (err) { alert("Error creating room"); }
  };

  const handleSelectUser = async (targetUser) => {
    try {
      const existingDm = rooms.find(r => r.isDirectMessage && r.members?.some(m => (m.user?._id || m.user || m).toString() === targetUser._id.toString()));
      if (existingDm) { setCurrentRoom(existingDm); } 
      else {
        const res = await api.post("/rooms/private", { targetUserId: targetUser._id });
        await fetchData(); 
        setCurrentRoom(res.data);
      }
    } catch (err) { console.error("DM Error:", err); }
  };

  const channels = (rooms || []).filter(r => !r.isDirectMessage);
  const directMessages = (rooms || []).filter(r => r.isDirectMessage);
  const currentUserRealId = user?._id || user?.id;
  const isOwner = (currentRoom?.owner?._id || currentRoom?.owner)?.toString() === currentUserRealId?.toString();

  const getUniqueMembers = (members) => {
    const seen = new Set();
    return (members || []).filter(m => {
      const id = m.user?._id || m.user || m._id;
      if (!id || seen.has(id.toString())) return false;
      seen.add(id.toString());
      return true;
    });
  };

  const getDMName = () => {
    const other = currentRoom.members?.find(m => (m.user?._id || m.user || m).toString() !== currentUserRealId?.toString());
    return other?.user?.username || other?.username || "Private Chat";
  };

  if (loading) return <div className="vh-100 d-flex justify-content-center align-items-center">Loading‚Ä¶</div>;

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 overflow-hidden">
        <RoomList
          rooms={rooms}
          channels={channels}
          directMessages={directMessages}
          users={users}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={handleSelectRoom}
          onSelectUser={handleSelectUser}
          onJoinRoom={handleJoinRoom} 
          onCreateRoom={() => setIsCreating(true)}
          onLogout={handleLogout}
          onRefresh={fetchData}
          onDeleteRoom={(id) => api.delete(`/rooms/${id}`).then(() => fetchData())} 
        />

        <div className="flex-grow-1 bg-white d-flex flex-column position-relative">
          {callConfig.active && (
            <VideoCallOverlay currentRoom={currentRoom} user={user} type={callConfig.type} onEnd={() => setCallConfig({ active: false })} />
          )}

          {isCreating ? (
            <div className="p-5 d-flex justify-content-center">
              <div className="card shadow-sm p-4 w-100" style={{maxWidth: '500px'}}>
                <h3>Create New Room</h3>
                <Form onSubmit={handleCreateRoom}>
                  <Form.Group className="mb-3">
                    <Form.Label>Room Name</Form.Label>
                    <Form.Control type="text" required onChange={(e) => setNewRoomData({...newRoomData, name: e.target.value})} />
                  </Form.Group>
                  <Form.Check type="switch" label="Private Room" className="mb-4" checked={newRoomData.isPrivate} onChange={(e) => setNewRoomData({...newRoomData, isPrivate: e.target.checked})} />
                  <div className="d-flex gap-2">
                    <Button variant="primary" type="submit">Create</Button>
                    <Button variant="secondary" onClick={() => setIsCreating(false)}>Cancel</Button>
                  </div>
                </Form>
              </div>
            </div>
          ) : currentRoom ? (
            <div className="d-flex flex-column h-100">
              <div className="d-flex justify-content-between align-items-center p-3 border-bottom bg-light">
                <div className="d-flex align-items-center gap-3" style={{ cursor: "pointer" }} onClick={() => setShowInfoModal(true)}>
                  <h5 className="mb-0 fw-bold">{currentRoom.isDirectMessage ? getDMName() : currentRoom.name}</h5>
                  {currentRoom.isPrivate && <Badge bg="dark">üîí Private</Badge>}
                </div>
                <div className="d-flex gap-3">
                  <span className="text-secondary" style={{ cursor: "pointer" }} onClick={() => setShowSearchBar(!showSearchBar)}>üîç</span>
                  <Dropdown align="end">
                    <Dropdown.Toggle variant="link" className="text-secondary p-0 border-0 no-caret shadow-none">‚ãÆ</Dropdown.Toggle>
                    <Dropdown.Menu>
                      <Dropdown.Item onClick={() => setShowInfoModal(true)}>‚ìò Info</Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                </div>
              </div>
              {showSearchBar && (
                <div className="p-2 border-bottom bg-light">
                  <FormControl type="text" placeholder="Search messages..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} autoFocus />
                </div>
              )}
              <div className="flex-grow-1 overflow-hidden">
                <ChatBox messages={messages.filter(m => m.content?.toLowerCase().includes(searchTerm.toLowerCase()))} currentUserId={currentUserRealId} onSendText={(txt) => onSendMessage(txt)} />
              </div>
            </div>
          ) : (
            <div className="h-100 d-flex justify-content-center align-items-center text-muted">Select a chat to start</div>
          )}
        </div>
      </div>

      {/* FIXED MODAL: NO INTERNAL IDS */}
      <Modal show={showInfoModal} onHide={() => setShowInfoModal(false)} centered>
        <Modal.Header closeButton><Modal.Title>Room Info</Modal.Title></Modal.Header>
        <Modal.Body className="text-center py-4">
          <h4>{currentRoom?.isDirectMessage ? getDMName() : currentRoom?.name}</h4>
          <hr />

          {currentRoom?.isPrivate && !currentRoom.isDirectMessage && (
            <div className="mb-4 text-start">
              <label className="fw-bold small text-muted d-block mb-1">6-DIGIT INVITE CODE</label>
              <div className="d-flex gap-2">
                <FormControl readOnly className="bg-light fw-bold text-center fs-5" value={currentRoom.inviteCode} />
                <Button variant="outline-primary" onClick={() => {
                  navigator.clipboard.writeText(currentRoom.inviteCode);
                  alert("Copied!");
                }}>Copy</Button>
              </div>
            </div>
          )}

          <div className="text-start">
            <label className="small text-muted fw-bold mb-2">MEMBERS ({getUniqueMembers(currentRoom?.members).length})</label>
            <ListGroup variant="flush" className="border rounded">
              {getUniqueMembers(currentRoom?.members).map((m, idx) => (
                <ListGroup.Item key={idx}>üë§ {m.user?.username || m.username || "User"}</ListGroup.Item>
              ))}
            </ListGroup>
          </div>
        </Modal.Body>
      </Modal>
    </div>
  );
}
