import { useEffect, useState } from "react";
import { api } from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket"; 
import { useNavigate } from "react-router-dom";

export default function Chat({ setToken }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]); 
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const navigate = useNavigate();

  // 1. Initial Data Fetching
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");

    if (!storedUser || !token) {
      navigate("/login");
      return;
    }

    const parsedUser = JSON.parse(storedUser);
    setUser(parsedUser);
    socket.connect(); 

    // Load Rooms & Users
    Promise.all([
      api.get("/rooms"),
      api.get("/auth/users")
    ]).then(([roomsRes, usersRes]) => {
      setRooms(roomsRes.data);
      setUsers(usersRes.data);
      // Optional: Set the first room you are already a member of as default
      const joinedRoom = roomsRes.data.find(r => r.members.includes(parsedUser.id || parsedUser._id));
      if (joinedRoom) setCurrentRoom(joinedRoom);
    }).catch(err => {
      if (err.response?.status === 401) handleLogout();
    });

    socket.on("room_created", (newRoom) => {
      setRooms((prev) => {
        const exists = prev.find(r => r._id === newRoom._id);
        return exists ? prev : [...prev, newRoom];
      });
    });

    return () => {
      socket.off("room_created");
      socket.disconnect();
    };
  }, [navigate]);

  // 2. Room Synchronization
  useEffect(() => {
    if (!currentRoom) return;

    // Only join the socket room if the user is a member of the room in the DB
    const isMember = currentRoom.members?.includes(user?.id || user?._id);
    if (!isMember && !currentRoom.isPrivate) return; 

    socket.emit("join_room", currentRoom._id);

    api.get(`/messages/${currentRoom._id}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error("Error loading messages:", err));

    const handleReceive = (msg) => {
      if (msg.roomId === currentRoom._id) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("receive_message", handleReceive);

    return () => {
      socket.off("receive_message", handleReceive);
      socket.emit("leave_room", currentRoom._id);
    };
  }, [currentRoom, user]);

  // --- NEW: JOIN ROOM LOGIC ---
  const handleJoinRoom = async (room) => {
    try {
      // API call to add the current user to the members array
      const res = await api.post(`/rooms/${room._id}/join`);
      const updatedRoom = res.data;

      // Update rooms list with new membership data
      setRooms(prev => prev.map(r => r._id === updatedRoom._id ? updatedRoom : r));
      setCurrentRoom(updatedRoom);
    } catch (err) {
      console.error("Join room error:", err);
      alert("Failed to join room.");
    }
  };

  // Interaction: Start 1-on-1 Chat
  const handleSelectUser = async (targetUser) => {
    if (targetUser._id === user._id || targetUser.id === user.id) return;
    try {
      const res = await api.post("/rooms/private", { targetUserId: targetUser._id });
      const privateRoom = res.data;
      setRooms((prev) => {
        const exists = prev.find(r => r._id === privateRoom._id);
        return exists ? prev : [...prev, privateRoom];
      });
      setCurrentRoom(privateRoom);
    } catch (err) {
      console.error("Error opening private chat:", err);
    }
  };

  const handleCreateRoom = async () => {
    const roomName = prompt("Enter new room name:");
    if (!roomName || !roomName.trim()) return;
    try {
      const res = await api.post("/rooms", { 
        name: roomName.trim(),
        createdBy: user.id || user._id 
      });
      const newRoom = res.data;
      setRooms((prev) => [...prev, newRoom]);
      setCurrentRoom(newRoom);
      socket.emit("create_room", newRoom);
    } catch (err) {
      alert("Failed to create room.");
    }
  };

  const sendTextMessage = (text) => {
    if (!currentRoom || !text.trim() || !user) return;
    const messageData = {
      roomId: currentRoom._id,
      content: text,
      sender: user.id || user._id, 
      type: "text",
    };
    socket.emit("send_message", messageData);
  };

  const sendFileMessage = async (file) => {
    if (!currentRoom || !user) return;
    setIsUploading(true);
    const formData = new FormData();
    formData.append("file", file);
    formData.append("roomId", currentRoom._id);
    formData.append("senderId", user.id || user._id);
    try {
      await api.post("/messages/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
    } catch (err) {
      alert("Upload failed.");
    } finally {
      setIsUploading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    if (setToken) setToken(null);
    socket.disconnect();
    navigate("/login");
  };

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 shadow-sm">
        <RoomList
          rooms={rooms}
          users={users}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={setCurrentRoom}
          onSelectUser={handleSelectUser}
          onJoinRoom={handleJoinRoom} // Pass Join function to Sidebar
          onCreateRoom={handleCreateRoom} 
          onLogout={handleLogout}
        />

        <div className="d-flex flex-column flex-grow-1 bg-white">
          {currentRoom ? (
            <div className="d-flex flex-column h-100 p-0">
              <div className="p-3 border-bottom d-flex justify-content-between align-items-center bg-white shadow-sm">
                <div>
                  <h5 className="mb-0 fw-bold text-dark">
                    {currentRoom.isPrivate ? "ðŸ‘¤ " : "# "} 
                    {currentRoom.name || "Private Chat"}
                  </h5>
                </div>
                <button onClick={handleLogout} className="btn btn-outline-danger btn-sm">Logout</button>
              </div>

              <div className="flex-grow-1 overflow-hidden p-3 bg-light">
                {/* Logic: Only show ChatBox if user is a member */}
                {currentRoom.members?.includes(user?.id || user?._id) ? (
                  <ChatBox 
                    messages={messages} 
                    onSendText={sendTextMessage} 
                    onSendFile={sendFileMessage}
                    currentUserId={user?.id || user?._id}
                  />
                ) : (
                  <div className="h-100 d-flex flex-column justify-content-center align-items-center">
                    <h3>You are not a member of #{currentRoom.name}</h3>
                    <button className="btn btn-success btn-lg mt-3" onClick={() => handleJoinRoom(currentRoom)}>
                      Join Room to Start Chatting
                    </button>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div className="d-flex flex-grow-1 justify-content-center align-items-center">
              <div className="text-center">
                <h4 className="text-muted">Welcome to ChatBox</h4>
                <p>Select a user to message or join a room from the sidebar.</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
