import React, { useState } from "react";

function ChatBox({ messages, onSendText, onSendFile }) {
  const [text, setText] = useState("");
  const [file, setFile] = useState(null);

  const handleSendText = () => {
    if (!text.trim()) return;
    onSendText(text);
    setText("");
  };

  const handleSendFile = () => {
    if (!file) return;
    onSendFile(file);
    setFile(null);
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", height: "100%" }}>
      {/* Messages */}
      <div
        style={{
          flex: 1,
          overflowY: "auto",
          border: "1px solid #ccc",
          padding: "10px",
          marginBottom: "8px",
        }}
      >
        {messages.map((msg) => (
          <div key={msg._id} style={{ marginBottom: "8px" }}>
            <strong>{msg.sender?.username || "User"}:</strong>{" "}
            {msg.type === "text" && msg.content}

            {msg.type === "image" && (
              <div>
                <img
                  src={`http://localhost:5000/${msg.content}`}
                  alt="uploaded"
                  style={{ maxWidth: "200px", marginTop: "5px" }}
                />
              </div>
            )}

            {msg.type === "file" && (
              <div>
                <a
                  href={`http://localhost:5000/${msg.content}`}
                  target="_blank"
                  rel="noreferrer"
                >
                  Download file
                </a>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Input */}
      <div style={{ display: "flex", gap: "6px", flexWrap: "wrap" }}>
        <input
          type="text"
          value={text}
          placeholder="Type a message"
          onChange={(e) => setText(e.target.value)}
          style={{ flex: 1, padding: "8px" }}
        />

        <input
          type="file"
          onChange={(e) => setFile(e.target.files[0])}
        />

        <button onClick={handleSendText}>Send</button>
        <button onClick={handleSendFile}>Upload</button>
      </div>
    </div>
  );
}

export default ChatBox;
