import { useEffect, useState, useCallback } from "react";
import api from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";

export default function Chat({ setToken }) {
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const [isCreating, setIsCreating] = useState(false);
  const [isJoining, setIsJoining] = useState(false);

  const [inviteCodeInput, setInviteCodeInput] = useState("");
  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
    showHistoryToNewMembers: true,
  });

  const navigate = useNavigate();

  /* ================= LOGOUT ================= */
  const handleLogout = useCallback(() => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    if (setToken) setToken(null);
    socket.disconnect();
    navigate("/login", { replace: true });
  }, [navigate, setToken]);

  /* ================= INITIAL LOAD ================= */
  useEffect(() => {
    const storedUser = localStorage.getItem("user");

    // ðŸ”´ DO NOT force logout here
    // Let routing handle auth protection
    if (storedUser) {
      setUser(JSON.parse(storedUser));
      socket.connect();
    }

    const fetchData = async () => {
      try {
        const [roomsRes, usersRes] = await Promise.all([
          api.get("/rooms"),
          api.get("/auth/users"),
        ]);
        setRooms(roomsRes.data);
        setUsers(usersRes.data);
      } catch (err) {
        console.error("Chat load error:", err);
        if (err.response?.status === 401) {
          handleLogout();
        }
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    return () => {
      socket.off("receive_message");
      socket.disconnect();
    };
  }, [handleLogout]);

  /* ================= ROOM SYNC ================= */
  useEffect(() => {
    if (!currentRoom || !user) return;

    const roomId = currentRoom._id;

    socket.emit("join_room", {
      roomId,
      username: user.username,
    });

    api
      .get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error("Message fetch error:", err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("receive_message", handleReceive);

    return () => {
      socket.off("receive_message", handleReceive);
    };
  }, [currentRoom, user]);

  /* ================= SEND MESSAGE ================= */
  const onSendMessage = async (content, file = null) => {
    if (!currentRoom) return;

    try {
      let res;

      if (file) {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("roomId", currentRoom._id);
        res = await api.post("/messages/upload", formData);
      } else {
        res = await api.post("/messages", {
          roomId: currentRoom._id,
          content,
        });
      }

      socket.emit("send_message", res.data);
    } catch (err) {
      console.error("Send error:", err);
    }
  };

  /* ================= CREATE ROOM ================= */
  const handleCreateSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/rooms", newRoomData);
      setRooms((prev) => [...prev, res.data]);
      setCurrentRoom(res.data);
      setIsCreating(false);
      setNewRoomData({ name: "", isPrivate: false, showHistoryToNewMembers: true });

      if (res.data.isPrivate) {
        alert(`Invite Code: ${res.data.inviteCode}`);
      }
    } catch (err) {
      alert(err.response?.data?.message || "Room creation failed");
    }
  };

  /* ================= JOIN BY CODE ================= */
  const handleJoinByCode = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post(`/rooms/join/${inviteCodeInput}`);
      setRooms((prev) =>
        prev.find((r) => r._id === res.data._id) ? prev : [...prev, res.data]
      );
      setCurrentRoom(res.data);
      setInviteCodeInput("");
      setIsJoining(false);
    } catch (err) {
      alert(err.response?.data?.message || "Invalid invite code");
    }
  };

  /* ================= PRIVATE CHAT ================= */
  const handleSelectUser = async (targetUser) => {
    try {
      const res = await api.post("/rooms/private", {
        targetUserId: targetUser._id,
      });
      setRooms((prev) =>
        prev.find((r) => r._id === res.data._id) ? prev : [...prev, res.data]
      );
      setCurrentRoom(res.data);
      setIsCreating(false);
      setIsJoining(false);
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) {
    return (
      <div className="vh-100 d-flex justify-content-center align-items-center">
        Loadingâ€¦
      </div>
    );
  }

  /* ================= UI ================= */
  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 overflow-hidden">
        <RoomList
          rooms={rooms}
          users={users}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={(room) => {
            setCurrentRoom(room);
            setIsCreating(false);
            setIsJoining(false);
          }}
          onSelectUser={handleSelectUser}
          onCreateRoom={() => {
            setIsCreating(true);
            setIsJoining(false);
          }}
          onLogout={handleLogout}
        />

        <div className="flex-grow-1 bg-white d-flex flex-column">
          {isCreating ? (
            <form onSubmit={handleCreateSubmit} className="p-4">
              <input
                className="form-control mb-2"
                placeholder="Room name"
                value={newRoomData.name}
                onChange={(e) =>
                  setNewRoomData({ ...newRoomData, name: e.target.value })
                }
                required={!newRoomData.isPrivate}
              />
              <button className="btn btn-primary w-100">Create</button>
            </form>
          ) : currentRoom ? (
            <ChatBox
              messages={messages}
              currentUserId={user?._id || user?.id}
              onSendText={(txt) => onSendMessage(txt)}
              onSendFile={(file) => onSendMessage(null, file)}
            />
          ) : (
            <div className="h-100 d-flex justify-content-center align-items-center text-muted">
              Select a room to start chatting
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
