import { useEffect, useState } from "react";
import { socket } from "../services/socket";
import { api } from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";

export default function Chat() {
  const [rooms, setRooms] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);

  // --------------------
  // Load rooms on mount
  // --------------------
  useEffect(() => {
    api.get("/rooms")
      .then((res) => {
        setRooms(res.data);
        if (res.data.length > 0) {
          setCurrentRoom(res.data[0]);
        }
      })
      .catch((err) => {
        console.error("Failed to load rooms:", err);
      });
  }, []);

  // --------------------
  // Join room & load messages
  // --------------------
  useEffect(() => {
    if (!currentRoom) return;

    socket.emit("join_room", currentRoom._id);

    api.get(`/messages/${currentRoom._id}`)
      .then((res) => {
        setMessages(res.data);
      })
      .catch((err) => {
        console.error("Failed to load messages:", err);
      });

    const handleReceive = (msg) => {
      setMessages((prev) => [...prev, msg]);
    };

    socket.on("receive_message", handleReceive);

    return () => {
      socket.off("receive_message", handleReceive);
    };
  }, [currentRoom]);

  // --------------------
  // Send text message
  // --------------------
  const sendTextMessage = (text) => {
    if (!currentRoom) return;

    socket.emit("send_message", {
      roomId: currentRoom._id,
      content: text,
      type: "text",
    });
  };

  // --------------------
  // Send file / image
  // --------------------
  const sendFileMessage = async (file) => {
    if (!currentRoom || !file) return;

    const formData = new FormData();
    formData.append("file", file);
    formData.append("roomId", currentRoom._id);
    formData.append(
      "type",
      file.type.startsWith("image/") ? "image" : "file"
    );

    try {
      const res = await api.post("/messages/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      // message will also come via socket, but this ensures UI update
      setMessages((prev) => [...prev, res.data]);
    } catch (err) {
      console.error("File upload failed:", err);
    }
  };

  // --------------------
  // Render
  // --------------------
  return (
    <div style={{ display: "flex", height: "100vh" }}>
      <RoomList
        rooms={rooms}
        currentRoomId={currentRoom?._id}
        onSelectRoom={setCurrentRoom}
      />

      <div style={{ flex: 1, padding: "10px" }}>
        {currentRoom ? (
          <>
            <h3>{currentRoom.name}</h3>
            <ChatBox
              messages={messages}
              onSendText={sendTextMessage}
              onSendFile={sendFileMessage}
            />
          </>
        ) : (
          <p>Select a room to start chatting</p>
        )}
      </div>
    </div>
  );
}
