import { useEffect, useState, useCallback } from "react";
import api from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";
import { Dropdown, Modal, Button, InputGroup, FormControl, ListGroup, Badge, Form } from "react-bootstrap"; 

export default function Chat({ setAuth }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showInfoModal, setShowInfoModal] = useState(false);

  // State for Joining by Code
  const [showJoinModal, setShowJoinModal] = useState(false);
  const [joinCode, setJoinCode] = useState("");

  const [isCreating, setIsCreating] = useState(false);
  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
  });

  const navigate = useNavigate();

  /* ================= FETCH DATA ================= */
  const fetchData = useCallback(async () => {
    try {
      const [roomsRes, usersRes] = await Promise.all([
        api.get("/rooms"),
        api.get("/auth/users"),
      ]);
      setRooms(roomsRes.data || []);
      setUsers(usersRes.data || []);

      if (currentRoom) {
        const updatedRoom = (roomsRes.data || []).find(r => r._id === currentRoom._id);
        if (updatedRoom) setCurrentRoom(updatedRoom);
      }
    } catch (err) {
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [currentRoom]);

  const handleLogout = useCallback(() => {
    if (setAuth) { setAuth(null); } 
    else { localStorage.clear(); window.location.href = "/login"; }
    socket.disconnect();
  }, [setAuth]);

  /* ================= INITIAL LOAD ================= */
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
        socket.connect();
      } catch (e) { handleLogout(); }
    }
    fetchData();
    return () => {
      socket.off("receive_message");
      socket.disconnect();
    };
  }, []); 

  /* ================= ROOM SYNC ================= */
  useEffect(() => {
    if (!currentRoom || !user) return;
    const roomId = currentRoom._id;
    socket.emit("join_room", { roomId, username: user.username });

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error(err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId || msg.room === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };
    socket.on("receive_message", handleReceive);
    return () => socket.off("receive_message", handleReceive);
  }, [currentRoom?._id, user?.username]);

  /* ================= HANDLERS ================= */

  // ‚úÖ NEW: Join Room using 6-Digit Code
  const handleJoinByCode = async (e) => {
    e.preventDefault();
    try {
      // Backend should look for 'roomCode' or 'shortId'
      const res = await api.post("/rooms/join-code", { code: joinCode });
      await fetchData(); 
      setCurrentRoom(res.data);
      setShowJoinModal(false);
      setJoinCode("");
    } catch (err) {
      alert(err.response?.data?.message || "Invalid Code or Room not found");
    }
  };

  const handleJoinRoom = async (id) => {
    try {
      const res = await api.post("/rooms/join", { roomId: id });
      await fetchData(); 
      setCurrentRoom(res.data);
    } catch (err) {
      alert(err.response?.data?.message || "Error joining room");
    }
  };

  const handleDeleteRoomById = async (roomId) => {
    if (window.confirm("Permanently delete this room?")) {
      try {
        await api.delete(`/rooms/${roomId}`);
        setRooms((prev) => prev.filter((r) => r._id !== roomId));
        if (currentRoom?._id === roomId) {
          setCurrentRoom(null);
          setMessages([]);
        }
        setShowInfoModal(false);
      } catch (err) {
        alert(err.response?.data?.message || "Delete failed.");
      }
    }
  };

  const onSendMessage = async (content, file = null) => {
    if (!currentRoom) return;
    try {
      let res;
      if (file) {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("roomId", currentRoom._id);
        res = await api.post("/messages/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" }
        });
      } else {
        res = await api.post("/messages", { roomId: currentRoom._id, content });
      }
      socket.emit("send_message", res.data);
    } catch (err) { console.error(err); }
  };

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    try {
      // ‚úÖ Back-end should generate a 6-digit code upon creation
      const res = await api.post("/rooms", {
        name: newRoomData.name,
        isPrivate: newRoomData.isPrivate,
      });
      await fetchData(); 
      setCurrentRoom(res.data);
      setIsCreating(false);
      setNewRoomData({ name: "", isPrivate: false });
    } catch (err) { alert("Error creating room"); }
  };

  const handleSelectUser = async (targetUser) => {
    try {
      const existingDm = rooms.find(r => 
        r.isDirectMessage && 
        r.members?.some(m => (m.user?._id || m.user || m) === targetUser._id)
      );
      if (existingDm) {
        setCurrentRoom(existingDm);
      } else {
        const res = await api.post("/rooms/dm", { recipientId: targetUser._id });
        await fetchData(); 
        setCurrentRoom(res.data);
      }
    } catch (err) { console.error("DM Error:", err); }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    alert("Code Copied!");
  };

  /* ================= CALCULATIONS ================= */
  const channels = (rooms || []).filter(r => !r.isDirectMessage);
  const directMessages = (rooms || []).filter(r => r.isDirectMessage);
  const currentUserRealId = user?._id || user?.id;
  const roomOwnerId = currentRoom?.owner?._id || currentRoom?.owner;
  const isOwner = roomOwnerId && currentUserRealId && roomOwnerId.toString() === currentUserRealId.toString();

  const getDMName = () => {
    const otherMember = currentRoom.members?.find(m => (m.user?._id || m.user || m) !== currentUserRealId);
    return otherMember?.user?.username || otherMember?.username || "Private Chat";
  };

  if (loading) return <div className="vh-100 d-flex justify-content-center align-items-center">Loading‚Ä¶</div>;

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 overflow-hidden">
        <div className="d-flex flex-column border-end bg-white" style={{ width: "350px" }}>
            <RoomList
                rooms={rooms || []}
                channels={channels}
                directMessages={directMessages}
                users={users || []}
                currentUser={user}
                currentRoomId={currentRoom?._id}
                onSelectRoom={(room) => { setCurrentRoom(room); setIsCreating(false); }}
                onSelectUser={handleSelectUser}
                onJoinRoom={(room) => handleJoinRoom(room._id)}
                onCreateRoom={() => setIsCreating(true)}
                onLogout={handleLogout}
                onRefresh={fetchData}
                onDeleteRoom={handleDeleteRoomById} 
            />
            {/* ‚úÖ ADDED JOIN BY CODE BUTTON AT SIDEBAR BOTTOM */}
            <div className="p-3 border-top mt-auto">
                <Button variant="outline-primary" className="w-100 fw-bold" onClick={() => setShowJoinModal(true)}>
                    + Join with Code
                </Button>
            </div>
        </div>

        <div className="flex-grow-1 bg-white d-flex flex-column">
          {isCreating ? (
            <div className="p-5 d-flex justify-content-center">
              <div className="card shadow-sm p-4 w-100" style={{maxWidth: '500px'}}>
                <h3>Create New Room</h3>
                <Form onSubmit={handleCreateRoom}>
                  <Form.Group className="mb-3">
                    <Form.Label>Room Name</Form.Label>
                    <Form.Control type="text" required onChange={(e) => setNewRoomData({...newRoomData, name: e.target.value})} />
                  </Form.Group>
                  <Form.Check type="switch" label="Private Room (Hidden)" className="mb-4" checked={newRoomData.isPrivate} onChange={(e) => setNewRoomData({...newRoomData, isPrivate: e.target.checked})} />
                  <div className="d-flex gap-2">
                    <Button variant="primary" type="submit">Create</Button>
                    <Button variant="secondary" onClick={() => setIsCreating(false)}>Cancel</Button>
                  </div>
                </Form>
              </div>
            </div>
          ) : currentRoom ? (
            <div className="d-flex flex-column h-100">
              {/* HEADER */}
              <div className="d-flex justify-content-between align-items-center p-3 border-bottom bg-light">
                <div className="d-flex align-items-center gap-3" style={{ cursor: "pointer" }} onClick={() => setShowInfoModal(true)}>
                  <div className="bg-secondary rounded-circle d-flex align-items-center justify-content-center text-white" style={{ width: "40px", height: "40px" }}>
                    {currentRoom.isDirectMessage ? "üë§" : "üë•"}
                  </div>
                  <div>
                    <h5 className="mb-0 fw-bold">
                        {currentRoom.isDirectMessage ? getDMName() : currentRoom.name}
                    </h5>
                    {!currentRoom.isDirectMessage && <small className="text-muted">Code: {currentRoom.roomCode || "N/A"}</small>}
                  </div>
                </div>

                <div className="d-flex align-items-center gap-3">
                  {isOwner && !currentRoom.isDirectMessage && (
                    <button className="btn btn-outline-danger btn-sm rounded-circle d-flex align-items-center justify-content-center border-0" style={{ width: "35px", height: "35px" }} onClick={(e) => { e.stopPropagation(); handleDeleteRoomById(currentRoom._id); }}>
                      üóëÔ∏è
                    </button>
                  )}
                  <span className="text-secondary" style={{ cursor: "pointer", fontSize: "1.3rem" }}>üìπ</span>
                  <span className="text-secondary" style={{ cursor: "pointer", fontSize: "1.3rem" }}>üìû</span>
                  <div className="vr mx-1" style={{ height: "20px" }}></div>
                  <span className="text-secondary" style={{ cursor: "pointer", fontSize: "1.3rem" }}>üîç</span>
                  <Dropdown align="end">
                    <Dropdown.Toggle variant="link" className="text-secondary p-0 border-0 no-caret shadow-none"><span style={{ fontSize: "1.6rem" }}>‚ãÆ</span></Dropdown.Toggle>
                    <Dropdown.Menu>
                      <Dropdown.Item onClick={() => setShowInfoModal(true)}>‚ìò Contact info</Dropdown.Item>
                      <Dropdown.Item onClick={() => setCurrentRoom(null)}>‚úñ Close chat</Dropdown.Item>
                      <Dropdown.Divider />
                      {isOwner && !currentRoom.isDirectMessage && (
                        <Dropdown.Item onClick={() => handleDeleteRoomById(currentRoom._id)} className="text-danger fw-bold">üóë Delete Room</Dropdown.Item>
                      )}
                    </Dropdown.Menu>
                  </Dropdown>
                </div>
              </div>

              <div className="flex-grow-1 overflow-hidden">
                <ChatBox messages={messages} currentUserId={currentUserRealId} onSendText={(txt) => onSendMessage(txt)} onSendFile={(f) => onSendMessage(null, f)} />
              </div>

              {/* INFO MODAL */}
              <Modal show={showInfoModal} onHide={() => setShowInfoModal(false)} centered>
                <Modal.Header closeButton>
                  <Modal.Title>Info</Modal.Title>
                </Modal.Header>
                <Modal.Body className="text-center">
                  <h4>{currentRoom.isDirectMessage ? getDMName() : currentRoom.name}</h4>

                  {!currentRoom.isDirectMessage && (
                    <div className="mt-3 p-3 bg-light border rounded">
                      <p className="mb-1 fw-bold text-muted">SHARE JOIN CODE</p>
                      <h2 className="letter-spacing-2 fw-bold text-primary mb-2">
                        {currentRoom.roomCode || "No Code"}
                      </h2>
                      <Button size="sm" variant="primary" onClick={() => copyToClipboard(currentRoom.roomCode)}>Copy Code</Button>
                    </div>
                  )}
                </Modal.Body>
              </Modal>
            </div>
          ) : (
            <div className="h-100 d-flex justify-content-center align-items-center text-muted">
              <h4>Select a chat to start messaging</h4>
            </div>
          )}
        </div>

        {/* ‚úÖ MODAL TO JOIN BY CODE */}
        <Modal show={showJoinModal} onHide={() => setShowJoinModal(false)} centered>
          <Modal.Header closeButton>
            <Modal.Title>Join Room by Code</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form onSubmit={handleJoinByCode}>
              <Form.Group>
                <Form.Label>Enter 6-Digit Code</Form.Label>
                <Form.Control 
                  type="text" 
                  placeholder="e.g. 123456" 
                  maxLength={6} 
                  required 
                  value={joinCode}
                  onChange={(e) => setJoinCode(e.target.value)}
                  className="text-center fw-bold fs-4"
                />
              </Form.Group>
              <Button type="submit" className="w-100 mt-3" variant="success">Join Room</Button>
            </Form>
          </Modal.Body>
        </Modal>

      </div>
    </div>
  );
}
