import React from "react";

function RoomList({ rooms, currentRoomId, onSelectRoom }) {
  return (
    <div className="bg-light border-end d-flex flex-column" style={{ width: "260px", height: "80vh" }}>
      <div className="p-3 border-bottom bg-white d-flex justify-content-between align-items-center">
        <h5 className="mb-0 fw-bold text-primary">Channels</h5>
        <span className="badge bg-primary rounded-pill">{rooms.length}</span>
      </div>

      <div className="list-group list-group-flush overflow-auto flex-grow-1 p-2">
        {rooms.length === 0 && (
          <div className="text-center mt-4 text-muted small">No rooms found.</div>
        )}

        {rooms.map((room) => (
          <button
            key={room._id}
            onClick={() => onSelectRoom(room)}
            className={`list-group-item list-group-item-action border-0 rounded mb-1 d-flex align-items-center ${
              room._id === currentRoomId ? "active shadow-sm" : ""
            }`}
          >
            <span className="me-2">
              {room.isPrivate ? "ðŸ”’" : "#"}
            </span>
            <span className="text-truncate" style={{ maxWidth: "180px" }}>
              {room.name}
            </span>
            
            {/* Optional: Online indicator or unread dot could go here */}
            {room._id === currentRoomId && (
              <div className="ms-auto spinner-grow spinner-grow-sm text-light" role="status"></div>
            )}
          </button>
        ))}
      </div>
      
      {/* Create Room Button could go here */}
      <div className="p-3 border-top bg-white">
        <button className="btn btn-outline-primary btn-sm w-100">+ Create Room</button>
      </div>
    </div>
  );
}

export default RoomList;
