import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

// Page Imports
import Login from "./pages/Login";
import Register from "./pages/Register";
import Chat from "./pages/Chat";

/**
 * App Component
 * Handles Global State for Authentication and Protected Routing
 */
function App() {
  // Initialize state directly from localStorage to prevent flickering
  const [token, setToken] = useState(localStorage.getItem("token"));

  /**
   * Centralized Authentication Handler
   * Updates both LocalStorage and React State simultaneously
   */
  const handleSetToken = (newToken) => {
    if (newToken) {
      localStorage.setItem("token", newToken);
    } else {
      localStorage.removeItem("token");
      localStorage.removeItem("user"); // Clean up user profile on logout
    }
    setToken(newToken);
  };

  const isAuthenticated = !!token;

  return (
    <Router 
      future={{ 
        v7_startTransition: true, 
        v7_relativeSplatPath: true 
      }}
    >
      <div className="bg-light" style={{ minHeight: "100vh" }}>
        <Routes>
          {/* PUBLIC ROUTES */}
          {/* If logged in, redirect away from Login/Register to Chat */}
          <Route 
            path="/login" 
            element={
              !isAuthenticated ? (
                <Login setToken={handleSetToken} />
              ) : (
                <Navigate to="/chat" replace />
              )
            } 
          />
          <Route 
            path="/register" 
            element={
              !isAuthenticated ? (
                <Register setToken={handleSetToken} />
              ) : (
                <Navigate to="/chat" replace />
              )
            } 
          />

          {/* PROTECTED ROUTES */}
          {/* If NOT logged in, redirect to Login */}
          <Route 
            path="/chat" 
            element={
              isAuthenticated ? (
                <Chat setToken={handleSetToken} />
              ) : (
                <Navigate to="/login" replace />
              )
            } 
          />

          {/* FALLBACK ROUTE */}
          <Route 
            path="*" 
            element={<Navigate to={isAuthenticated ? "/chat" : "/login"} replace />} 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
