import { useEffect, useState, useCallback } from "react";
import api, { uploadFile } from "../services/api"; 
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";

export default function Chat({ setToken }) {
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const [isCreating, setIsCreating] = useState(false);
  const [isJoining, setIsJoining] = useState(false);

  const [inviteCodeInput, setInviteCodeInput] = useState("");
  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
    showHistoryToNewMembers: false,
  });

  const navigate = useNavigate();

  /* ================= LOGOUT ================= */
  const handleLogout = useCallback(() => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    if (setToken) setToken(null);
    socket.disconnect();
    navigate("/login");
  }, [navigate, setToken]);

  /* ================= INITIAL LOAD ================= */
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");

    if (!storedUser || !token) {
      handleLogout();
      return;
    }

    setUser(JSON.parse(storedUser));
    socket.connect();

    /**
     * Unified Fetch Data logic
     * Stops execution if token is missing to prevent 401/500 errors
     */
    const fetchData = async () => {
      const activeToken = localStorage.getItem("token");
      if (!activeToken) return; 

      setLoading(true);
      try {
        const [roomsRes, usersRes] = await Promise.all([
          api.get("/rooms"),
          api.get("/auth/users"),
        ]);
        setRooms(roomsRes.data);
        setUsers(usersRes.data);
      } catch (err) {
        console.error("Error fetching data:", err.response?.data || err.message);
        if (err.response?.status === 401) handleLogout();
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    socket.on("room_created", (room) => {
      setRooms((prev) => (prev.find((r) => r._id === room._id) ? prev : [...prev, room]));
    });

    return () => {
      socket.off("room_created");
    };
  }, [handleLogout]);

  /* ================= ROOM SYNC ================= */
  useEffect(() => {
    if (!currentRoom || !user) return;

    const roomId = currentRoom._id;
    socket.emit("join_room", { roomId });

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error("Message fetch error:", err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("receive_message", handleReceive);

    return () => {
      socket.off("receive_message", handleReceive);
      socket.emit("leave_room", { roomId });
    };
  }, [currentRoom, user]);

  /* ================= HANDLERS ================= */
  const onSendMessage = async (content, file = null) => {
    if (!currentRoom) return;
    try {
      let newMessage;
      if (file) {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("roomId", currentRoom._id);
        formData.append("type", file.type.startsWith("image/") ? "image" : "file");
        const res = await uploadFile(formData);
        newMessage = res.data;
      } else {
        const res = await api.post("/messages", { roomId: currentRoom._id, content });
        newMessage = res.data;
      }
      socket.emit("send_message", newMessage);
      setMessages((prev) => [...prev, newMessage]);
    } catch (err) {
      console.error("Send error:", err);
    }
  };

  const handleCreateSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/rooms", {
        name: newRoomData.name.trim(),
        isPrivate: newRoomData.isPrivate,
        showHistoryToNewMembers: newRoomData.showHistoryToNewMembers,
      });
      setRooms((prev) => [...prev, res.data]);
      setCurrentRoom(res.data);
      setIsCreating(false);
      setNewRoomData({ name: "", isPrivate: false, showHistoryToNewMembers: false });
    } catch (err) {
      alert(err.response?.data?.message || "Room creation failed");
    }
  };

  const handleJoinByCode = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/rooms/join", { inviteCode: inviteCodeInput });
      setRooms((prev) => [...prev, res.data]);
      setCurrentRoom(res.data);
      setInviteCodeInput("");
      setIsJoining(false);
    } catch (err) {
      alert(err.response?.data?.message || "Invalid invite code");
    }
  };

  const handleSelectUser = async (targetUser) => {
    try {
      const res = await api.post("/rooms/private", { targetUserId: targetUser._id });
      setRooms((prev) => (prev.find((r) => r._id === res.data._id) ? prev : [...prev, res.data]));
      setCurrentRoom(res.data);
      setIsCreating(false);
      setIsJoining(false);
    } catch (err) { console.error(err); }
  };

  /* ================= RENDERING ================= */
  if (!user) return null;
  if (loading) return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div className="spinner-border text-primary" role="status">
        <span className="visually-hidden">Loading...</span>
      </div>
    </div>
  );

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 shadow-sm overflow-hidden">
        {/* Sidebar */}
        <div className="col-md-3 border-end bg-white d-flex flex-column">
          <div className="p-3 border-bottom d-flex justify-content-between align-items-center">
            <h4 className="text-primary fw-bold mb-0">ChatBox</h4>
            <button className="btn btn-sm btn-outline-danger" onClick={handleLogout}>Logout</button>
          </div>
          <div className="p-3 border-bottom d-grid gap-2">
            <button className="btn btn-primary btn-sm" onClick={() => { setIsCreating(true); setIsJoining(false); }}>+ Create Room</button>
            <button className="btn btn-outline-secondary btn-sm" onClick={() => { setIsJoining(true); setIsCreating(false); }}># Join by Code</button>
          </div>
          <div className="flex-grow-1 overflow-auto">
            <RoomList
              rooms={rooms}
              users={users}
              currentUser={user}
              currentRoomId={currentRoom?._id}
              onSelectRoom={setCurrentRoom}
              onSelectUser={handleSelectUser}
            />
          </div>
        </div>

        {/* Main Content Area */}
        <div className="flex-grow-1 bg-white d-flex flex-column">
          {isCreating ? (
            <div className="p-5 w-50">
              <h3>Create Room</h3>
              <form onSubmit={handleCreateSubmit}>
                <input className="form-control mb-2" placeholder="Room Name" value={newRoomData.name} onChange={e => setNewRoomData({...newRoomData, name: e.target.value})} required />
                <div className="form-check mb-3">
                  <input className="form-check-input" type="checkbox" checked={newRoomData.isPrivate} onChange={e => setNewRoomData({...newRoomData, isPrivate: e.target.checked})} id="privateCheck" />
                  <label className="form-check-label" htmlFor="privateCheck">Private Room</label>
                </div>
                <button type="submit" className="btn btn-success me-2">Create</button>
                <button type="button" className="btn btn-link" onClick={() => setIsCreating(false)}>Cancel</button>
              </form>
            </div>
          ) : isJoining ? (
            <div className="p-5 w-50">
              <h3>Join Room</h3>
              <form onSubmit={handleJoinByCode}>
                <input className="form-control mb-2" placeholder="Enter Invite Code" value={inviteCodeInput} onChange={e => setInviteCodeInput(e.target.value.toUpperCase())} required />
                <button type="submit" className="btn btn-primary me-2">Join</button>
                <button type="button" className="btn btn-link" onClick={() => setIsJoining(false)}>Cancel</button>
              </form>
            </div>
          ) : currentRoom ? (
            <ChatBox messages={messages} currentUserId={user._id || user.id} onSendMessage={onSendMessage} roomName={currentRoom.name} />
          ) : (
            <div className="h-100 d-flex justify-content-center align-items-center text-muted">
              Select a room or user to start chatting
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
