import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import Login from "./pages/Login";
import Register from "./pages/Register";
import Chat from "./pages/Chat";

function App() {
  // Initialize state directly from localStorage to avoid extra renders
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    setToken(storedToken);
    setLoading(false);
  }, []);

  // Helper to logout/clear token from everywhere
  const handleSetToken = (newToken) => {
    if (newToken) {
      localStorage.setItem("token", newToken);
    } else {
      localStorage.removeItem("token");
    }
    setToken(newToken);
  };

  const isAuthenticated = !!token;

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: "100vh" }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <Router 
      future={{ 
        v7_startTransition: true, 
        v7_relativeSplatPath: true 
      }}
    >
      <div className="bg-light" style={{ minHeight: "100vh" }}>
        <Routes>
          {/* If authenticated, don't allow access to Login/Register */}
          <Route 
            path="/login" 
            element={!isAuthenticated ? <Login setToken={handleSetToken} /> : <Navigate to="/chat" replace />} 
          />
          <Route 
            path="/register" 
            element={!isAuthenticated ? <Register /> : <Navigate to="/chat" replace />} 
          />

          {/* If NOT authenticated, don't allow access to Chat */}
          <Route 
            path="/chat" 
            element={isAuthenticated ? <Chat setToken={handleSetToken} /> : <Navigate to="/login" replace />} 
          />

          {/* Fallback: If logged in, go to chat. If not, go to login. */}
          <Route path="*" element={<Navigate to={isAuthenticated ? "/chat" : "/login"} replace />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
