import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

// Import your components
// ðŸ’¡ DOUBLE CHECK: Ensure these files exist in src/pages/ with these exact names
import Login from "./pages/Login";
import Register from "./pages/Register";
import Chat from "./pages/Chat";

function App() {
  // 1. Use State for the token so the UI updates immediately on Login/Logout
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check for token on initial load
    const storedToken = localStorage.getItem("token");
    setToken(storedToken);
    setLoading(false);
  }, []);

  // 2. Helper function to check auth
  const isAuthenticated = !!token;

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: "100vh" }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="bg-light" style={{ minHeight: "100vh" }}>
        <Routes>
          {/* Public Routes: Redirect to /chat if already logged in */}
          <Route 
            path="/login" 
            element={!isAuthenticated ? <Login setToken={setToken} /> : <Navigate to="/chat" />} 
          />
          <Route 
            path="/register" 
            element={!isAuthenticated ? <Register /> : <Navigate to="/chat" />} 
          />

          {/* Protected Route: Redirect to /login if NOT logged in */}
          <Route 
            path="/chat" 
            element={isAuthenticated ? <Chat setToken={setToken} /> : <Navigate to="/login" />} 
          />

          {/* Default Route: Catch-all */}
          <Route path="*" element={<Navigate to="/chat" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
