import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

// Import your components
import Login from "./pages/Login";
import Register from "./pages/Register";
import Chat from "./pages/Chat";

function App() {
  // Check if token exists to determine if user is logged in
  const isAuthenticated = !!localStorage.getItem("token");

  return (
    <Router>
      <div className="bg-light" style={{ minHeight: "100vh" }}>
        <Routes>
          {/* Public Routes: If logged in, redirect away from Login/Register to Chat */}
          <Route 
            path="/login" 
            element={!isAuthenticated ? <Login /> : <Navigate to="/chat" />} 
          />
          <Route 
            path="/register" 
            element={!isAuthenticated ? <Register /> : <Navigate to="/chat" />} 
          />

          {/* Protected Route: If NOT logged in, redirect to Login */}
          <Route 
            path="/chat" 
            element={isAuthenticated ? <Chat /> : <Navigate to="/login" />} 
          />

          {/* Default Route: Send everything else to /chat (which redirects to login if needed) */}
          <Route path="*" element={<Navigate to="/chat" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
