import { useEffect, useState, useCallback, useRef } from "react";
import api from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";
import { Dropdown, Modal, Button, FormControl, ListGroup, Badge, Form } from "react-bootstrap"; 

/* --- Embedded Call Component --- */
const VideoCallOverlay = ({ currentRoom, user, type, onEnd }) => {
  const jitsiContainerRef = useRef(null);
  const jitsiApiRef = useRef(null);

  useEffect(() => {
    const domain = "meet.jit.si";
    const options = {
      roomName: `MernChat_Room_${currentRoom._id}`,
      width: "100%",
      height: "100%",
      parentNode: jitsiContainerRef.current,
      userInfo: { displayName: user?.username || "User" },
      configOverwrite: {
        startWithAudioMuted: false,
        startWithVideoMuted: type === 'audio',
        disableInviteFunctions: true
      },
    };
    jitsiApiRef.current = new window.JitsiMeetExternalAPI(domain, options);
    jitsiApiRef.current.addEventListener("videoConferenceLeft", onEnd);

    return () => {
      if (jitsiApiRef.current) jitsiApiRef.current.dispose();
    };
  }, [currentRoom._id, user, type, onEnd]);

  return (
    <div className="position-absolute top-0 start-0 w-100 h-100 bg-dark d-flex flex-column animate-pop-in" style={{ zIndex: 1050 }}>
      <div className="p-2 d-flex justify-content-between align-items-center bg-black text-white border-bottom border-secondary">
        <span className="fw-bold px-2">{type === 'video' ? 'üìπ Video Call' : 'üìû Audio Call'}: {currentRoom.name}</span>
        <Button variant="danger" size="sm" onClick={onEnd}>End Call</Button>
      </div>
      <div ref={jitsiContainerRef} className="flex-grow-1" />
    </div>
  );
};

export default function Chat({ setAuth }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showInfoModal, setShowInfoModal] = useState(false);
  const [isCreating, setIsCreating] = useState(false);
  const [callConfig, setCallConfig] = useState({ active: false, type: 'video' });
  const [searchTerm, setSearchTerm] = useState(""); // For the search feature

  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
    showHistoryToNewMembers: true,
  });

  const navigate = useNavigate();

  const fetchData = useCallback(async () => {
    try {
      const [roomsRes, usersRes] = await Promise.all([
        api.get("/rooms"),
        api.get("/auth/users"),
      ]);
      setRooms(roomsRes.data || []);
      setUsers(usersRes.data || []);

      if (currentRoom) {
        const updatedRoom = (roomsRes.data || []).find(r => r._id === currentRoom._id);
        if (updatedRoom) setCurrentRoom(updatedRoom);
      }
    } catch (err) {
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [currentRoom]);

  const handleLogout = useCallback(() => {
    if (setAuth) { setAuth(null); } 
    else { localStorage.clear(); window.location.href = "/login"; }
    socket.disconnect();
  }, [setAuth]);

  const onSendMessage = async (content, file = null) => {
    if (!currentRoom) return;
    try {
      let res;
      if (file) {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("roomId", currentRoom._id);
        res = await api.post("/messages/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" }
        });
      } else {
        res = await api.post("/messages", { roomId: currentRoom._id, content });
      }
      socket.emit("send_message", res.data);
    } catch (err) { console.error(err); }
  };

  const handleStartCall = (type) => {
    if (!currentRoom) return;
    setCallConfig({ active: true, type });
    onSendMessage(`üìû Started a ${type} call. Join me by clicking the icon above!`);
  };

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
        socket.connect();
      } catch (e) { handleLogout(); }
    }
    fetchData();
    return () => {
      socket.off("receive_message");
      socket.disconnect();
    };
  }, []); 

  useEffect(() => {
    if (!currentRoom || !user) return;
    const roomId = currentRoom._id;
    socket.emit("join_room", { roomId, username: user.username });

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error(err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId || msg.room === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };
    socket.on("receive_message", handleReceive);
    return () => socket.off("receive_message", handleReceive);
  }, [currentRoom?._id, user?.username]);

  const handleJoinRoom = async (item) => {
    try {
      let res;
      const input = typeof item === "string" ? item.trim() : item._id;
      if (input.length === 6 && !input.includes('-')) {
        res = await api.post(`/rooms/join-code/${input}`);
      } else {
        res = await api.post(`/rooms/join-id/${input}`);
      }
      await fetchData(); 
      setCurrentRoom(res.data.room || res.data);
      setIsCreating(false);
    } catch (err) {
      alert(err.response?.data?.message || "Error joining room.");
    }
  };

  const handleClearChat = async () => {
    if (window.confirm("Clear all messages?")) {
      try {
        await api.delete(`/messages/clear/${currentRoom._id}`);
        setMessages([]);
      } catch (err) { alert("Failed to clear chat"); }
    }
  };

  const handleDeleteRoomById = async (roomId) => {
    if (window.confirm("Permanently delete this room?")) {
      try {
        await api.delete(`/rooms/${roomId}`);
        setRooms((prev) => prev.filter((r) => r._id !== roomId));
        if (currentRoom?._id === roomId) {
          setCurrentRoom(null);
          setMessages([]);
        }
        setShowInfoModal(false);
      } catch (err) { alert("Delete failed."); }
    }
  };

  const handleSelectUser = async (targetUser) => {
    try {
      const existingDm = rooms.find(r => 
        r.isDirectMessage && 
        r.members?.some(m => (m.user?._id || m.user || m) === targetUser._id)
      );
      if (existingDm) {
        setCurrentRoom(existingDm);
      } else {
        const res = await api.post("/rooms/private", { targetUserId: targetUser._id });
        await fetchData(); 
        setCurrentRoom(res.data);
      }
    } catch (err) { console.error("DM Error:", err); }
  };

  const getDMName = () => {
    const otherMember = currentRoom.members?.find(m => (m.user?._id || m.user || m) !== (user?._id || user?.id));
    return otherMember?.user?.username || otherMember?.username || "Private Chat";
  };

  if (loading) return <div className="vh-100 d-flex justify-content-center align-items-center">Loading‚Ä¶</div>;

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 overflow-hidden">
        <RoomList
          rooms={rooms || []}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={(room) => { setCurrentRoom(room); setIsCreating(false); setCallConfig({active: false}); }}
          onSelectUser={handleSelectUser}
          onJoinRoom={handleJoinRoom} 
          onCreateRoom={() => setIsCreating(true)}
          onLogout={handleLogout}
          onRefresh={fetchData}
        />

        <div className="flex-grow-1 d-flex flex-column position-relative bg-white">
          {callConfig.active && (
            <VideoCallOverlay 
              currentRoom={currentRoom} 
              user={user} 
              type={callConfig.type} 
              onEnd={() => setCallConfig({ active: false })} 
            />
          )}

          {currentRoom ? (
            <div className="d-flex flex-column h-100 animate-pop-in">
              {/* --- UPDATED HEADER WITH SEARCH AND ROOM NAME --- */}
              <div className="d-flex justify-content-between align-items-center p-3 border-bottom glass-panel bg-light shadow-sm">
                <div className="d-flex align-items-center gap-3" style={{ cursor: "pointer" }} onClick={() => setShowInfoModal(true)}>
                  <div className="bg-primary rounded-circle d-flex align-items-center justify-content-center text-white" style={{ width: "40px", height: "40px" }}>
                    {currentRoom.isDirectMessage ? "üë§" : "üë•"}
                  </div>
                  <h5 className="mb-0 fw-bold text-dark">
                    {currentRoom.isDirectMessage ? getDMName() : (currentRoom.isPrivate ? `üîí ${currentRoom.name}` : `# ${currentRoom.name}`)}
                  </h5>
                </div>

                <div className="d-flex align-items-center gap-3">
                  {/* Search Icon */}
                  <span className="text-secondary" style={{ cursor: "pointer", fontSize: "1.2rem" }} title="Search Messages">üîç</span>

                  {/* Call Icons */}
                  <span className="text-secondary" style={{ cursor: "pointer", fontSize: "1.3rem" }} onClick={() => handleStartCall('video')} title="Video Call">üìπ</span>
                  <span className="text-secondary" style={{ cursor: "pointer", fontSize: "1.3rem" }} onClick={() => handleStartCall('audio')} title="Audio Call">üìû</span>

                  <div className="vr mx-1" style={{ height: "20px" }}></div>

                  <Dropdown align="end">
                    <Dropdown.Toggle variant="link" className="text-secondary p-0 border-0 no-caret shadow-none">
                      <span style={{ fontSize: "1.6rem" }}>‚ãÆ</span>
                    </Dropdown.Toggle>
                    <Dropdown.Menu style={{ borderRadius: '12px' }}>
                      <Dropdown.Item onClick={() => setShowInfoModal(true)}>‚ìò Group info</Dropdown.Item>
                      <Dropdown.Item onClick={handleClearChat}>‚ûñ Clear messages</Dropdown.Item>
                      <Dropdown.Item onClick={() => setCurrentRoom(null)}>‚úñ Close chat</Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                </div>
              </div>

              <div className="flex-grow-1 overflow-hidden">
                <ChatBox 
                  messages={messages} 
                  currentUserId={user?._id || user?.id} 
                  onSendText={onSendMessage} 
                  onSendFile={(f) => onSendMessage(null, f)} 
                />
              </div>
            </div>
          ) : (
            <div className="h-100 d-flex flex-column justify-content-center align-items-center text-muted">
              <h4>Welcome back, {user?.username}</h4>
              <p>Select a room to begin</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
