import { useEffect, useState, useCallback } from "react";
import api from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";
import { Dropdown, Modal, Button, InputGroup, FormControl, ListGroup, Badge } from "react-bootstrap"; 

export default function Chat({ setAuth }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showInfoModal, setShowInfoModal] = useState(false);

  const [isCreating, setIsCreating] = useState(false);
  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
    showHistoryToNewMembers: true,
  });

  const navigate = useNavigate();

  const fetchData = useCallback(async () => {
    try {
      const [roomsRes, usersRes] = await Promise.all([
        api.get("/rooms"),
        api.get("/auth/users"),
      ]);
      setRooms(roomsRes.data);
      setUsers(usersRes.data);

      // If we have a current room, refresh its data to get updated members
      if (currentRoom) {
        const updatedRoom = roomsRes.data.find(r => r._id === currentRoom._id);
        if (updatedRoom) setCurrentRoom(updatedRoom);
      }
    } catch (err) {
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [currentRoom]);

  const handleLogout = useCallback(() => {
    if (setAuth) { setAuth(null); } 
    else { localStorage.clear(); window.location.href = "/login"; }
    socket.disconnect();
  }, [setAuth]);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
        socket.connect();
      } catch (e) { handleLogout(); }
    }
    fetchData();
    return () => {
      socket.off("receive_message");
      socket.disconnect();
    };
  }, [handleLogout]); // Removed fetchData to prevent infinite loops, handled inside fetchData

  useEffect(() => {
    if (!currentRoom || !user) return;
    const roomId = currentRoom._id;
    socket.emit("join_room", { roomId, username: user.username });

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error(err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId || msg.room === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };
    socket.on("receive_message", handleReceive);
    return () => socket.off("receive_message", handleReceive);
  }, [currentRoom, user]);

  const onSendMessage = async (content, file = null) => {
    if (!currentRoom) return;
    try {
      let res;
      if (file) {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("roomId", currentRoom._id);
        res = await api.post("/messages/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" }
        });
      } else {
        res = await api.post("/messages", { roomId: currentRoom._id, content });
      }
      socket.emit("send_message", res.data);
    } catch (err) { console.error(err); }
  };

  const handleClearChat = async () => {
    if (window.confirm("Clear all messages?")) {
      try {
        await api.delete(`/messages/clear/${currentRoom._id}`);
        setMessages([]);
      } catch (err) { alert("Failed to clear chat"); }
    }
  };

  const handleDeleteRoom = async () => {
    if (window.confirm("Permanently delete this room?")) {
      try {
        await api.delete(`/rooms/${currentRoom._id}`);
        setRooms(rooms.filter(r => r._id !== currentRoom._id));
        setCurrentRoom(null);
        setShowInfoModal(false);
      } catch (err) { alert("Delete failed"); }
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    alert("Copied to clipboard!");
  };

  if (loading) return <div className="vh-100 d-flex justify-content-center align-items-center">Loading‚Ä¶</div>;

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 overflow-hidden">
        <RoomList
          rooms={rooms}
          users={users}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={(room) => { setCurrentRoom(room); setIsCreating(false); }}
          onJoinRoom={async (code) => {
             const res = await api.post(`/rooms/join`, { inviteCode: code });
             fetchData();
             if (res.data) setCurrentRoom(res.data);
          }}
          onCreateRoom={() => setIsCreating(true)}
          onLogout={handleLogout}
        />

        <div className="flex-grow-1 bg-white d-flex flex-column">
          {isCreating ? (
            <div className="p-5 d-flex justify-content-center">
               <div className="card shadow-sm p-4 w-100" style={{maxWidth: '500px'}}>
                  <h3>Create New Room</h3>
                  <button className="btn btn-secondary mt-3" onClick={() => setIsCreating(false)}>Cancel</button>
               </div>
            </div>
          ) : currentRoom ? (
            <div className="d-flex flex-column h-100">
              <div className="d-flex justify-content-between align-items-center p-3 border-bottom bg-light">
                <h5 className="mb-0 fw-bold">{currentRoom.name}</h5>

                <Dropdown align="end">
                  <Dropdown.Toggle variant="link" className="text-dark p-0 border-0 no-caret shadow-none">
                    <span style={{ fontSize: "1.6rem", cursor: "pointer", padding: '0 10px' }}>‚ãÆ</span>
                  </Dropdown.Toggle>

                  <Dropdown.Menu>
                    <Dropdown.Item onClick={() => { fetchData(); setShowInfoModal(true); }}>‚ÑπÔ∏è Room Info</Dropdown.Item>
                    <Dropdown.Item onClick={() => alert("Audio Call...")}>üìû Audio Call</Dropdown.Item>
                    <Dropdown.Item onClick={() => alert("Video Call...")}>üìπ Video Call</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item onClick={() => alert("Select mode enabled")}>üìë Select Messages</Dropdown.Item>
                    <Dropdown.Item onClick={handleClearChat} className="text-danger">üßπ Clear Chat</Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </div>

              <div className="flex-grow-1 overflow-hidden">
                <ChatBox messages={messages} currentUserId={user?._id || user?.id} onSendText={(txt) => onSendMessage(txt)} onSendFile={(f) => onSendMessage(null, f)} />
              </div>

              {/* UPDATED ROOM INFO MODAL WITH MEMBERS */}
              <Modal show={showInfoModal} onHide={() => setShowInfoModal(false)} centered scrollable>
                <Modal.Header closeButton>
                  <Modal.Title>Room Details</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  <div className="mb-3">
                    <label className="small text-muted fw-bold">INVITE CODE</label>
                    <InputGroup>
                      <FormControl readOnly value={currentRoom.inviteCode} className="bg-light fw-bold text-center" style={{letterSpacing: '2px'}} />
                      <Button variant="outline-primary" onClick={() => copyToClipboard(currentRoom.inviteCode)}>Copy</Button>
                    </InputGroup>
                  </div>

                  <div className="mb-3">
                    <label className="small text-muted fw-bold">ROOM ID</label>
                    <InputGroup size="sm">
                      <FormControl readOnly value={currentRoom._id} className="bg-light text-muted" />
                      <Button variant="outline-secondary" onClick={() => copyToClipboard(currentRoom._id)}>Copy</Button>
                    </InputGroup>
                  </div>

                  <hr />

                  {/* MEMBERS SECTION */}
                  <div className="mb-4">
                    <label className="small text-muted fw-bold d-block mb-2">
                      MEMBERS ({currentRoom.members?.length || 0})
                    </label>
                    <ListGroup variant="flush" className="border rounded bg-light" style={{maxHeight: '200px', overflowY: 'auto'}}>
                      {currentRoom.members && currentRoom.members.length > 0 ? (
                        currentRoom.members.map((member, index) => (
                          <ListGroup.Item key={index} className="bg-transparent d-flex justify-content-between align-items-center">
                            <span>üë§ {member.username || "Unknown User"}</span>
                            {member._id === currentRoom.owner && (
                              <Badge bg="warning" text="dark">Admin</Badge>
                            )}
                          </ListGroup.Item>
                        ))
                      ) : (
                        <ListGroup.Item className="bg-transparent italic text-muted">No members found</ListGroup.Item>
                      )}
                    </ListGroup>
                  </div>

                  <div className="d-flex flex-column gap-2">
                    <Button variant="outline-dark" onClick={() => { /* download history logic */ }}>üì• Download History</Button>
                    {currentRoom.owner === (user?._id || user?.id) && (
                      <Button variant="danger" className="fw-bold mt-2" onClick={handleDeleteRoom}>‚ùå Delete Room Permanently</Button>
                    )}
                  </div>
                </Modal.Body>
              </Modal>

            </div>
          ) : (
            <div className="h-100 d-flex flex-column justify-content-center align-items-center text-muted">
              <h4>Welcome</h4>
              <p>Select a channel to start.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
