import { useEffect, useState } from "react";
import { socket } from "../services/socket";
import { api } from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";

export default function Chat() {
  const [rooms] = useState(["general", "tech", "random"]);
  const [currentRoom, setCurrentRoom] = useState("general");
  const [messages, setMessages] = useState([]);

  const username = "User"; // later can be taken from auth token

  // Load messages & join room
  useEffect(() => {
    socket.emit("join_room", currentRoom);

    api.get(`/messages/${currentRoom}`).then((res) => {
      setMessages(res.data);
    });

    const handleReceive = (msg) => {
      setMessages((prev) => [...prev, msg]);
    };

    socket.on("receive_message", handleReceive);

    return () => {
      socket.off("receive_message", handleReceive);
    };
  }, [currentRoom]);

  // Send message
  const sendMessage = (text) => {
    socket.emit("send_message", {
      roomId: currentRoom,
      content: text,
      sender: username,
    });
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      <RoomList
        rooms={rooms}
        currentRoom={currentRoom}
        onSelectRoom={setCurrentRoom}
      />

      <div style={{ flex: 1 }}>
        <h3 style={{ padding: "10px" }}>Room: {currentRoom}</h3>
        <ChatBox messages={messages} onSend={sendMessage} />
      </div>
    </div>
  );
}
