import { useEffect, useState, useCallback } from "react";
import { api } from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket"; 
import { useNavigate } from "react-router-dom";

export default function Chat({ setToken }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]); 
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const navigate = useNavigate();

  // Consistently handles session cleanup
  const handleLogout = useCallback(() => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    if (setToken) setToken(null);
    socket.disconnect();
    navigate("/login");
  }, [navigate, setToken]);

  // 1. Initial Data Fetching with Privacy Filtering
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");

    if (!storedUser || !token) {
      handleLogout();
      return;
    }

    const parsedUser = JSON.parse(storedUser);
    setUser(parsedUser);
    const userId = parsedUser.id || parsedUser._id;
    socket.connect(); 

    const fetchData = async () => {
      try {
        const [roomsRes, usersRes] = await Promise.all([
          api.get("/rooms"),
          api.get("/auth/users")
        ]);

        /**
         * FILTER LOGIC:
         * 1. Show all Public rooms (!isPrivate)
         * 2. Show Private rooms only if the user is in the 'members' array
         */
        const authorizedRooms = roomsRes.data.filter(room => 
          !room.isPrivate || room.members.some(m => (m._id || m) === userId)
        );

        setRooms(authorizedRooms);
        setUsers(usersRes.data);

        if (authorizedRooms.length > 0) setCurrentRoom(authorizedRooms[0]);
      } catch (err) {
        if (err.response?.status === 401) handleLogout();
      }
    };

    fetchData();

    // Listen for new rooms created by others
    socket.on("room_created", (newRoom) => {
      setRooms((prev) => {
        const isAuthorized = !newRoom.isPrivate || newRoom.members.includes(userId);
        const exists = prev.find(r => r._id === newRoom._id);
        if (isAuthorized && !exists) return [...prev, newRoom];
        return prev;
      });
    });

    return () => {
      socket.off("room_created");
      socket.disconnect();
    };
  }, [handleLogout]);

  // 2. Room & Message Synchronization
  useEffect(() => {
    if (!currentRoom || !user) return;

    const roomId = currentRoom._id;
    socket.emit("join_room", roomId);

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error("History error:", err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId || msg.room === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("receive_message", handleReceive);

    return () => {
      socket.off("receive_message", handleReceive);
      socket.emit("leave_room", roomId);
    };
  }, [currentRoom, user]);

  // --- ACTIONS: CREATE (WITH CHOICE) ---

  const handleCreateRoom = async () => {
    const roomName = prompt("Enter new room name:");
    if (!roomName?.trim()) return;

    // The Choice: Private or Public
    const isPrivate = window.confirm("Create as a PRIVATE room? (Cancel for Public)");

    try {
      const res = await api.post("/rooms", { 
        name: roomName.trim(),
        isPrivate: isPrivate 
      });
      const newRoom = res.data;

      setRooms(prev => [...prev, newRoom]);
      setCurrentRoom(newRoom);
      socket.emit("create_room", newRoom); 
    } catch (err) {
      alert("Failed to create room. Name might be taken.");
    }
  };

  const handleSelectUser = async (targetUser) => {
    const myId = user?.id || user?._id;
    if (targetUser._id === myId) return;
    try {
      const res = await api.post("/rooms/private", { targetUserId: targetUser._id });
      const privateRoom = res.data;
      setRooms(prev => (prev.find(r => r._id === privateRoom._id) ? prev : [...prev, privateRoom]));
      setCurrentRoom(privateRoom);
    } catch (err) {
      console.error("DM error:", err);
    }
  };

  const sendTextMessage = (text) => {
    if (!currentRoom || !text.trim() || !user) return;
    socket.emit("send_message", {
      roomId: currentRoom._id,
      content: text,
      sender: user.id || user._id, 
      type: "text",
    });
  };

  const sendFileMessage = async (file) => {
    if (!currentRoom || !user) return;
    setIsUploading(true);
    const formData = new FormData();
    formData.append("file", file);
    formData.append("roomId", currentRoom._id);
    formData.append("senderId", user.id || user._id);
    try {
      await api.post("/messages/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
    } catch (err) {
      alert("Upload failed.");
    } finally { setIsUploading(false); }
  };

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 shadow-sm overflow-hidden">
        <RoomList
          rooms={rooms}
          users={users}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={setCurrentRoom}
          onSelectUser={handleSelectUser}
          onCreateRoom={handleCreateRoom} 
          onLogout={handleLogout}
        />

        <div className="d-flex flex-column flex-grow-1 bg-white position-relative">
          {isUploading && (
            <div className="position-absolute top-0 w-100 bg-primary text-white text-center py-1 z-3">
              Uploading...
            </div>
          )}

          {currentRoom ? (
            <div className="d-flex flex-column h-100">
              <div className="p-3 border-bottom d-flex justify-content-between align-items-center">
                <h5 className="mb-0 fw-bold">
                  {currentRoom.isPrivate ? "ðŸ”’ Private: " : "# "} {currentRoom.name}
                </h5>
              </div>

              <div className="flex-grow-1 overflow-hidden bg-light">
                {/* Security check: Ensure user is a member of the current private room */}
                {(currentRoom.isPrivate && !currentRoom.members.some(m => (m._id || m) === (user?.id || user?._id))) ? (
                  <div className="h-100 d-flex flex-column justify-content-center align-items-center">
                    <h3>Private Channel</h3>
                    <p>You need an invite to join this conversation.</p>
                  </div>
                ) : (
                  <ChatBox 
                    messages={messages} 
                    onSendText={sendTextMessage} 
                    onSendFile={sendFileMessage}
                    currentUserId={user?.id || user?._id}
                  />
                )}
              </div>
            </div>
          ) : (
            <div className="h-100 d-flex flex-column justify-content-center align-items-center text-center">
              <h4 className="text-muted">Welcome to ChatBox</h4>
              <p>Select a user or create a room to get started.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
