import React, { useState } from "react";
import { Modal, Button, Form } from 'react-bootstrap';
import api from "../services/api";

function RoomList({ 
  rooms, 
  users, 
  currentUser, 
  currentRoomId, 
  onSelectRoom, 
  onSelectUser, 
  onJoinRoom, 
  onCreateRoom, 
  onLogout,
  onRefresh,
  onDeleteRoom 
}) {
  const [searchTerm, setSearchTerm] = useState(""); 
  const [inviteCode, setInviteCode] = useState("");
  const [showJoinModal, setShowJoinModal] = useState(false);
  const [isJoining, setIsJoining] = useState(false);

  const myId = currentUser?.id || currentUser?._id;

  // 1. My Channels Filtering (Member + Search + Not DM)
  const myRooms = (rooms || []).filter(r => 
    r.isActive !== false && 
    !r.isDirectMessage && 
    r.members?.some(m => (m.user?._id || m.user || m) === myId) &&
    (r.name || "").toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 2. Discover Filtering (Public + Search + Not Member + Not DM)
  const otherRooms = (rooms || []).filter(r => 
    r.isActive !== false &&
    r.isPrivate === false && 
    !r.isDirectMessage && 
    !r.members?.some(m => (m.user?._id || m.user || m) === myId) &&
    (r.name || "").toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 3. Direct Messages Filtering
  const filteredUsers = (users || []).filter(u => 
    (u._id || u.id) !== myId &&
    (u.username || "").toLowerCase().includes(searchTerm.toLowerCase())
  );

  // ‚úÖ Updated Join Handler to support 6-digit codes specifically
  const handleJoinByCode = async (e) => {
    e.preventDefault();
    if (inviteCode.length !== 6 && inviteCode.length < 20) {
        return alert("Please enter a valid 6-digit code or Room ID");
    }
    setIsJoining(true);
    try {
      // Logic: If 6 digits, use the join-code endpoint. Otherwise, use standard join.
      const endpoint = inviteCode.length === 6 ? `/rooms/join-code` : `/rooms/join`;
      const payload = inviteCode.length === 6 ? { code: inviteCode } : { roomId: inviteCode };

      const res = await api.post(endpoint, payload);
      setInviteCode("");
      setShowJoinModal(false);

      if (onRefresh) onRefresh();
      // Select the newly joined room automatically
      if (onSelectRoom) onSelectRoom(res.data); 

    } catch (err) {
      alert(err.response?.data?.message || "Join failed. Check the ID/Code.");
    } finally {
      setIsJoining(false);
    }
  };

  const getRoomDisplayName = (room) => {
    if (!room.isDirectMessage) return room.name;
    const otherMember = room.members?.find(m => (m.user?._id || m.user || m) !== myId);
    return otherMember?.user?.username || "Private Chat";
  };

  return (
    <div className="bg-light border-end d-flex flex-column shadow-sm" style={{ width: "300px", height: "100vh" }}>

      {/* 1. HEADER & SEARCH */}
      <div className="p-3 border-bottom bg-white">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h5 className="mb-0 fw-bold text-primary">ChatBox</h5>
          <button onClick={onCreateRoom} className="btn btn-sm btn-primary rounded-pill px-3 shadow-sm">
            + Create
          </button>
        </div>

        <div className="d-grid gap-2 mb-3">
           <button 
             className="btn btn-sm btn-outline-success fw-bold" 
             onClick={() => setShowJoinModal(true)}
           >
             # Join with Code
           </button>
        </div>

        <div className="input-group input-group-sm mb-1">
          <span className="input-group-text bg-white border-end-0 text-muted">üîç</span>
          <input 
            type="text" 
            className="form-control border-start-0 ps-0 shadow-none" 
            placeholder="Search channels or people..." 
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      {/* SCROLLABLE LIST AREA */}
      <div className="flex-grow-1 overflow-auto custom-scrollbar p-2">

        {/* 2. MY CHANNELS */}
        <div className="p-2 fw-bold text-muted small text-uppercase" style={{ letterSpacing: "1px", fontSize: "0.7rem" }}>My Channels</div>
        <div className="list-group list-group-flush mb-3">
          {myRooms.length > 0 ? (
            myRooms.map((room) => {
              const isOwner = (room.owner?._id || room.owner) === myId;
              return (
                <div key={room._id} className="position-relative d-flex align-items-center mb-1 group-item-container">
                  <button
                    onClick={() => onSelectRoom(room)}
                    className={`list-group-item list-group-item-action border-0 rounded px-3 py-2 flex-grow-1 d-flex align-items-center justify-content-between ${
                      room._id === currentRoomId ? "active shadow-sm" : "bg-transparent"
                    }`}
                  >
                    <div className="text-truncate d-flex align-items-center">
                      <span className="me-2">{room.isPrivate ? "üîí" : "#"}</span>
                      <span className="fw-bold small">{getRoomDisplayName(room)}</span>
                    </div>
                  </button>

                  {isOwner && (
                    <button 
                      className={`btn btn-link text-danger p-0 px-2 position-absolute end-0 me-1 delete-btn ${room._id === currentRoomId ? 'text-white' : ''}`}
                      onClick={(e) => {
                        e.stopPropagation();
                        onDeleteRoom(room._id);
                      }}
                      title="Delete Room"
                    >
                      üóëÔ∏è
                    </button>
                  )}
                </div>
              );
            })
          ) : (
            <div className="small text-muted ps-2 fst-italic">No rooms joined</div>
          )}
        </div>

        {/* 3. DISCOVER */}
        {otherRooms.length > 0 && (
          <>
            <div className="p-2 fw-bold text-muted small text-uppercase" style={{ letterSpacing: "1px", fontSize: "0.7rem" }}>Discover</div>
            <div className="list-group list-group-flush mb-3">
              {otherRooms.map((room) => (
                <div key={room._id} className="list-group-item bg-transparent border-0 d-flex align-items-center justify-content-between p-2">
                  <span className="text-muted small text-truncate">üåê {room.name}</span>
                  <button 
                    className="btn btn-xs btn-success py-0 px-2 fw-bold" 
                    onClick={() => onJoinRoom(room._id)}
                  >
                    Join
                  </button>
                </div>
              ))}
            </div>
          </>
        )}

        {/* 4. DIRECT MESSAGES */}
        <div className="p-2 fw-bold text-muted small text-uppercase" style={{ letterSpacing: "1px", fontSize: "0.7rem" }}>Direct Messages</div>
        <div className="list-group list-group-flush">
          {filteredUsers.map((u) => (
            <button key={u._id || u.id} onClick={() => onSelectUser(u)} className="list-group-item list-group-item-action border-0 rounded mb-1 d-flex align-items-center bg-transparent py-2 px-3">
              <div className="position-relative me-2">
                <div className="bg-primary text-white rounded-circle d-flex align-items-center justify-content-center border" style={{ width: "32px", height: "32px", fontSize: "12px", fontWeight: "bold" }}>
                  {u.username?.charAt(0).toUpperCase()}
                </div>
                {u.isOnline && <div className="position-absolute bottom-0 end-0 bg-success border border-white rounded-circle" style={{ width: "10px", height: "10px" }}></div>}
              </div>
              <span className="small fw-medium">{u.username}</span>
            </button>
          ))}
        </div>
      </div>

      {/* 5. USER PROFILE FOOTER */}
      <div className="p-3 border-top bg-white mt-auto">
        <div className="d-flex align-items-center justify-content-between mb-3">
          <div className="d-flex align-items-center overflow-hidden">
            <div className="bg-primary text-white rounded-circle d-flex align-items-center justify-content-center fw-bold me-2 shadow-sm" style={{ width: "38px", height: "38px", flexShrink: 0 }}>
              {currentUser?.username?.charAt(0).toUpperCase()}
            </div>
            <div className="text-truncate">
              <div className="fw-bold small text-dark">{currentUser?.username || "Guest"}</div>
              <div className="text-success small d-flex align-items-center" style={{fontSize: '0.7rem'}}>
                <span className="bg-success rounded-circle me-1" style={{width: '6px', height: '6px'}}></span> Online
              </div>
            </div>
          </div>
        </div>
        <button onClick={onLogout} className="btn btn-sm btn-outline-danger w-100 py-1 fw-bold">
          Logout
        </button>
      </div>

      {/* JOIN MODAL */}
      <Modal show={showJoinModal} onHide={() => setShowJoinModal(false)} centered>
        <Modal.Header closeButton><Modal.Title>Join Room</Modal.Title></Modal.Header>
        <Form onSubmit={handleJoinByCode}>
          <Modal.Body>
            <Form.Label className="small text-muted">Enter the 6-digit invite code</Form.Label>
            <Form.Control 
              type="text" 
              placeholder="e.g. 123456" 
              value={inviteCode}
              onChange={(e) => setInviteCode(e.target.value.toUpperCase())}
              className="text-center fw-bold fs-4 shadow-none"
              maxLength={6}
              required
            />
          </Modal.Body>
          <Modal.Footer>
            <Button variant="success" type="submit" className="w-100" disabled={isJoining}>
              {isJoining ? "Joining..." : "Join Now"}
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>

      <style>{`
        .custom-scrollbar::-webkit-scrollbar { width: 4px; }
        .custom-scrollbar::-webkit-scrollbar-thumb { background: #cbd5e0; border-radius: 10px; }
        .btn-xs { padding: 1px 5px; font-size: 10px; }
        .list-group-item.active { background-color: #0d6efd !important; border-color: #0d6efd !important; }
        .group-item-container .delete-btn { opacity: 0; transition: opacity 0.2s; }
        .group-item-container:hover .delete-btn { opacity: 1; }
        .delete-btn:hover { transform: scale(1.2); }
      `}</style>
    </div>
  );
}

export default RoomList;
