import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import Login from "./pages/Login";
import Register from "./pages/Register";
import Chat from "./pages/Chat";

function App() {
  // 1. Initialize state directly from localStorage
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if token exists on initial mount
    const storedToken = localStorage.getItem("token");
    if (storedToken !== token) {
      setToken(storedToken);
    }
    setLoading(false);
  }, [token]);

  // 2. Centralized Authentication Handler
  const handleSetToken = (newToken) => {
    if (newToken) {
      localStorage.setItem("token", newToken);
    } else {
      localStorage.removeItem("token");
      localStorage.removeItem("user"); // Clear user data on logout too
    }
    setToken(newToken);
  };

  const isAuthenticated = !!token;

  // Loading Screen
  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: "100vh" }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <Router 
      future={{ 
        v7_startTransition: true, 
        v7_relativeSplatPath: true 
      }}
    >
      <div className="bg-light" style={{ minHeight: "100vh" }}>
        <Routes>
          {/* Public Routes: Redirect to chat if already logged in */}
          <Route 
            path="/login" 
            element={!isAuthenticated ? <Login setToken={handleSetToken} /> : <Navigate to="/chat" replace />} 
          />
          <Route 
            path="/register" 
            element={!isAuthenticated ? <Register setToken={handleSetToken} /> : <Navigate to="/chat" replace />} 
          />

          {/* Protected Route: Redirect to login if not authenticated */}
          <Route 
            path="/chat" 
            element={isAuthenticated ? <Chat setToken={handleSetToken} /> : <Navigate to="/login" replace />} 
          />

          {/* Global Fallback Redirect */}
          <Route 
            path="*" 
            element={<Navigate to={isAuthenticated ? "/chat" : "/login"} replace />} 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
