import { useEffect, useState, useCallback, useRef } from "react";
import api from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";
import { Dropdown, Modal, Button, FormControl, Form } from "react-bootstrap"; 

/* --- Embedded Call Component --- */
const VideoCallOverlay = ({ currentRoom, user, type, onEnd }) => {
  const jitsiContainerRef = useRef(null);
  const jitsiApiRef = useRef(null);

  useEffect(() => {
    const domain = "meet.jit.si";
    const options = {
      roomName: `MernChat_Room_${currentRoom._id}`,
      width: "100%", height: "100%",
      parentNode: jitsiContainerRef.current,
      userInfo: { displayName: user?.username || "User" },
      configOverwrite: { startWithVideoMuted: type === 'audio' },
    };
    jitsiApiRef.current = new window.JitsiMeetExternalAPI(domain, options);
    jitsiApiRef.current.addEventListener("videoConferenceLeft", onEnd);
    return () => { if (jitsiApiRef.current) jitsiApiRef.current.dispose(); };
  }, [currentRoom._id, user, type, onEnd]);

  return (
    <div className="position-absolute top-0 start-0 w-100 h-100 bg-dark d-flex flex-column" style={{ zIndex: 1050 }}>
      <div className="p-2 d-flex justify-content-between align-items-center bg-black text-white">
        <span className="fw-bold px-2">{type === 'video' ? 'üìπ Video' : 'üìû Audio'}: {currentRoom.name}</span>
        <Button variant="danger" size="sm" onClick={onEnd}>End Call</Button>
      </div>
      <div ref={jitsiContainerRef} className="flex-grow-1" />
    </div>
  );
};

export default function Chat({ setAuth }) { 
  const [rooms, setRooms] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showInfoModal, setShowInfoModal] = useState(false);
  const [isCreating, setIsCreating] = useState(false);
  const [callConfig, setCallConfig] = useState({ active: false, type: 'video' });

  // Search States
  const [searchTerm, setSearchTerm] = useState("");
  const [showSearchBar, setShowSearchBar] = useState(false);

  // Background Image - Using a neat abstract wallpaper
  const bgImage = "https://images.unsplash.com/photo-1497215728101-856f4ea42174?auto=format&fit=crop&q=80&w=2070";

  const fetchData = useCallback(async () => {
    try {
      const [roomsRes] = await Promise.all([api.get("/rooms")]);
      setRooms(roomsRes.data || []);
      if (currentRoom) {
        const updated = (roomsRes.data || []).find(r => r._id === currentRoom._id);
        if (updated) setCurrentRoom(updated);
      }
    } catch (err) { if (err.response?.status === 401) handleLogout(); }
    finally { setLoading(false); }
  }, [currentRoom]);

  const handleLogout = useCallback(() => {
    localStorage.clear();
    socket.disconnect();
    window.location.href = "/login";
  }, []);

  const onSendMessage = async (content, file = null) => {
    if (!currentRoom) return;
    try {
      let res;
      if (file) {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("roomId", currentRoom._id);
        res = await api.post("/messages/upload", formData);
      } else {
        res = await api.post("/messages", { roomId: currentRoom._id, content });
      }
      socket.emit("send_message", res.data);
    } catch (err) { console.error(err); }
  };

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) { setUser(JSON.parse(storedUser)); socket.connect(); }
    fetchData();
    return () => { socket.off("receive_message"); socket.disconnect(); };
  }, []); 

  useEffect(() => {
    if (!currentRoom || !user) return;
    const roomId = currentRoom._id;
    socket.emit("join_room", { roomId, username: user.username });
    api.get(`/messages/${roomId}`).then(res => setMessages(res.data));
    const handleReceive = (msg) => { if (msg.roomId === roomId) setMessages(prev => [...prev, msg]); };
    socket.on("receive_message", handleReceive);
    return () => socket.off("receive_message", handleReceive);
  }, [currentRoom?._id, user?.username]);

  const getDMName = () => {
    const other = currentRoom.members?.find(m => (m.user?._id || m) !== (user?._id || user?.id));
    return other?.user?.username || other?.username || "Direct Chat";
  };

  // Filter messages based on search
  const filteredMessages = messages.filter(m => m.content?.toLowerCase().includes(searchTerm.toLowerCase()));

  if (loading) return <div className="vh-100 d-flex justify-content-center align-items-center bg-dark text-white">Loading...</div>;

  return (
    <div className="container-fluid p-0 d-flex" style={{ 
      height: "100vh", 
      backgroundImage: `url(${bgImage})`, 
      backgroundSize: 'cover', 
      backgroundPosition: 'center' 
    }}>
      {/* Sidebar with slight glass effect */}
      <div style={{ width: "320px", background: "rgba(33, 37, 41, 0.85)", backdropFilter: "blur(10px)", borderRight: "1px solid rgba(255,255,255,0.1)" }}>
        <RoomList
          rooms={rooms}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={(room) => { setCurrentRoom(room); setShowSearchBar(false); setSearchTerm(""); }}
          onLogout={handleLogout}
        />
      </div>

      {/* Main Content */}
      <div className="flex-grow-1 d-flex flex-column position-relative">
        {callConfig.active && <VideoCallOverlay currentRoom={currentRoom} user={user} type={callConfig.type} onEnd={() => setCallConfig({ active: false })} />}

        {currentRoom ? (
          <>
            {/* GLASS HEADER */}
            <div className="d-flex justify-content-between align-items-center p-3 text-white border-bottom shadow-sm" style={{ 
              background: "rgba(255, 255, 255, 0.1)", 
              backdropFilter: "blur(15px)",
              borderColor: "rgba(255,255,255,0.1)" 
            }}>
              <div className="d-flex align-items-center gap-3" onClick={() => setShowInfoModal(true)} style={{ cursor: 'pointer' }}>
                <div className="bg-primary rounded-circle d-flex align-items-center justify-content-center" style={{ width: "45px", height: "45px", fontSize: "1.2rem" }}>
                  {currentRoom.isDirectMessage ? "üë§" : "üë•"}
                </div>
                <div>
                  <h5 className="mb-0 fw-bold">{currentRoom.isDirectMessage ? getDMName() : currentRoom.name}</h5>
                  <small className="opacity-75">{currentRoom.isPrivate ? "üîí Private Room" : "üåê Public Group"}</small>
                </div>
              </div>

              <div className="d-flex align-items-center gap-3">
                {/* Search Toggle */}
                <span style={{ cursor: 'pointer' }} className="fs-5" onClick={() => setShowSearchBar(!showSearchBar)}>üîç</span>
                <span style={{ cursor: 'pointer' }} className="fs-5" onClick={() => setCallConfig({ active: true, type: 'video' })}>üìπ</span>
                <Dropdown align="end">
                  <Dropdown.Toggle variant="link" className="text-white p-0 no-caret shadow-none fs-4">‚ãÆ</Dropdown.Toggle>
                  <Dropdown.Menu variant="dark">
                    <Dropdown.Item onClick={() => setShowInfoModal(true)}>Info</Dropdown.Item>
                    <Dropdown.Item onClick={() => setMessages([])}>Clear Chat</Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </div>
            </div>

            {/* NEAT SEARCH OVERLAY */}
            {showSearchBar && (
              <div className="p-2 animate-slide-in" style={{ background: "rgba(0,0,0,0.4)", backdropFilter: "blur(5px)" }}>
                <FormControl
                  placeholder="Search messages..."
                  className="bg-transparent text-white border-0 shadow-none border-bottom rounded-0 placeholder-white"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  autoFocus
                />
              </div>
            )}

            {/* Chat Messages Area */}
            <div className="flex-grow-1 overflow-hidden">
              <ChatBox 
                messages={searchTerm ? filteredMessages : messages} 
                currentUserId={user?._id} 
                onSendText={onSendMessage} 
              />
            </div>
          </>
        ) : (
          <div className="h-100 d-flex flex-column justify-content-center align-items-center text-white text-center" style={{ background: "rgba(0,0,0,0.3)" }}>
            <div className="p-5 rounded-4 shadow" style={{ background: "rgba(255,255,255,0.1)", backdropFilter: "blur(20px)" }}>
              <h1 className="display-3">üí¨</h1>
              <h2 className="fw-bold">Hi, {user?.username}</h2>
              <p className="opacity-75">Select a room from the sidebar to begin chatting.</p>
            </div>
          </div>
        )}
      </div>

      {/* MODAL FOR INFO */}
      <Modal show={showInfoModal} onHide={() => setShowInfoModal(false)} centered contentClassName="bg-dark text-white border-secondary">
        <Modal.Header closeButton closeVariant="white" className="border-secondary"><Modal.Title>Group Info</Modal.Title></Modal.Header>
        <Modal.Body className="text-center">
          <div className="bg-primary rounded-circle d-inline-flex align-items-center justify-content-center mb-3" style={{width: '80px', height: '80px', fontSize: '2rem'}}>üë•</div>
          <h4>{currentRoom?.name}</h4>
          <p className="text-secondary small">Room ID: {currentRoom?._id}</p>
          <Button variant="outline-light" className="mt-3" onClick={() => setShowInfoModal(false)}>Close</Button>
        </Modal.Body>
      </Modal>
    </div>
  );
}
