import { useEffect, useState } from "react";
import { api } from "../services/api";
import { useNavigate, Link } from "react-router-dom";

function Register() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false); // Added loading state
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      navigate("/chat");
    }
  }, [navigate]);

  const handleRegister = async (e) => {
    // Prevent default if you ever wrap this in a <form>
    if (e) e.preventDefault();

    setError("");
    setLoading(true);

    if (!username || !email || !password) {
      setError("All fields are required");
      setLoading(false);
      return;
    }

    try {
      const response = await api.post("/auth/register", {
        username,
        email,
        password,
      });

      // If successful, navigate to login
      console.log("Success:", response.data);
      navigate("/login");
    } catch (err) {
      // ✅ FIX: Use the actual message sent by your Express server
      const serverMessage = err.response?.data?.message || "Something went wrong. Please try again.";
      setError(serverMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: "350px", margin: "80px auto", padding: "30px", border: "1px solid #ddd", borderRadius: "8px", boxShadow: "0 4px 6px rgba(0,0,0,0.1)", backgroundColor: "#fff" }}>
      <h3 style={{ textAlign: "center", marginBottom: "20px" }}>Register</h3>

      {error && (
        <div style={{ color: "#721c24", backgroundColor: "#f8d7da", padding: "10px", borderRadius: "4px", marginBottom: "15px", fontSize: "14px", border: "1px solid #f5c6cb" }}>
          {error}
        </div>
      )}

      <div style={{ marginBottom: "15px" }}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ccc" }}
        />
      </div>

      <div style={{ marginBottom: "15px" }}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ccc" }}
        />
      </div>

      <div style={{ marginBottom: "20px" }}>
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "1px solid #ccc" }}
        />
      </div>

      <button
        onClick={handleRegister}
        disabled={loading}
        style={{ width: "100%", padding: "10px", borderRadius: "4px", border: "none", backgroundColor: loading ? "#ccc" : "#007bff", color: "white", cursor: loading ? "not-allowed" : "pointer", fontSize: "16px" }}
      >
        {loading ? "Registering..." : "Register"}
      </button>

      <p style={{ marginTop: "20px", fontSize: "14px", textAlign: "center", color: "#666" }}>
        Already have an account? <Link to="/login" style={{ color: "#007bff", textDecoration: "none" }}>Login</Link>
      </p>
    </div>
  );
}

export default Register;
