import { useEffect, useState, useCallback } from "react";
import api from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";
import { Dropdown } from "react-bootstrap"; 

export default function Chat({ setAuth }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const [isCreating, setIsCreating] = useState(false);
  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
    showHistoryToNewMembers: true,
  });

  const navigate = useNavigate();

  /* ================= FETCH DATA HELPER ================= */
  const fetchData = useCallback(async () => {
    try {
      const [roomsRes, usersRes] = await Promise.all([
        api.get("/rooms"),
        api.get("/auth/users"),
      ]);
      setRooms(roomsRes.data);
      setUsers(usersRes.data);
    } catch (err) {
      console.error("Chat fetch error:", err);
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, []);

  /* ================= LOGOUT ================= */
  const handleLogout = useCallback(() => {
    if (setAuth) {
      setAuth(null); 
    } else {
      localStorage.clear();
      window.location.href = "/login";
    }
    socket.disconnect();
  }, [setAuth]);

  /* ================= INITIAL LOAD ================= */
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
        socket.connect();
      } catch (e) {
        handleLogout();
      }
    }
    fetchData();
    return () => {
      socket.off("receive_message");
      socket.disconnect();
    };
  }, [handleLogout, fetchData]);

  /* ================= ROOM SYNC ================= */
  useEffect(() => {
    if (!currentRoom || !user) return;
    const roomId = currentRoom._id;
    socket.emit("join_room", { roomId, username: user.username });

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error("Message fetch error:", err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId || msg.room === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };
    socket.on("receive_message", handleReceive);
    return () => socket.off("receive_message", handleReceive);
  }, [currentRoom, user]);

  /* ================= CHAT ACTIONS ================= */
  const onSendMessage = async (content, file = null) => {
    if (!currentRoom) return;
    try {
      let res;
      if (file) {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("roomId", currentRoom._id);
        res = await api.post("/messages/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" }
        });
      } else {
        res = await api.post("/messages", { roomId: currentRoom._id, content });
      }
      socket.emit("send_message", res.data);
    } catch (err) { console.error("Send error:", err); }
  };

  const handleClearChat = async () => {
    if (window.confirm("Are you sure you want to clear all messages?")) {
      try {
        await api.delete(`/messages/clear/${currentRoom._id}`);
        setMessages([]);
      } catch (err) { alert("Failed to clear chat"); }
    }
  };

  const startCall = (type) => {
    alert(`Starting ${type} call...`);
  };

  const downloadChatHistory = () => {
    if (!messages.length) return alert("No history to download");
    const fileContent = messages.map(msg => {
      const time = new Date(msg.createdAt).toLocaleTimeString();
      const sender = msg.sender?.username || "System";
      return `[${time}] ${sender}: ${msg.text || msg.content}`;
    }).join("\n");
    const blob = new Blob([fileContent], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `${currentRoom.name}_History.txt`;
    link.click();
  };

  const handleDeleteRoom = async () => {
    if (window.confirm("Are you sure you want to delete this room for everyone?")) {
      try {
        await api.delete(`/rooms/${currentRoom._id}`);
        setRooms(rooms.filter(r => r._id !== currentRoom._id));
        setCurrentRoom(null);
      } catch (err) { alert("Failed to delete room"); }
    }
  };

  if (loading) return <div className="vh-100 d-flex justify-content-center align-items-center">Loading‚Ä¶</div>;

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 overflow-hidden">
        <RoomList
          rooms={rooms}
          users={users}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={(room) => { setCurrentRoom(room); setIsCreating(false); }}
          onJoinRoom={async (code) => {
             const res = await api.post(`/rooms/join`, { inviteCode: code });
             fetchData();
             if (res.data) setCurrentRoom(res.data);
          }}
          onCreateRoom={() => setIsCreating(true)}
          onLogout={handleLogout}
        />

        <div className="flex-grow-1 bg-white d-flex flex-column">
          {isCreating ? (
            <div className="p-5 d-flex justify-content-center">
               <div className="card shadow-sm p-4 w-100" style={{maxWidth: '500px'}}>
                  <h3>Create New Room</h3>
                  {/* Your form here */}
                  <button className="btn btn-secondary mt-3" onClick={() => setIsCreating(false)}>Cancel</button>
               </div>
            </div>
          ) : currentRoom ? (
            <div className="d-flex flex-column h-100">
              {/* CLEAN HEADER - NO IDs SHOWN */}
              <div className="d-flex justify-content-between align-items-center p-3 border-bottom bg-light">
                <h5 className="mb-0 fw-bold text-dark">{currentRoom.name}</h5>

                <Dropdown align="end">
                  <Dropdown.Toggle variant="link" className="text-dark p-0 border-0 no-caret shadow-none">
                    <span style={{ fontSize: "1.6rem", cursor: "pointer", padding: '0 10px' }}>‚ãÆ</span>
                  </Dropdown.Toggle>

                  <Dropdown.Menu>
                    <Dropdown.Item onClick={() => startCall("audio")}>üìû Audio Call</Dropdown.Item>
                    <Dropdown.Item onClick={() => startCall("video")}>üìπ Video Call</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item onClick={() => alert("Select mode enabled")}>üìë Select Messages</Dropdown.Item>
                    <Dropdown.Item onClick={downloadChatHistory}>üì• Download History</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item onClick={handleClearChat} className="text-danger">üßπ Clear Chat</Dropdown.Item>
                    {currentRoom.owner === (user?._id || user?.id) && (
                      <Dropdown.Item onClick={handleDeleteRoom} className="text-danger fw-bold">‚ùå Delete Room</Dropdown.Item>
                    )}
                  </Dropdown.Menu>
                </Dropdown>
              </div>

              <div className="flex-grow-1 overflow-hidden">
                <ChatBox
                  messages={messages}
                  currentUserId={user?._id || user?.id}
                  onSendText={(txt) => onSendMessage(txt)}
                  onSendFile={(file) => onSendMessage(null, file)}
                />
              </div>
            </div>
          ) : (
            <div className="h-100 d-flex flex-column justify-content-center align-items-center text-muted">
              <h4>Welcome</h4>
              <p>Select a channel to start chatting.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
