import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { getMyRooms, createRoom, joinRoomByCode } from "../services/api";
import { Button, Modal, Form, ListGroup, Badge } from "react-bootstrap";

function Chat({ auth, setAuth }) {
  const [rooms, setRooms] = useState([]);
  const [showCreate, setShowCreate] = useState(false);
  const [showJoin, setShowJoin] = useState(false);

  const [newRoomName, setNewRoomName] = useState("");
  const [joinCode, setJoinCode] = useState("");

  // Load rooms on mount
  useEffect(() => {
    fetchRooms();
  }, []);

  const fetchRooms = async () => {
    try {
      const res = await getMyRooms();
      setRooms(res.data);
    } catch (err) {
      console.error("Failed to load rooms");
    }
  };

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    try {
      // Logic: Backend generates random inviteCode (e.g. 5X8A2P)
      const res = await createRoom({ name: newRoomName, isPrivate: true });
      alert(`Room Created! Share this ID: ${res.data.inviteCode}`);
      setNewRoomName("");
      setShowCreate(false);
      fetchRooms(); // Refresh list to show new room
    } catch (err) {
      alert("Error creating room");
    }
  };

  const handleJoinRoom = async (e) => {
    e.preventDefault();
    try {
      // Convert to uppercase to match backend storage
      await joinRoomByCode(joinCode.toUpperCase().trim());
      alert("Join request sent! The owner must approve you before the room appears in your list.");
      setJoinCode("");
      setShowJoin(false);
      // Optional: Set an interval to fetchRooms() every few seconds 
      // to see when the owner approves you.
    } catch (err) {
      alert(err.response?.data?.message || "Invalid Room ID");
    }
  };

  return (
    <div className="chat-container vh-100 d-flex flex-column bg-white">
      {/* --- 1. TOP NAVBAR --- */}
      <nav className="navbar navbar-dark bg-primary px-4 shadow-sm flex-shrink-0">
        <span className="navbar-brand fw-bold">MERN Chat</span>

        <div className="d-flex gap-3 align-items-center">
          <span className="text-light small d-none d-sm-block">
            Welcome, <strong>{auth.user?.username}</strong>
          </span>
          <Button variant="outline-light" size="sm" onClick={() => setShowJoin(true)}>
            Join Room
          </Button>
          <Button variant="light" size="sm" className="text-primary fw-bold" onClick={() => setShowCreate(true)}>
            + Create Room
          </Button>
          <Button variant="danger" size="sm" onClick={() => setAuth(null)}>
            Logout
          </Button>
        </div>
      </nav>

      {/* --- 2. MAIN LAYOUT --- */}
      <div className="container-fluid flex-grow-1 overflow-hidden">
        <div className="row h-100">

          {/* LEFT SIDEBAR: Room List */}
          <div className="col-md-3 border-end bg-light p-3 overflow-auto">
            <div className="d-flex justify-content-between align-items-center mb-3">
              <h5 className="mb-0">Your Rooms</h5>
              <Button variant="link" size="sm" onClick={fetchRooms} title="Refresh Rooms">
                ðŸ”„
              </Button>
            </div>

            <ListGroup variant="flush" className="shadow-sm rounded">
              {rooms.length > 0 ? (
                rooms.map(room => (
                  <ListGroup.Item 
                    key={room._id} 
                    action 
                    className="d-flex justify-content-between align-items-start border-bottom"
                  >
                    <div className="ms-2 me-auto">
                      <div className="fw-bold">{room.name}</div>
                      <small className="text-muted">ID: {room.inviteCode}</small>
                    </div>
                    {room.owner === auth.user?.id && (
                      <Badge bg="info" pill>Owner</Badge>
                    )}
                  </ListGroup.Item>
                ))
              ) : (
                <div className="text-center text-muted mt-4 small">
                  No rooms yet. Create or join one!
                </div>
              )}
            </ListGroup>
          </div>

          {/* RIGHT SIDE: Chat Area Placeholder */}
          <div className="col-md-9 d-flex flex-column justify-content-center align-items-center bg-white text-muted">
             <h3>Select a room to start chatting</h3>
             <p>Share your Room ID with others to invite them.</p>
          </div>

        </div>
      </div>

      {/* --- 3. MODALS (CREATE & JOIN) --- */}

      {/* CREATE MODAL */}
      <Modal show={showCreate} onHide={() => setShowCreate(false)} centered>
        <Modal.Header closeButton className="bg-primary text-white">
          <Modal.Title>Create New Room</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleCreateRoom}>
          <Modal.Body>
            <Form.Group className="mb-3">
              <Form.Label className="fw-bold">Room Name</Form.Label>
              <Form.Control 
                type="text" 
                placeholder="e.g. Study Group" 
                value={newRoomName} 
                onChange={(e) => setNewRoomName(e.target.value)} 
                required 
              />
            </Form.Group>
            <p className="text-muted small">A unique 6-character ID will be generated automatically.</p>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowCreate(false)}>Cancel</Button>
            <Button variant="primary" type="submit">Create & Generate ID</Button>
          </Modal.Footer>
        </Form>
      </Modal>

      {/* JOIN MODAL */}
      <Modal show={showJoin} onHide={() => setShowJoin(false)} centered>
        <Modal.Header closeButton className="bg-success text-white">
          <Modal.Title>Join with Room ID</Modal.Title>
        </Modal.Header>
        <Form onSubmit={handleJoinRoom}>
          <Modal.Body>
            <Form.Group className="mb-3">
              <Form.Label className="fw-bold">Enter Room ID (Invite Code)</Form.Label>
              <Form.Control 
                type="text" 
                placeholder="Example: 5X8A2P" 
                value={joinCode} 
                onChange={(e) => setJoinCode(e.target.value)} 
                required 
                className="text-uppercase"
              />
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowJoin(false)}>Cancel</Button>
            <Button variant="success" type="submit">Send Join Request</Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </div>
  );
}

export default Chat;
