import axios from "axios";

/**
 * Axios instance
 * - Uses JWT from localStorage (Authorization header)
 * - Supports cookies if backend ever uses them
 */
const api = axios.create({
  baseURL: "http://localhost:5000/api",
  withCredentials: true, // safe even if backend uses JWT headers
});

/**
 * REQUEST INTERCEPTOR
 * Attach JWT token to every request
 */
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("token");

    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }

    return config;
  },
  (error) => Promise.reject(error)
);

/**
 * RESPONSE INTERCEPTOR
 * Handle auth failures globally
 */
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      console.error("401 Unauthorized → Logging out");

      localStorage.removeItem("token");
      localStorage.removeItem("user");

      // Hard redirect (don’t rely on React state)
      window.location.replace("/login");
    }

    return Promise.reject(error);
  }
);

/* ============================
   ROOM APIS
============================ */
export const createRoom = (data) => api.post("/rooms", data);
export const getRooms = () => api.get("/rooms");

/* ============================
   MESSAGE APIS (if used)
============================ */
export const getMessages = (roomId) =>
  api.get(`/messages/${roomId}`);

export const sendMessage = (data) =>
  api.post("/messages", data);

/* ============================
   AUTH APIS (optional)
============================ */
export const login = (data) => api.post("/auth/login", data);
export const register = (data) => api.post("/auth/register", data);

export default api;
