import React from "react";

function RoomList({ rooms, currentRoomId, onSelectRoom }) {
  return (
    <div
      style={{
        width: "220px",
        borderRight: "1px solid #ccc",
        padding: "10px",
        overflowY: "auto",
      }}
    >
      <h4>Rooms</h4>

      {rooms.map((room) => (
        <div
          key={room._id}
          onClick={() => onSelectRoom(room)}
          style={{
            padding: "8px",
            marginBottom: "6px",
            cursor: "pointer",
            borderRadius: "4px",
            backgroundColor:
              room._id === currentRoomId ? "#e0e0e0" : "transparent",
          }}
        >
          {room.isPrivate ? "ðŸ”’ " : "ðŸ’¬ "}
          {room.name}
        </div>
      ))}
    </div>
  );
}

export default RoomList;
