import { useEffect, useState, useCallback } from "react";
import api from "../services/api"; // ðŸ”´ FIXED IMPORT
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";

export default function Chat({ setToken }) {
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);

  const [isCreating, setIsCreating] = useState(false);
  const [isJoining, setIsJoining] = useState(false);

  const [inviteCodeInput, setInviteCodeInput] = useState("");
  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
    showHistory: false,
  });

  const navigate = useNavigate();

  /* ================= LOGOUT ================= */
  const handleLogout = useCallback(() => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    if (setToken) setToken(null);
    socket.disconnect();
    navigate("/login");
  }, [navigate, setToken]);

  /* ================= INITIAL LOAD ================= */
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");

    if (!storedUser || !token) {
      handleLogout();
      return;
    }

    const parsedUser = JSON.parse(storedUser);
    setUser(parsedUser);

    socket.connect();

    const fetchData = async () => {
      try {
        const [roomsRes, usersRes] = await Promise.all([
          api.get("/rooms"),
          api.get("/auth/users"),
        ]);

        setRooms(roomsRes.data);
        setUsers(usersRes.data);
      } catch (err) {
        if (err.response?.status === 401) handleLogout();
      }
    };

    fetchData();

    socket.on("room_created", (room) => {
      setRooms((prev) =>
        prev.find((r) => r._id === room._id) ? prev : [...prev, room]
      );
    });

    socket.on("room_deleted", (id) => {
      setRooms((prev) => prev.filter((r) => r._id !== id));
      if (currentRoom?._id === id) setCurrentRoom(null);
    });

    return () => {
      socket.off("room_created");
      socket.off("room_deleted");
      socket.disconnect();
    };
  }, [handleLogout, currentRoom]);

  /* ================= ROOM SYNC ================= */
  useEffect(() => {
    if (!currentRoom || !user) return;

    const roomId = currentRoom._id;

    socket.emit("join_room", { roomId });

    api
      .get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch(console.error);

    const handleReceive = (msg) => {
      if (msg.roomId === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("receive_message", handleReceive);

    return () => {
      socket.off("receive_message", handleReceive);
      socket.emit("leave_room", { roomId });
    };
  }, [currentRoom, user]);

  /* ================= HANDLERS ================= */
  const handleCreateSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await api.post("/rooms", {
        name: newRoomData.name.trim(),
        isPrivate: newRoomData.isPrivate,
      });

      setRooms((prev) => [...prev, res.data]);
      setCurrentRoom(res.data);
      setIsCreating(false);
      setNewRoomData({ name: "", isPrivate: false, showHistory: false });
    } catch (err) {
      alert(err.response?.data?.message || "Room creation failed");
    }
  };

  const handleJoinByCode = async (e) => {
    e.preventDefault();
    try {
      await api.post(`/rooms/join/${inviteCodeInput}`);
      alert("Join request sent");
      setInviteCodeInput("");
      setIsJoining(false);
    } catch {
      alert("Invalid invite code");
    }
  };

  const handleJoinRoom = async (room) => {
    try {
      const res = await api.post(`/rooms/${room._id}/join`);
      setRooms((prev) =>
        prev.map((r) => (r._id === res.data._id ? res.data : r))
      );
      setCurrentRoom(res.data);
    } catch {
      alert("Failed to join room");
    }
  };

  const handleSelectUser = async (targetUser) => {
    try {
      const res = await api.post("/rooms/private", {
        targetUserId: targetUser._id,
      });

      setRooms((prev) =>
        prev.find((r) => r._id === res.data._id)
          ? prev
          : [...prev, res.data]
      );

      setCurrentRoom(res.data);
      setIsCreating(false);
      setIsJoining(false);
    } catch (err) {
      console.error(err);
    }
  };

  /* ================= HARD GUARD (YOU ASKED FOR THIS) ================= */
  if (!user) {
    console.log("User not logged in. Chat overlay not injected.");
    return null;
  }

  /* ================= UI ================= */
  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 shadow-sm overflow-hidden">
        {/* Sidebar */}
        <div className="col-md-3 border-end bg-white d-flex flex-column">
          <div className="p-3 border-bottom">
            <h4 className="text-primary fw-bold mb-0">ChatBox</h4>
          </div>

          <div className="p-3 border-bottom d-grid gap-2">
            <button
              className="btn btn-outline-primary"
              onClick={() => {
                setIsCreating(true);
                setIsJoining(false);
                setCurrentRoom(null);
              }}
            >
              + Create Room
            </button>

            <button
              className="btn btn-outline-primary"
              onClick={() => {
                setIsJoining(true);
                setIsCreating(false);
                setCurrentRoom(null);
              }}
            >
              # Join with ID
            </button>
          </div>

          <div className="flex-grow-1 overflow-auto">
            <RoomList
              rooms={rooms}
              users={users}
              currentUser={user}
              currentRoomId={currentRoom?._id}
              onSelectRoom={setCurrentRoom}
              onJoinRoom={handleJoinRoom}
              onSelectUser={handleSelectUser}
              onLogout={handleLogout}
            />
          </div>
        </div>

        {/* Main */}
        <div className="flex-grow-1 bg-white">
          {currentRoom ? (
            <ChatBox
              messages={messages}
              currentUserId={user._id || user.id}
            />
          ) : (
            <div className="h-100 d-flex justify-content-center align-items-center text-muted">
              Select or create a room
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
