import React, { useState, useRef, useEffect } from "react";

function ChatBox({ messages, onSendText, onSendFile }) {
  const [text, setText] = useState("");
  const [file, setFile] = useState(null);
  const scrollRef = useRef();

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    scrollRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSendText = (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    onSendText(text);
    setText("");
  };

  const handleSendFile = () => {
    if (!file) return;
    onSendText(""); // Clear text if any
    onSendFile(file);
    setFile(null);
  };

  return (
    <div className="card d-flex flex-column" style={{ height: "80vh" }}>
      {/* Messages Header */}
      <div className="card-header bg-primary text-white">
        <h5 className="mb-0">Chat Room</h5>
      </div>

      {/* Messages Body */}
      <div className="card-body overflow-auto bg-light" style={{ flex: "1" }}>
        {messages.map((msg) => (
          <div key={msg._id} className="mb-3 d-flex flex-column">
            <div className="bg-white p-2 rounded shadow-sm border" style={{ maxWidth: "75%", width: "fit-content" }}>
              <small className="text-primary fw-bold d-block mb-1">
                {msg.sender?.username || "User"}
              </small>
              
              {msg.type === "text" && <p className="mb-0">{msg.content}</p>}

              {msg.type === "image" && (
                <div className="mt-1">
                  <img
                    src={`http://localhost:5000/${msg.content}`}
                    alt="uploaded"
                    className="img-fluid rounded"
                    style={{ maxHeight: "250px" }}
                  />
                </div>
              )}

              {msg.type === "file" && (
                <div className="mt-1 bg-light p-2 rounded border">
                   ðŸ“Ž <a href={`http://localhost:5000/${msg.content}`} target="_blank" rel="noreferrer" className="text-decoration-none">
                    Download Attachment
                  </a>
                </div>
              )}
            </div>
          </div>
        ))}
        <div ref={scrollRef} />
      </div>

      {/* Input Footer */}
      <div className="card-footer bg-white border-top">
        <form onSubmit={handleSendText} className="mb-2">
          <div className="input-group">
            <input
              type="text"
              className="form-control"
              placeholder="Type a message..."
              value={text}
              onChange={(e) => setText(e.target.value)}
            />
            <button className="btn btn-primary" type="submit">Send</button>
          </div>
        </form>

        <div className="input-group input-group-sm">
          <input
            type="file"
            className="form-control"
            onChange={(e) => setFile(e.target.files[0])}
          />
          <button className="btn btn-outline-secondary" onClick={handleSendFile} disabled={!file}>
            Upload File
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChatBox;
