import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import Login from "./pages/Login";
import Register from "./pages/Register";
import Chat from "./pages/Chat";

function App() {
  const [auth, setAuth] = useState(() => {
    const token = localStorage.getItem("token");
    const user = localStorage.getItem("user");
    // Only set auth if both exist and user is valid JSON
    try {
      return token && user ? { token, user: JSON.parse(user) } : null;
    } catch (e) {
      return null;
    }
  });

  // Sync state if localStorage changes (e.g., manual delete in DevTools)
  useEffect(() => {
    const syncAuth = () => {
      const token = localStorage.getItem("token");
      const user = localStorage.getItem("user");
      if (!token) setAuth(null);
    };
    window.addEventListener("storage", syncAuth);
    return () => window.removeEventListener("storage", syncAuth);
  }, []);

  const handleSetAuth = (token, user) => {
    if (token && user) {
      localStorage.setItem("token", token);
      localStorage.setItem("user", JSON.stringify(user));
      setAuth({ token, user });
    } else {
      localStorage.removeItem("token");
      localStorage.removeItem("user");
      setAuth(null);
    }
  };

  const isAuthenticated = Boolean(auth);

  return (
    <Router>
      <div className="bg-light" style={{ minHeight: "100vh" }}>
        <Routes>
          {/* PUBLIC ROUTES */}
          <Route
            path="/login"
            element={
              !isAuthenticated ? (
                <Login setAuth={handleSetAuth} />
              ) : (
                <Navigate to="/chat" replace />
              )
            }
          />

          <Route
            path="/register"
            element={
              !isAuthenticated ? (
                <Register />
              ) : (
                <Navigate to="/chat" replace />
              )
            }
          />

          {/* PROTECTED ROUTES */}
          <Route
            path="/chat"
            element={
              isAuthenticated ? (
                /* CRITICAL: Passing setAuth here fixes the Logout issue */
                <Chat setAuth={handleSetAuth} auth={auth} />
              ) : (
                <Navigate to="/login" replace />
              )
            }
          />

          {/* FALLBACK */}
          <Route
            path="*"
            element={<Navigate to={isAuthenticated ? "/chat" : "/login"} replace />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
