import React from "react";

function RoomList({ rooms, currentRoom, onSelectRoom }) {
  return (
    <div style={{ borderRight: "1px solid #ccc", padding: "10px", width: "200px" }}>
      <h4>Rooms</h4>
      {rooms.map((room) => (
        <div
          key={room}
          onClick={() => onSelectRoom(room)}
          style={{
            padding: "6px",
            marginBottom: "4px",
            cursor: "pointer",
            backgroundColor: room === currentRoom ? "#e0e0e0" : "transparent",
          }}
        >
          {room}
        </div>
      ))}
    </div>
  );
}

export default RoomList;
