import { useEffect, useState } from "react";
import { api } from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";

export default function Chat() {
  const [rooms, setRooms] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // 1. Check Authentication on Load
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");

    if (!storedUser || !token) {
      navigate("/login"); // Redirect if not logged in
      return;
    }

    setUser(JSON.parse(storedUser));

    // Connect socket and pass token for server-side auth
    socket.connect(); 

    return () => socket.disconnect();
  }, [navigate]);

  // 2. Load Rooms
  useEffect(() => {
    api.get("/rooms")
      .then((res) => {
        setRooms(res.data);
        if (res.data.length > 0) {
          setCurrentRoom(res.data[0]);
        }
      })
      .catch((err) => console.error("Error loading rooms:", err));
  }, []);

  // 3. Handle Room Switching & Real-time Messages
  useEffect(() => {
    if (!currentRoom) return;

    // Join room in socket
    socket.emit("join_room", currentRoom._id);

    // Load message history from MongoDB
    api.get(`/messages/${currentRoom._id}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error("Error loading messages:", err));

    const handleReceive = (msg) => {
      // Only add message if it belongs to the current room
      if (msg.roomId === currentRoom._id) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("receive_message", handleReceive);

    return () => {
      socket.off("receive_message", handleReceive);
      // Optional: socket.emit("leave_room", currentRoom._id);
    };
  }, [currentRoom]);

  // 4. Send Message with Sender Info
  const sendTextMessage = (text) => {
    if (!currentRoom || !text.trim() || !user) return;

    const messageData = {
      roomId: currentRoom._id,
      content: text,
      senderId: user.id,      // ðŸ‘ˆ Added from logged-in user state
      senderName: user.username, // ðŸ‘ˆ Added for immediate UI display
      type: "text",
    };

    socket.emit("send_message", messageData);
  };

  return (
    <div style={{ display: "flex", height: "100vh", backgroundColor: "#f4f7f6" }}>
      <RoomList
        rooms={rooms}
        currentRoomId={currentRoom?._id}
        onSelectRoom={setCurrentRoom}
      />

      <div style={{ flex: 1, display: "flex", flexDirection: "column", padding: "20px" }}>
        {currentRoom ? (
          <>
            <div style={{ paddingBottom: "10px", borderBottom: "1px solid #ddd", marginBottom: "10px" }}>
              <h3 style={{ margin: 0 }}>#{currentRoom.name}</h3>
              <small style={{ color: "#888" }}>Logged in as: {user?.username}</small>
            </div>

            <ChatBox 
              messages={messages} 
              onSend={sendTextMessage} 
              currentUserId={user?.id} // ðŸ‘ˆ Pass this to style your own messages differently
            />
          </>
        ) : (
          <div style={{ display: "flex", flex: 1, justifyContent: "center", alignItems: "center" }}>
            <p style={{ color: "#999", fontSize: "1.2rem" }}>Select a room to start chatting</p>
          </div>
        )}
      </div>
    </div>
  );
}
