import { useEffect, useState } from "react";
import { api } from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket"; // Ensure this uses your socket.js with auth
import { useNavigate } from "react-router-dom";

export default function Chat() {
  const [rooms, setRooms] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [isUploading, setIsUploading] = useState(false); // ðŸ‘ˆ Track upload state
  const navigate = useNavigate();

  // 1. Initial Setup & Auth Check
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");

    if (!storedUser || !token) {
      navigate("/login");
      return;
    }

    setUser(JSON.parse(storedUser));
    socket.connect(); // Handshake includes token from socket.js

    // Load Rooms
    api.get("/rooms")
      .then((res) => {
        setRooms(res.data);
        if (res.data.length > 0) setCurrentRoom(res.data[0]);
      })
      .catch((err) => console.error("Error loading rooms:", err));

    return () => socket.disconnect();
  }, [navigate]);

  // 2. Room Switching & Real-time Listeners
  useEffect(() => {
    if (!currentRoom) return;

    socket.emit("join_room", currentRoom._id);

    api.get(`/messages/${currentRoom._id}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error("Error loading messages:", err));

    const handleReceive = (msg) => {
      // Logic check: make sure the message belongs to this room
      if (msg.roomId === currentRoom._id) {
        setMessages((prev) => [...prev, msg]);
      }
    };

    socket.on("receive_message", handleReceive);

    return () => {
      socket.off("receive_message", handleReceive);
      socket.emit("leave_room", currentRoom._id);
    };
  }, [currentRoom]);

  // 3. Send Text via Socket
  const sendTextMessage = (text) => {
    if (!currentRoom || !text.trim() || !user) return;

    const messageData = {
      roomId: currentRoom._id,
      content: text,
      sender: user.id, // Backend expects 'sender' ID
      type: "text",
    };

    socket.emit("send_message", messageData);
  };

  // 4. Send File/Image via API
  const sendFileMessage = async (file) => {
    if (!currentRoom || !user) return;

    setIsUploading(true); // Start loading spinner
    const formData = new FormData();
    formData.append("file", file);
    formData.append("roomId", currentRoom._id);
    formData.append("senderId", user.id);

    try {
      await api.post("/messages/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      // Note: We don't setMessages here because the server 
      // will emit "receive_message" to us via socket.
    } catch (err) {
      alert("Upload failed. Make sure the file is an image/pdf and under 5MB.");
    } finally {
      setIsUploading(false); // Stop loading spinner
    }
  };

  // 5. Logout Function
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    socket.disconnect();
    navigate("/login");
  };

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 shadow-sm">
        <RoomList
          rooms={rooms}
          currentRoomId={currentRoom?._id}
          onSelectRoom={setCurrentRoom}
        />

        <div className="d-flex flex-column flex-grow-1 bg-white">
          {currentRoom ? (
            <div className="d-flex flex-column h-100 p-0">
              {/* Header */}
              <div className="p-3 border-bottom d-flex justify-content-between align-items-center bg-white">
                <div>
                  <h5 className="mb-0 fw-bold text-dark">#{currentRoom.name}</h5>
                  {isUploading && <small className="text-primary animate-pulse">Uploading file...</small>}
                </div>
                <div className="d-flex align-items-center">
                  <div className="text-end me-3 d-none d-sm-block">
                    <small className="text-muted d-block">User</small>
                    <span className="fw-bold">{user?.username}</span>
                  </div>
                  <button onClick={handleLogout} className="btn btn-outline-danger btn-sm">Logout</button>
                </div>
              </div>

              {/* Chat Area */}
              <div className="flex-grow-1 overflow-hidden p-3">
                <ChatBox 
                  messages={messages} 
                  onSendText={sendTextMessage} 
                  onSendFile={sendFileMessage}
                  currentUserId={user?.id}
                />
              </div>
            </div>
          ) : (
            <div className="d-flex flex-grow-1 justify-content-center align-items-center">
              <div className="text-center">
                <div className="spinner-border text-primary mb-3" />
                <h5 className="text-muted">Select a room to begin</h5>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
