import React, { useState } from 'react';
import { joinRoomByCode, joinRoomById } from '../services/api'; 
import { useNavigate } from 'react-router-dom';

const JoinRoom = () => {
  const [inviteCode, setInviteCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // --- 1. JOIN PRIVATE ROOM (Via Code) ---
  const handleJoinByCode = async (e) => {
    e.preventDefault();
    
    const cleanCode = inviteCode.trim().toUpperCase();
    if (!cleanCode) {
      setError("Please enter a valid invite code.");
      return;
    }

    setLoading(true);
    setError('');

    try {
      // ✅ Now using the fixed api.js function that sends code in the BODY
      const { data } = await joinRoomByCode(cleanCode);

      console.log("Successfully joined private room:", data);

      // Navigate to chat and pass the new room data to auto-select it
      navigate('/chat', { state: { autoSelectRoom: data } }); 
      
    } catch (err) {
      const msg = err.response?.data?.message || "Invalid code or server error.";
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  // --- 2. JOIN PUBLIC ROOM (Via ID) ---
  // This can be triggered if you pass a roomId to this component
  const handleJoinPublic = async (roomId) => {
    setLoading(true);
    setError('');

    try {
      const { data } = await joinRoomById(roomId);
      navigate('/chat', { state: { autoSelectRoom: data } });
    } catch (err) {
      setError(err.response?.data?.message || "Could not join public room.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card shadow border-0">
            <div className="card-body p-4">
              <h3 className="text-center mb-4">Join a Room</h3>

              {error && (
                <div className="alert alert-danger py-2 text-center" role="alert">
                  <small>{error}</small>
                </div>
              )}

              <form onSubmit={handleJoinByCode}>
                <div className="mb-3">
                  <label className="form-label fw-bold">Invite Code</label>
                  <input
                    type="text"
                    className="form-control form-control-lg text-center"
                    placeholder="ENTER 6-DIGIT CODE"
                    maxLength="10"
                    value={inviteCode}
                    onChange={(e) => setInviteCode(e.target.value)}
                    required
                    autoFocus
                    style={{ letterSpacing: '2px', fontWeight: 'bold' }}
                  />
                  <div className="form-text text-center">
                    Private rooms require a unique 6-character code.
                  </div>
                </div>

                <button 
                  type="submit" 
                  className="btn btn-primary w-100 btn-lg mb-3"
                  disabled={loading}
                >
                  {loading ? (
                    <span>
                      <span className="spinner-border spinner-border-sm me-2"></span>
                      Joining...
                    </span>
                  ) : (
                    'Join Private Room'
                  )}
                </button>
              </form>

              <div className="text-center">
                <p className="text-muted small mb-2">Have a public room link?</p>
                <button 
                  className="btn btn-outline-secondary w-100"
                  onClick={() => navigate('/chat')} 
                >
                  Browse Public Rooms
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JoinRoom;
