import React from "react";

function RoomList({ 
  rooms, 
  users, 
  currentUser, 
  currentRoomId, 
  onSelectRoom, 
  onSelectUser, 
  onJoinRoom, 
  onCreateRoom, 
  onLogout 
}) {
  
  const myId = currentUser?.id || currentUser?._id;

  // 1. My Channels: Logic updated to look inside the { user: id } object
  const myRooms = rooms.filter(r => 
    r.members?.some(m => {
      const memberId = m.user?._id || m.user || m; // Handles populated or unpopulated
      return memberId === myId;
    })
  );

  // 2. Discover: Public rooms I haven't joined yet
  const otherRooms = rooms.filter(r => 
    r.isPrivate === false && 
    !r.members?.some(m => {
      const memberId = m.user?._id || m.user || m;
      return memberId === myId;
    })
  );

  // Helper to get a better name for private 1-on-1 rooms
  const getRoomDisplayName = (room) => {
    if (!room.isPrivate || room.name !== "Private Chat") return room.name;
    
    // Find the other member in the private chat to show their name
    const otherMember = room.members?.find(m => {
      const memberId = m.user?._id || m.user || m;
      return memberId !== myId;
    });
    
    // If user info is populated, show username, otherwise fallback
    return otherMember?.user?.username || "Private Chat";
  };

  return (
    <div className="bg-light border-end d-flex flex-column shadow-sm" style={{ width: "280px", height: "100vh" }}>
      
      {/* 1. HEADER */}
      <div className="p-3 border-bottom bg-white d-flex justify-content-between align-items-center">
        <h5 className="mb-0 fw-bold text-primary">ChatBox</h5>
        <button 
          onClick={onCreateRoom} 
          className="btn btn-sm btn-primary rounded-circle" 
          title="Create New Room"
        >
          +
        </button>
      </div>

      <div className="flex-grow-1 overflow-auto custom-scrollbar p-2">
        
        {/* 2. MY CHANNELS */}
        <div className="p-2 fw-bold text-muted small text-uppercase" style={{ letterSpacing: "1px" }}>My Channels</div>
        <div className="list-group list-group-flush mb-3">
          {myRooms.length > 0 ? (
            myRooms.map((room) => (
              <button
                key={room._id}
                onClick={() => onSelectRoom(room)}
                className={`list-group-item list-group-item-action border-0 rounded mb-1 d-flex align-items-center ${
                  room._id === currentRoomId ? "active shadow-sm text-white" : ""
                }`}
              >
                <span className="me-2">{room.isPrivate ? "üîí" : "#"}</span>
                <span className="text-truncate">{getRoomDisplayName(room)}</span>
              </button>
            ))
          ) : (
            <div className="small text-muted ps-2 italic">No channels joined</div>
          )}
        </div>

        {/* 3. DISCOVER CHANNELS */}
        {otherRooms.length > 0 && (
          <>
            <div className="p-2 fw-bold text-muted small text-uppercase" style={{ letterSpacing: "1px" }}>Discover</div>
            <div className="list-group list-group-flush mb-3">
              {otherRooms.map((room) => (
                <div key={room._id} className="list-group-item bg-transparent border-0 d-flex align-items-center justify-content-between p-1">
                  <span className="text-muted ps-2 text-truncate" style={{ maxWidth: "150px" }}># {room.name}</span>
                  <button 
                    className="btn btn-sm btn-outline-success py-0 px-2" 
                    onClick={() => onJoinRoom(room)}
                  >
                    Join
                  </button>
                </div>
              ))}
            </div>
          </>
        )}

        <hr className="mx-2" />

        {/* 4. USERS LIST (Direct Messages) */}
        <div className="p-2 fw-bold text-muted small text-uppercase" style={{ letterSpacing: "1px" }}>Direct Messages</div>
        <div className="list-group list-group-flush">
          {users.filter(u => (u._id || u.id) !== myId).map((u) => (
            <button
              key={u._id || u.id}
              onClick={() => onSelectUser(u)}
              className="list-group-item list-group-item-action border-0 rounded mb-1 d-flex align-items-center bg-transparent"
            >
              <div className="position-relative me-2">
                <div className="bg-secondary rounded-circle" style={{ width: "30px", height: "30px", display: "flex", alignItems: "center", justifyContent: "center", color: "white", fontSize: "12px" }}>
                  {u.username?.charAt(0).toUpperCase()}
                </div>
                {/* Simplified online indicator */}
                <div className="position-absolute bottom-0 end-0 bg-success border border-white rounded-circle" style={{ width: "10px", height: "10px" }}></div>
              </div>
              <span className="small fw-medium">{u.username}</span>
            </button>
          ))}
        </div>
      </div>

      {/* 5. FOOTER */}
      <div className="p-3 border-top bg-white">
        <div className="d-flex align-items-center mb-3">
          <div className="bg-primary text-white rounded-circle d-flex justify-content-center align-items-center me-2 shadow-sm" style={{ width: "40px", height: "40px", fontWeight: "bold" }}>
            {currentUser?.username?.charAt(0).toUpperCase()}
          </div>
          <div className="text-truncate">
            <div className="fw-bold small">{currentUser?.username}</div>
            <div className="text-success" style={{ fontSize: "10px", fontWeight: "bold" }}>‚óè Online</div>
          </div>
        </div>
        <div className="d-grid gap-2">
          <button onClick={onLogout} className="btn btn-sm btn-outline-danger w-100">Logout</button>
        </div>
      </div>

      <style>{`
        .custom-scrollbar::-webkit-scrollbar { width: 4px; }
        .custom-scrollbar::-webkit-scrollbar-thumb { background: #cbd5e0; border-radius: 10px; }
        .list-group-item.active { background-color: #0d6efd !important; border-color: #0d6efd !important; color: white !important; }
        .list-group-item-action:hover { background-color: #f8f9fa; }
      `}</style>
    </div>
  );
}

export default RoomList;
