import { useEffect, useState, useCallback, useRef } from "react";
import api from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket";
import { useNavigate } from "react-router-dom";
import { Dropdown, Modal, Button, FormControl, ListGroup, Badge, Form } from "react-bootstrap"; 

/* --- Embedded Call Component --- */
const VideoCallOverlay = ({ currentRoom, user, type, onEnd }) => {
  const jitsiContainerRef = useRef(null);
  const jitsiApiRef = useRef(null);

  useEffect(() => {
    const domain = "meet.jit.si";
    const options = {
      roomName: `MernChat_Room_${currentRoom._id}`,
      width: "100%",
      height: "100%",
      parentNode: jitsiContainerRef.current,
      userInfo: { displayName: user?.username || "User" },
      configOverwrite: {
        startWithAudioMuted: false,
        startWithVideoMuted: type === 'audio',
        disableInviteFunctions: true
      },
    };
    jitsiApiRef.current = new window.JitsiMeetExternalAPI(domain, options);
    jitsiApiRef.current.addEventListener("videoConferenceLeft", onEnd);

    return () => {
      if (jitsiApiRef.current) jitsiApiRef.current.dispose();
    };
  }, [currentRoom._id, user, type, onEnd]);

  return (
    <div className="position-absolute top-0 start-0 w-100 h-100 bg-dark d-flex flex-column animate-pop-in" style={{ zIndex: 1050 }}>
      <div className="p-2 d-flex justify-content-between align-items-center bg-black text-white border-bottom border-secondary">
        <span className="fw-bold px-2">{type === 'video' ? 'ðŸ“¹ Video Call' : 'ðŸ“ž Audio Call'}: {currentRoom.name}</span>
        <Button variant="danger" size="sm" onClick={onEnd}>End Call</Button>
      </div>
      <div ref={jitsiContainerRef} className="flex-grow-1" />
    </div>
  );
};

export default function Chat({ setAuth }) { 
  /* --- States --- */
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showInfoModal, setShowInfoModal] = useState(false);
  const [isCreating, setIsCreating] = useState(false);
  const [callConfig, setCallConfig] = useState({ active: false, type: 'video' });

  // Interactive Search States
  const [searchTerm, setSearchTerm] = useState("");
  const [showSearchBar, setShowSearchBar] = useState(false);

  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
    showHistoryToNewMembers: true,
  });

  const navigate = useNavigate();

  /* --- Fetch Data --- */
  const fetchData = useCallback(async () => {
    try {
      const [roomsRes, usersRes] = await Promise.all([
        api.get("/rooms"),
        api.get("/auth/users"),
      ]);
      setRooms(roomsRes.data || []);
      setUsers(usersRes.data || []);

      if (currentRoom) {
        const updatedRoom = (roomsRes.data || []).find(r => r._id === currentRoom._id);
        if (updatedRoom) setCurrentRoom(updatedRoom);
      }
    } catch (err) {
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [currentRoom]);

  /* --- Auth Handlers --- */
  const handleLogout = useCallback(() => {
    if (setAuth) { setAuth(null); } 
    else { localStorage.clear(); window.location.href = "/login"; }
    socket.disconnect();
  }, [setAuth]);

  /* --- Message Handlers --- */
  const onSendMessage = async (content, file = null) => {
    if (!currentRoom) return;
    try {
      let res;
      if (file) {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("roomId", currentRoom._id);
        res = await api.post("/messages/upload", formData, {
          headers: { "Content-Type": "multipart/form-data" }
        });
      } else {
        res = await api.post("/messages", { roomId: currentRoom._id, content });
      }
      socket.emit("send_message", res.data);
    } catch (err) { console.error(err); }
  };

  const handleClearChat = async () => {
    if (window.confirm("Clear all messages?")) {
      try {
        await api.delete(`/messages/clear/${currentRoom._id}`);
        setMessages([]);
      } catch (err) { alert("Failed to clear chat"); }
    }
  };

  /* --- Call Handlers --- */
  const handleStartCall = (type) => {
    if (!currentRoom) return;
    setCallConfig({ active: true, type });
    onSendMessage(`ðŸ“ž Started a ${type} call. Join me by clicking the icon above!`);
  };

  /* --- Initial Load --- */
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
        socket.connect();
      } catch (e) { handleLogout(); }
    }
    fetchData();
    return () => {
      socket.off("receive_message");
      socket.disconnect();
    };
  }, []); 

  /* --- Socket & Room Sync --- */
  useEffect(() => {
    if (!currentRoom || !user) return;
    const roomId = currentRoom._id;
    socket.emit("join_room", { roomId, username: user.username });

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error(err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId || msg.room === roomId) {
        setMessages((prev) => [...prev, msg]);
      }
    };
    socket.on("receive_message", handleReceive);
    return () => socket.off("receive_message", handleReceive);
  }, [currentRoom?._id, user?.username]);

  /* --- Room Management --- */
  const handleJoinRoom = async (item) => {
    try {
      let res;
      const input = typeof item === "string" ? item.trim() : item._id;
      if (input.length === 6 && !input.includes('-')) {
        res = await api.post(`/rooms/join-code/${input}`);
      } else {
        res = await api.post(`/rooms/join-id/${input}`);
      }
      await fetchData(); 
      setCurrentRoom(res.data.room || res.data);
      setIsCreating(false);
    } catch (err) {
      alert(err.response?.data?.message || "Error joining room.");
    }
  };

  const handleCreateRoom = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/rooms", {
        name: newRoomData.name,
        isPrivate: newRoomData.isPrivate,
      });
      await fetchData(); 
      setCurrentRoom(res.data);
      setIsCreating(false);
      setNewRoomData({ name: "", isPrivate: false });
    } catch (err) { alert("Error creating room"); }
  };

  const handleDeleteRoomById = async (roomId) => {
    if (window.confirm("Permanently delete this room?")) {
      try {
        await api.delete(`/rooms/${roomId}`);
        setRooms((prev) => prev.filter((r) => r._id !== roomId));
        if (currentRoom?._id === roomId) {
          setCurrentRoom(null);
          setMessages([]);
        }
        setShowInfoModal(false);
      } catch (err) { alert("Delete failed."); }
    }
  };

  const handleSelectUser = async (targetUser) => {
    try {
      const existingDm = rooms.find(r => 
        r.isDirectMessage && 
        r.members?.some(m => (m.user?._id || m.user || m) === targetUser._id)
      );
      if (existingDm) {
        setCurrentRoom(existingDm);
      } else {
        const res = await api.post("/rooms/private", { targetUserId: targetUser._id });
        await fetchData(); 
        setCurrentRoom(res.data);
      }
    } catch (err) { console.error("DM Error:", err); }
  };

  /* --- Helper Functions --- */
  const getDMName = () => {
    const otherMember = currentRoom.members?.find(m => (m.user?._id || m.user || m) !== (user?._id || user?.id));
    return otherMember?.user?.username || otherMember?.username || "Private Chat";
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    alert("Copied to clipboard!");
  };

  const filteredMessages = messages.filter((msg) =>
    msg.content?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const currentUserRealId = user?._id || user?.id;
  const roomOwnerId = currentRoom?.owner?._id || currentRoom?.owner;
  const isOwner = roomOwnerId && currentUserRealId && roomOwnerId.toString() === currentUserRealId.toString();

  if (loading) return <div className="vh-100 d-flex justify-content-center align-items-center auth-bg">Loadingâ€¦</div>;

  return (
    <div className="container-fluid p-0 auth-bg" style={{ height: "100vh" }}>
      <div className="d-flex h-100 overflow-hidden" style={{ background: "rgba(0,0,0,0.1)" }}>

        {/* Sidebar */}
        <RoomList
          rooms={rooms || []}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={(room) => { 
            setCurrentRoom(room); 
            setIsCreating(false); 
            setCallConfig({active: false}); 
            setShowSearchBar(false); 
            setSearchTerm(""); 
          }}
          onSelectUser={handleSelectUser}
          onJoinRoom={handleJoinRoom} 
          onCreateRoom={() => setIsCreating(true)}
          onLogout={handleLogout}
          onRefresh={fetchData}
        />

        {/* Main Chat Area */}
        <div className="flex-grow-1 d-flex flex-column position-relative bg-transparent">

          {/* Call Overlay */}
          {callConfig.active && (
            <VideoCallOverlay 
              currentRoom={currentRoom} 
              user={user} 
              type={callConfig.type} 
              onEnd={() => setCallConfig({ active: false })} 
            />
          )}

          {isCreating ? (
            <div className="p-5 d-flex justify-content-center animate-pop-in">
              <div className="glass-card p-4 w-100" style={{maxWidth: '500px'}}>
                <h3>Create New Room</h3>
                <Form onSubmit={handleCreateRoom}>
                  <Form.Group className="mb-3">
                    <Form.Label>Room Name</Form.Label>
                    <Form.Control type="text" required onChange={(e) => setNewRoomData({...newRoomData, name: e.target.value})} />
                  </Form.Group>
                  <Form.Check type="switch" label="Private Room (Requires Code)" className="mb-4" checked={newRoomData.isPrivate} onChange={(e) => setNewRoomData({...newRoomData, isPrivate: e.target.checked})} />
                  <div className="d-flex gap-2">
                    <Button variant="primary" type="submit">Create</Button>
                    <Button variant="secondary" onClick={() => setIsCreating(false)}>Cancel</Button>
                  </div>
                </Form>
              </div>
            </div>
          ) : currentRoom ? (
            <div className="d-flex flex-column h-100 animate-pop-in">

              {/* INTERACTIVE HEADER */}
              <div className="d-flex justify-content-between align-items-center p-3 border-bottom glass-panel text-white shadow-sm">
                <div className="d-flex align-items-center gap-3" style={{ cursor: "pointer" }} onClick={() => setShowInfoModal(true)}>
                  <div className="bg-primary rounded-circle d-flex align-items-center justify-content-center text-white" style={{ width: "40px", height: "40px", boxShadow: "0 0 10px rgba(0,0,0,0.2)" }}>
                    {currentRoom.isDirectMessage ? "ðŸ‘¤" : "ðŸ‘¥"}
                  </div>
                  <div>
                    <h5 className="mb-0 fw-bold">
                      {currentRoom.isDirectMessage ? getDMName() : (currentRoom.isPrivate ? `ðŸ”’ ${currentRoom.name}` : `# ${currentRoom.name}`)}
                    </h5>
                    <small className="opacity-75 text-white">Click for info</small>
                  </div>
                </div>

                <div className="d-flex align-items-center gap-3">
                  {/* Search Icon Toggle - Same style as calls */}
                  <span 
                    className={`p-2 rounded-circle ${showSearchBar ? 'bg-primary' : ''}`}
                    style={{ cursor: "pointer", fontSize: "1.2rem", transition: "0.3s", color: "white" }} 
                    onClick={() => { setShowSearchBar(!showSearchBar); if(showSearchBar) setSearchTerm(""); }}
                    title="Search Messages"
                  >ðŸ”</span>

                  {/* Call Icons */}
                  <span className="text-white p-2" style={{ cursor: "pointer", fontSize: "1.3rem" }} onClick={() => handleStartCall('video')} title="Video Call">ðŸ“¹</span>
                  <span className="text-white p-2" style={{ cursor: "pointer", fontSize: "1.3rem" }} onClick={() => handleStartCall('audio')} title="Audio Call">ðŸ“ž</span>

                  <div className="vr mx-1 bg-white" style={{ height: "20px" }}></div>

                  <Dropdown align="end">
                    <Dropdown.Toggle variant="link" className="text-white p-0 border-0 no-caret shadow-none">
                      <span style={{ fontSize: "1.6rem" }}>â‹®</span>
                    </Dropdown.Toggle>
                    <Dropdown.Menu className="glass-card border-0 shadow">
                      <Dropdown.Item onClick={() => setShowInfoModal(true)}>â“˜ Group info</Dropdown.Item>
                      <Dropdown.Item onClick={handleClearChat}>âž– Clear messages</Dropdown.Item>
                      <Dropdown.Item onClick={() => setCurrentRoom(null)}>âœ– Close chat</Dropdown.Item>
                      {isOwner && !currentRoom.isDirectMessage && (
                        <>
                          <Dropdown.Divider className="bg-secondary" />
                          <Dropdown.Item onClick={() => handleDeleteRoomById(currentRoom._id)} className="text-danger">ðŸ—‘ Delete room</Dropdown.Item>
                        </>
                      )}
                    </Dropdown.Menu>
                  </Dropdown>
                </div>
              </div>

              {/* DYNAMIC SEARCH BAR */}
              {showSearchBar && (
                <div className="px-3 py-2 border-bottom animate-slide-in" style={{ background: "rgba(255,255,255,0.1)", backdropFilter: "blur(5px)" }}>
                  <div className="input-group input-group-sm">
                    <span className="input-group-text bg-transparent text-white border-0">ðŸ”</span>
                    <FormControl
                      placeholder="Search messages..."
                      className="bg-transparent text-white border-0 shadow-none placeholder-white"
                      style={{ color: "white" }}
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      autoFocus
                    />
                    {searchTerm && (
                      <Button variant="link" className="text-white text-decoration-none" onClick={() => setSearchTerm("")}>âœ•</Button>
                    )}
                  </div>
                </div>
              )}

              {/* MESSAGES AREA */}
              <div className="flex-grow-1 overflow-hidden">
                <ChatBox 
                  messages={searchTerm ? filteredMessages : messages} 
                  currentUserId={currentUserRealId} 
                  onSendText={onSendMessage} 
                  onSendFile={(f) => onSendMessage(null, f)} 
                />
              </div>

              {/* INFO MODAL */}
              <Modal show={showInfoModal} onHide={() => setShowInfoModal(false)} centered className="glass-modal">
                <Modal.Header closeButton className="border-0"><Modal.Title>Room Details</Modal.Title></Modal.Header>
                <Modal.Body className="text-center pb-4">
                  <div className="bg-primary rounded-circle d-inline-flex align-items-center justify-content-center mb-3 text-white" style={{width: '80px', height: '80px', fontSize: '2rem'}}>
                    {currentRoom.isDirectMessage ? "ðŸ‘¤" : "ðŸ‘¥"}
                  </div>
                  <h4>{currentRoom.isDirectMessage ? getDMName() : currentRoom.name}</h4>
                  <hr className="bg-secondary" />
                  {!currentRoom.isDirectMessage && (
                    <div className="text-start">
                      <label className="fw-bold small opacity-75 mb-2">INVITE CODE</label>
                      <div className="d-flex gap-2 mb-3">
                        <FormControl readOnly className="bg-light fw-bold shadow-none" value={currentRoom.isPrivate ? (currentRoom.inviteCode || "No Code") : currentRoom._id} />
                        <Button variant="primary" onClick={() => copyToClipboard(currentRoom.isPrivate ? currentRoom.inviteCode : currentRoom._id)}>Copy</Button>
                      </div>
                    </div>
                  )}
                  <Button variant="outline-dark" className="w-100 mt-2" onClick={() => setShowInfoModal(false)}>Close</Button>
                </Modal.Body>
              </Modal>

            </div>
          ) : (
            <div className="h-100 d-flex flex-column justify-content-center align-items-center text-white text-center p-4">
              <div className="glass-card p-5">
                <h1 className="display-4">ðŸ’¬</h1>
                <h2 className="fw-bold">Welcome back, {user?.username}</h2>
                <p className="opacity-75">Select a friend or group to start the conversation.</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
