import { useEffect, useState, useCallback } from "react";
import { api } from "../services/api";
import ChatBox from "../components/ChatBox";
import RoomList from "../components/RoomList";
import socket from "../services/socket"; 
import { useNavigate } from "react-router-dom";

export default function Chat({ setToken }) { 
  const [rooms, setRooms] = useState([]);
  const [users, setUsers] = useState([]); 
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [isUploading, setIsUploading] = useState(false);

  // NEW STATE FOR FORM & PRIVATE JOINING
  const [isCreating, setIsCreating] = useState(false);
  const [inviteCodeInput, setInviteCodeInput] = useState("");
  const [newRoomData, setNewRoomData] = useState({
    name: "",
    isPrivate: false,
    showHistory: false
  });

  const navigate = useNavigate();

  const handleLogout = useCallback(() => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    if (setToken) setToken(null);
    socket.disconnect();
    navigate("/login");
  }, [navigate, setToken]);

  // 1. Initial Data & Socket Setup
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    const token = localStorage.getItem("token");

    if (!storedUser || !token) {
      handleLogout();
      return;
    }

    const parsedUser = JSON.parse(storedUser);
    setUser(parsedUser);
    const userId = parsedUser.id || parsedUser._id;
    socket.connect(); 

    const fetchData = async () => {
      try {
        const [roomsRes, usersRes] = await Promise.all([
          api.get("/rooms"),
          api.get("/auth/users")
        ]);

        const visibleRooms = roomsRes.data.filter(r => 
          !r.isPrivate || r.members.some(m => (m.user?._id || m.user || m) === userId)
        );

        setRooms(visibleRooms);
        setUsers(usersRes.data);
      } catch (err) {
        if (err.response?.status === 401) handleLogout();
      }
    };

    fetchData();

    // SOCKET LISTENERS: Room Management
    socket.on("room_created", (newRoom) => {
      setRooms((prev) => (prev.find(r => r._id === newRoom._id) ? prev : [...prev, newRoom]));
    });

    socket.on("room_deleted", (roomId) => {
      setRooms(prev => prev.filter(r => r._id !== roomId));
      if (currentRoom?._id === roomId) setCurrentRoom(null);
    });

    // SOCKET LISTENERS: Message Lifecycle
    socket.on("message_deleted", (messageId) => {
      setMessages(prev => prev.filter(m => m._id !== messageId));
    });

    socket.on("message_edited", ({ messageId, content }) => {
      setMessages(prev => prev.map(m => m._id === messageId ? { ...m, content, isEdited: true } : m));
    });

    return () => {
      socket.off("room_created");
      socket.off("room_deleted");
      socket.off("message_deleted");
      socket.off("message_edited");
      socket.disconnect();
    };
  }, [handleLogout, currentRoom]);

  // 2. Room Messaging Sync
  useEffect(() => {
    if (!currentRoom || !user) return;
    const roomId = currentRoom._id;
    const username = user.username;

    socket.emit("join_room", { roomId, username });

    api.get(`/messages/${roomId}`)
      .then((res) => setMessages(res.data))
      .catch((err) => console.error("History error:", err));

    const handleReceive = (msg) => {
      if (msg.roomId === roomId) setMessages((prev) => [...prev, msg]);
    };

    socket.on("receive_message", handleReceive);
    return () => {
      socket.off("receive_message", handleReceive);
      socket.emit("leave_room", { roomId, username });
    };
  }, [currentRoom, user]);

  // --- ACTIONS ---

  const handleCreateSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post("/rooms", {
        name: newRoomData.name.trim(),
        isPrivate: newRoomData.isPrivate,
        showHistoryToNewMembers: newRoomData.showHistory
      });

      const newRoom = res.data;
      setRooms(prev => [...prev, newRoom]);
      setCurrentRoom(newRoom);
      setIsCreating(false);
      setNewRoomData({ name: "", isPrivate: false, showHistory: false });

      if (newRoom.isPrivate) alert(`Private Room Created! Invite Code: ${newRoom.inviteCode}`);
      socket.emit("create_room", newRoom); 
    } catch (err) {
      alert("Error creating room.");
    }
  };

  const handleJoinByCode = async () => {
    if (!inviteCodeInput) return;
    try {
      await api.post(`/rooms/join/${inviteCodeInput}`);
      alert("Join request sent to the room owner!");
      setInviteCodeInput("");
    } catch (err) {
      alert(err.response?.data?.message || "Invalid invite code.");
    }
  };

  const handleDeleteRoom = async (roomId) => {
    if (!window.confirm("Are you sure? All messages will be deleted.")) return;
    try {
      await api.delete(`/rooms/${roomId}`);
      socket.emit("delete_room", roomId); 
    } catch (err) {
      alert("Unauthorized.");
    }
  };

  const handleApproveUser = async (roomId, userId) => {
    try {
      await api.put("/rooms/approve", { roomId, userId });
      alert("User approved!");
      // Refresh room data to update members list
      const res = await api.get("/rooms");
      setRooms(res.data);
    } catch (err) {
      alert("Approval failed.");
    }
  };

  const onUpdateMessage = async (messageId, newContent) => {
    try {
      await api.put(`/messages/${messageId}`, { content: newContent });
      socket.emit("edit_message", { roomId: currentRoom._id, messageId, content: newContent });
    } catch (err) { alert("Failed to edit."); }
  };

  const onDeleteMessage = async (messageId) => {
    try {
      await api.delete(`/messages/${messageId}`);
      socket.emit("delete_message", { roomId: currentRoom._id, messageId });
    } catch (err) { alert("Failed to delete."); }
  };

  const sendTextMessage = (text) => {
    if (!currentRoom || !text.trim() || !user) return;
    socket.emit("send_message", {
      roomId: currentRoom._id,
      content: text,
      sender: user, 
      type: "text",
    });
  };

  const sendFileMessage = async (file) => {
    if (!currentRoom || !user) return;
    setIsUploading(true);
    const formData = new FormData();
    formData.append("file", file);
    formData.append("roomId", currentRoom._id);
    try {
      await api.post("/messages/upload", formData);
    } catch (err) { alert("Media upload failed."); } 
    finally { setIsUploading(false); }
  };

  return (
    <div className="container-fluid p-0 bg-light" style={{ height: "100vh" }}>
      <div className="d-flex h-100 shadow-sm overflow-hidden">
        <RoomList
          rooms={rooms}
          users={users}
          currentUser={user}
          currentRoomId={currentRoom?._id}
          onSelectRoom={(r) => { setCurrentRoom(r); setIsCreating(false); }}
          onLogout={handleLogout}
          onCreateRoom={() => setIsCreating(true)}
        />

        <div className="d-flex flex-column flex-grow-1 bg-white position-relative">
          {isUploading && (
            <div className="position-absolute top-0 start-0 w-100 bg-primary text-white text-center py-1 z-3">
              Uploading media...
            </div>
          )}

          {isCreating ? (
            /* --- CREATE ROOM FORM --- */
            <div className="h-100 d-flex justify-content-center align-items-center bg-light">
              <div className="card shadow p-4" style={{ width: "400px" }}>
                <h3 className="mb-4">Create New Room</h3>
                <form onSubmit={handleCreateSubmit}>
                  <input 
                    className="form-control mb-3" 
                    placeholder="Room Name" 
                    value={newRoomData.name}
                    onChange={e => setNewRoomData({...newRoomData, name: e.target.value})}
                    required 
                  />
                  <div className="form-check form-switch mb-2">
                    <input className="form-check-input" type="checkbox" checked={newRoomData.isPrivate} onChange={e => setNewRoomData({...newRoomData, isPrivate: e.target.checked})} />
                    <label className="form-check-label">Private Room (Requires Invite Code)</label>
                  </div>
                  <div className="form-check form-switch mb-4">
                    <input className="form-check-input" type="checkbox" checked={newRoomData.showHistory} onChange={e => setNewRoomData({...newRoomData, showHistory: e.target.checked})} />
                    <label className="form-check-label">Allow new members to see old history</label>
                  </div>
                  <div className="d-flex gap-2">
                    <button type="submit" className="btn btn-primary w-100">Create</button>
                    <button type="button" className="btn btn-outline-secondary w-100" onClick={() => setIsCreating(false)}>Cancel</button>
                  </div>
                </form>
              </div>
            </div>
          ) : currentRoom ? (
            /* --- CHAT INTERFACE --- */
            <div className="d-flex flex-column h-100">
              <div className="p-3 border-bottom bg-white d-flex justify-content-between align-items-center">
                <div>
                  <h5 className="mb-0 fw-bold">{currentRoom.isPrivate ? "ðŸ”’ " : "# "} {currentRoom.name}</h5>
                  {currentRoom.isPrivate && <small className="text-muted">Code: {currentRoom.inviteCode}</small>}
                </div>

                <div className="d-flex gap-2">
                  {/* ADMIN ONLY BUTTONS */}
                  {(currentRoom.owner === (user?.id || user?._id)) && (
                    <>
                      <button className="btn btn-outline-primary btn-sm" onClick={() => {/* Modal for joinRequests */}}>
                        Manage Requests ({currentRoom.joinRequests?.length || 0})
                      </button>
                      <button className="btn btn-danger btn-sm" onClick={() => handleDeleteRoom(currentRoom._id)}>
                        Delete Room
                      </button>
                    </>
                  )}
                </div>
              </div>

              <div className="flex-grow-1 overflow-hidden">
                <ChatBox 
                  messages={messages} 
                  onSendText={sendTextMessage} 
                  onSendFile={sendFileMessage}
                  onDeleteMessage={onDeleteMessage}
                  onEditMessage={onUpdateMessage}
                  currentUserId={user?.id || user?._id}
                  isRoomOwner={currentRoom.owner === (user?.id || user?._id)}
                />
              </div>
            </div>
          ) : (
            <div className="h-100 d-flex flex-column justify-content-center align-items-center">
              <h4>Welcome to the Hub</h4>
              <div className="mt-3 p-3 bg-light border rounded">
                <h6>Join Private Room</h6>
                <div className="input-group">
                  <input className="form-control" placeholder="6-digit code" value={inviteCodeInput} onChange={e => setInviteCodeInput(e.target.value.toUpperCase())} />
                  <button className="btn btn-primary" onClick={handleJoinByCode}>Join</button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
